  'CFC-113': { color: 'black', data: [[1940, 0], [1950, 1], [1960, 5], [1970, 15], [1980, 40], [1990, 80], [2000, 85], [2010, 75], [2020, 70]] },
  'CCl4': { color: 'green', data: [[1940, 50], [1950, 60], [1960, 75], [1970, 90], [1980, 100], [1990, 105], [2000, 95], [2010, 85], [2020, 80]] }
};
const cfcList = Object.keys(cfcData);

