/* ----------------------- Utility Functions ----------------------- */
// Linear interpolation for a given year on a specific CFC dataset
function interpolateCFC(cfcKey, year) {
  const data = cfcData[cfcKey].data;
  if (year <= data[0][0]) return data[0][1];
  if (year >= data[data.length - 1][0]) return data[data.length - 1][1];
  for (let i = 0; i < data.length - 1; i++) {
    const [x0, y0] = data[i];
    const [x1, y1] = data[i + 1];
    if (year >= x0 && year <= x1) {
      const t = (year - x0) / (x1 - x0);
      return y0 + t * (y1 - y0);
    }
  }
  return data[data.length - 1][1];
}

/* Map CFC concentration [0..max] to ozone layer opacity [0.2..0.85] (inverse relation) */
function concToOpacity(conc) {
  const maxConc = 550; // approximate max in the dataset
  const norm = Math.min(Math.max(conc / maxConc, 0), 1);
  const opacity = 0.85 - 0.65 * norm;
  return Math.min(0.85, Math.max(0.2, opacity));
}

/* ----------------------- Plotly: CFC Plot ----------------------- */
function renderCFCPlot(selectedKey, showAll, year) {
