      y: ys,
      type: 'scatter',
      mode: 'lines',
      name: key,
      line: {
        color: cfcData[key].color,
        width: (showAll && key === selectedKey) ? 4 : 2
      },
      visible: showAll ? true : (key === selectedKey)
    };
    traces.push(t);
  });

  // Red marker at current year on ACTIVE trace
  const concAtYear = interpolateCFC(selectedKey, year);
  traces.push({
    x: [year],
    y: [concAtYear],
    type: 'scatter',
    mode: 'markers',
    marker: { color: 'red', size: 9 },
