function renderOzonePlot(concAtYear, timeMax) {
  // Pseudo-first-order: 1/[O3](t) = 1/[O3](0) + k * [CFC] * t
  const y0 = 20; // arbitrary baseline to resemble snapshot scale
  const k = 0.006; // proportionality constant
  const slope = k * concAtYear;

  const N = Math.max(2, Math.floor(40 * Math.max(timeMax, 0.001) / 30)); // dense enough points
  const xs = [];
  const ys = [];
  for (let i = 0; i < N; i++) {
    const t = (timeMax * i) / (N - 1);
    xs.push(t);
    ys.push(y0 + slope * t);
