// Update ozone opacity from a concentration
function updateOzoneOpacity(conc) {
  if (!ozoneMesh) return;
  ozoneMesh.material.opacity = concToOpacity(conc);
}

/* ----------------------- Update Logic ----------------------- */
function update() {
  const selectedKey = selectCFC.value;
  const showAll = checkboxShowAll.checked;
  const year = Number(sliderYear.value);
  const timeMax = Number(sliderTime.value);

  labelYear.textContent = String(year);
  labelTime.textContent = String(timeMax);

  // Plot CFCs and get concentration at selected year
  const concAtYear = renderCFCPlot(selectedKey, showAll, year);

  // Update Ozone plot based on selected CFC concentration and time
  renderOzonePlot(concAtYear, timeMax);

  // Update 3D ozone layer opacity
  updateOzoneOpacity(concAtYear);
}

/* ----------------------- Event Listeners ----------------------- */
selectCFC.addEventListener('change', update);
checkboxShowAll.addEventListener('change', update);
sliderYear.addEventListener('input', update);
sliderTime.addEventListener('input', update);

/* ----------------------- Resize Handling ----------------------- */
window.addEventListener('resize', () => {
  // Resize plots
  Plotly.Plots.resize('plot-ozone');
  Plotly.Plots.resize('plot-cfc');
  // Resize three.js renderer
  const container = document.getElementById('scene-container');
  if (renderer && camera) {
    const w = container.clientWidth;
    const h = container.clientHeight;
    renderer.setSize(w, h);
    camera.aspect = w / h;
    camera.updateProjectionMatrix();
  }
});

/* ----------------------- Initialize ----------------------- */
initThreeScene();
update();
</script>

</body>
</html>
