    <label for="checkbox-zoom" style="margin-left:18px;">zoom</label>
    <input type="checkbox" id="checkbox-zoom">
  </div>

  <div id="visualization-container">
    <!-- p5 will create the canvas and attach it here with id="demo-canvas" -->
  </div>

<script>
  // Global state
  let angle = 7;        // in degrees
  let isZoomed = false; // zoom toggle
  let canvas;

  // DOM elements
  const angleSlider = document.getElementById('slider-post-angle');
  const angleDisplay = document.getElementById('display-post-angle');
  const zoomCheckbox = document.getElementById('checkbox-zoom');

  angleSlider.addEventListener('input', () => {
    angle = parseFloat(angleSlider.value);
    angleDisplay.textContent = angle.toFixed(1);
    if (typeof redraw === 'function') redraw();
  });

  zoomCheckbox.addEventListener('change', () => {
    isZoomed = zoomCheckbox.checked;
    if (typeof redraw === 'function') redraw();
  });

  // p5 sketch
  function setup() {
    canvas = createCanvas(600, 450);
    canvas.parent('visualization-container');
    // set explicit id as required
    canvas.id('demo-canvas');
    angleMode(DEGREES);
    noLoop(); // we'll manually redraw on control changes
    redraw();
  }

  function draw() {
    background('#F0F0F0');
    drawSunRays();

    if (isZoomed) {
      drawZoomedView();
    } else {
      drawFullView();
    }
  }

  // Common: Sun's rays on the right side
  function drawSunRays() {
    const leftX = width * 0.55;
    const rightX = width - 20;
    const rayCol = color('#DAA520');

    push();
    stroke(rayCol);
    strokeWeight(2);
    fill(rayCol);
    for (let y = 60; y <= height - 40; y += 40) {
      line(leftX, y, rightX, y);
      // Arrowhead pointing left at the right end
      const ah = 8;
      triangle(rightX, y,
               rightX - ah, y - ah * 0.6,
               rightX - ah, y + ah * 0.6);
    }
    pop();
  }

  // Utility for dashed lines
  function dashedLine(x1, y1, x2, y2, dash = 6, gap = 4) {
    push();
    stroke(0);
    strokeWeight(1.6);
    drawingContext.setLineDash([dash, gap]);
    line(x1, y1, x2, y2);
    drawingContext.setLineDash([]); // reset
    pop();
  }

  // Small arrow/marker triangle pointing toward a direction (theta)
  function arrowhead(px, py, theta, size = 8) {
    // apex at (px, py); base corners rotated +/- 150 degrees from theta
    push();
    noStroke();
    fill(0);
    const x1 = px + size * cos(theta + 150);
    const y1 = py + size * sin(theta + 150);
    const x2 = px + size * cos(theta - 150);
    const y2 = py + size * sin(theta - 150);
    triangle(px, py, x1, y1, x2, y2);
    pop();
  }

  function drawFullView() {
    const cx = 240;
    const cy = 240;
    const R  = 180;

    // Earth
    push();
    noStroke();
    fill('#468296');
    circle(cx, cy, R * 2);
    pop();

    // Earth label
    push();
    fill(0);
    noStroke();
    textSize(22);
    textAlign(LEFT, CENTER);
    text('Earth', cx - 60, cy + 4);
    pop();

    // Points
    const a = -angle; // use negative so that positive slider rotates CCW visually
    const ax = cx + R * cos(a);
    const ay = cy + R * sin(a);
    const sx = cx + R;
    const sy = cy;

    // Dashed Syene radius (horizontal)
    dashedLine(cx, cy, sx, sy);

    // Dashed Alexandria radius
    dashedLine(cx, cy, ax, ay);

    // Dashed horizontal through Alexandria
    dashedLine(cx, ay, sx, ay);

    // Labels
    push();
    fill(0);
    noStroke();
    textSize(12);
    textAlign(LEFT, BOTTOM);
    text('Syene', sx + 6, sy + 4);
    textAlign(LEFT, CENTER);
    text('Alexandria', sx + 6, ay);
    pop();

    // Angle visualization at the center (equivalent to local shadow angle)
    const rArc = R * 0.44;
    push();
    noFill();
    stroke(0);
    strokeWeight(2);
    arc(cx, cy, rArc * 2, rArc * 2, 0, a);
    pop();

    // Angle text near the arc
    const midAng = a / 2;
    const tx = cx + (rArc + 16) * cos(midAng);
    const ty = cy + (rArc + 16) * sin(midAng);
    push();
    noStroke();
    fill(0);
    textSize(14);
    textAlign(CENTER, CENTER);
    text(angle.toFixed(1) + '°', tx, ty);
    pop();

    // Small arrowhead at the Alexandria observation point, pointing inward
    const thetaToCenter = atan2(cy - ay, cx - ax);
    // shift slightly inward so it's visible atop the circle edge
    const inx = ax + 2 * cos(thetaToCenter);
    const iny = ay + 2 * sin(thetaToCenter);
    arrowhead(inx, iny, thetaToCenter, 9);
  }

  function drawZoomedView() {
    // Center near bottom-left, large radius to show a sector
    const cx = 90;
    const cy = height - 20;
    const R  = 520;

    // Earth sector as a pie slice from 0 to -angle
    push();
    noStroke();
    fill('#468296');
    arc(cx, cy, R * 2, R * 2, 0, -angle, PIE);
    pop();

    // Radii (dashed)
    const sx = cx + R;
    const sy = cy;
    const ax = cx + R * cos(-angle);
    const ay = cy + R * sin(-angle);

    dashedLine(cx, cy, sx, sy);       // Syene radius (horizontal)
    dashedLine(cx, cy, ax, ay);       // Alexandria radius

    // Labels at arc ends
    push();
    fill(0);
    noStroke();
    textSize(12);
    textAlign(LEFT, BOTTOM);
    text('Syene', sx + 6, sy + 2);
    textAlign(LEFT, CENTER);
    text('Alexandria', ax + 8, ay);
    // Earth label
    textAlign(LEFT, TOP);
    textSize(22);
    text('Earth', cx + 14, cy - 110);
    pop();

    // Angle arc at center
    const rArc = R * 0.38;
    push();
    noFill();
    stroke(0);
    strokeWeight(2);
    arc(cx, cy, rArc * 2, rArc * 2, 0, -angle);
    pop();

    // Angle text
    const midAng = -angle / 2;
    const tx = cx + (rArc + 24) * cos(midAng);
    const ty = cy + (rArc + 24) * sin(midAng);
    push();
    noStroke();
    fill(0);
    textSize(14);
    textAlign(CENTER, CENTER);
    text(angle.toFixed(1) + '°', tx, ty);
    pop();

    // Post and shadow triangle at Alexandria (outside the arc)
    const ux = (ax - cx) / R; // unit radial outward
    const uy = (ay - cy) / R;
    const p0x = ax + ux * 8;     // right angle vertex just outside surface
    const p0y = ay + uy * 8;
    const postLen = 34;
    const shadowLen = 30;

    const p1x = p0x + ux * postLen;      // along the post (radial)
    const p1y = p0y + uy * postLen;

    const p2x = p0x - shadowLen;         // horizontal left (sun ray direction)
    const p2y = p0y;

    push();
    noStroke();
    fill('#D2B48C'); // tan
    triangle(p0x, p0y, p1x, p1y, p2x, p2y);
    pop();
  }
</script>
</body>
</html>
