  <script>
    // Grab DOM elements
    const sliderMoonPos  = document.getElementById('slider-moon-pos');
    const sliderMoonPull = document.getElementById('slider-moon-pull');
    const sliderSunPos   = document.getElementById('slider-sun-pos');
    const sliderSunPull  = document.getElementById('slider-sun-pull');
    const btnReset       = document.getElementById('btn-reset');

    // Attach slider interactions: trigger immediate redraw on input
    const sliders = [sliderMoonPos, sliderMoonPull, sliderSunPos, sliderSunPull];
    sliders.forEach(s => s.addEventListener('input', () => {
      if (typeof redraw === 'function') redraw();
    }));

    // Reset button: restore defaults and redraw
    btnReset.addEventListener('click', () => {
      sliderMoonPos.value  = "144";
      sliderMoonPull.value = "40";
      sliderSunPos.value   = "144";
      sliderSunPull.value  = "20";
      if (typeof redraw === 'function') redraw();
    });

    // -------- p5.js sketch (global mode) ----------
    let earthRadius = 180;
    let baseWaterRadius = 190;      // slightly larger than Earth
    let scalingFactor = 0.6;        // converts "pull" to pixel displacement

    function setup() {
      const c = createCanvas(500, 500);
      c.parent('viz-container');
      pixelDensity(1);
      noLoop();     // draw only when told
      redraw();     // render once using initial slider values
    }

    function draw() {
      background(255); // white

      // Move origin to canvas center
      translate(width / 2, height / 2);

      // Read current slider values
      const moonAngleDeg = parseFloat(sliderMoonPos.value);
      const moonPull     = parseFloat(sliderMoonPull.value);
      const sunAngleDeg  = parseFloat(sliderSunPos.value);
      const sunPull      = parseFloat(sliderSunPull.value);

      const moonAngleRad = radians(moonAngleDeg);
      const sunAngleRad  = radians(sunAngleDeg);

      // Draw water layer (deformed)
      stroke(0);
      strokeWeight(1);
      fill('#aed6f1');

      beginShape();
      for (let deg = 0; deg <= 360; deg += 1) {
        const theta = radians(deg);
        const cosMoon = Math.cos(theta - moonAngleRad);
        const cosSun  = Math.cos(theta - sunAngleRad);

        const moonEffect = moonPull * (cosMoon * cosMoon); // cos^2
        const sunEffect  = sunPull  * (cosSun  * cosSun);  // cos^2

        const totalRadius = baseWaterRadius + scalingFactor * (moonEffect + sunEffect);

        const x = totalRadius * Math.cos(theta);
        const y = totalRadius * Math.sin(theta);
        vertex(x, y);
      }
      endShape(CLOSE);

      // Draw Earth on top
      noStroke();
      fill('#966F33');
      ellipse(0, 0, earthRadius * 2, earthRadius * 2);
    }
  </script>
</body>
