  <script>
  // Utility
  const deg2rad = d => d * Math.PI / 180;

  function ellipsePoint(a, e, theta){
    const c = a * e;
    const b = a * Math.sqrt(1 - e*e);
    const x = a * Math.cos(theta) - c; // translate so Sun (focus) is at origin
    const z = b * Math.sin(theta);
    return new THREE.Vector3(x, 0, z);
  }

  // DOM
  const telCanvas = document.getElementById('telescopic-canvas');
  const orbCanvas = document.getElementById('orbit-canvas');

  const sliderEarth = document.getElementById('slider-earth-position');
  const sliderComet = document.getElementById('slider-comet-position');
  const sliderComa  = document.getElementById('slider-coma');
  const sliderTail  = document.getElementById('slider-tail');
  const sliderSpread= document.getElementById('slider-tail-spread');

  // Renderers
  const telRenderer = new THREE.WebGLRenderer({ canvas: telCanvas, antialias:true, alpha:false, powerPreference:'high-performance' });
  const orbRenderer = new THREE.WebGLRenderer({ canvas: orbCanvas, antialias:true, alpha:false, powerPreference:'high-performance' });
  telRenderer.setPixelRatio(Math.min(window.devicePixelRatio, 2));
  orbRenderer.setPixelRatio(Math.min(window.devicePixelRatio, 2));

  // Scenes
  const telescopeScene = new THREE.Scene();
  telescopeScene.background = new THREE.Color(0x000000);

  const orbitScene = new THREE.Scene();
  orbitScene.background = new THREE.Color(0xffffff);

  // Cameras
  const telescopeCamera = new THREE.PerspectiveCamera(15, 1, 0.01, 2000);
  telescopeScene.add(telescopeCamera);

  const orbitCamera = new THREE.PerspectiveCamera(45, 1, 0.1, 4000);
  orbitCamera.position.set(0, 26, 26);
  orbitCamera.lookAt(0, 0, 0);
  orbitScene.add(orbitCamera);

  // Lights
  const telAmbient = new THREE.AmbientLight(0xffffff, 0.9);
  telescopeScene.add(telAmbient);

  const orbAmbient = new THREE.AmbientLight(0xffffff, 0.6);
  orbitScene.add(orbAmbient);
  const sunLight = new THREE.PointLight(0xffffff, 1.0, 0, 2);
  sunLight.position.set(0,0,0);
  orbitScene.add(sunLight);

  // Sun
  const sun = new THREE.Mesh(
    new THREE.SphereGeometry(0.45, 32, 16),
    new THREE.MeshBasicMaterial({ color: 0xffcc33 })
  );
  orbitScene.add(sun);

  // Helper to draw orbits (top-down XZ plane)
  function makeOrbitLine(a, e, color){
    const pts = [];
    for(let i=0;i<=256;i++){
      const t = i/256 * Math.PI*2;
      const v = ellipsePoint(a, e, t);
      pts.push(new THREE.Vector3(v.x, 0, v.z));
    }
    const geom = new THREE.BufferGeometry().setFromPoints(pts);
    const mat  = new THREE.LineBasicMaterial({ color, linewidth: 1 });
    return new THREE.LineLoop(geom, mat);
  }

  // Draw orbits
  const orbitEarth   = makeOrbitLine(4.0, 0.0167, 0x2176ff);
  const orbitMars    = makeOrbitLine(6.1, 0.0934, 0xff3b30);
  const orbitJupiter = makeOrbitLine(20.8, 0.0489, 0xff9500);
  const orbitComet   = makeOrbitLine(14.4, 0.432, 0xffd60a);
  orbitScene.add(orbitEarth, orbitMars, orbitJupiter, orbitComet);

