  // Comet: nucleus, coma, tail (as a single THREE.Group)
  const cometGroup = new THREE.Group();

  const nucleus = new THREE.Mesh(
    new THREE.SphereGeometry(0.18, 20, 12),
    new THREE.MeshBasicMaterial({ color: 0xffffff })
  );
  cometGroup.add(nucleus);

  const comaMat = new THREE.MeshBasicMaterial({
    color: 0xd6ff75,
    transparent: true,
    opacity: 0.35,
    blending: THREE.AdditiveBlending,
    depthWrite: false
  });
  const coma = new THREE.Mesh(new THREE.SphereGeometry(0.6, 32, 24), comaMat);
  cometGroup.add(coma);

  // Tail geometry: apex at origin, extends along +Y by default
  const baseTailHeight = 2.0;
  const tailGeom = new THREE.ConeGeometry(0.35, baseTailHeight, 32, 1, true);
  tailGeom.translate(0, -baseTailHeight/2, 0); // place apex at (0,0,0)
  const tailMat = new THREE.MeshBasicMaterial({
    color: 0xffe08a,
    transparent: true,
    opacity: 0.4,
    blending: THREE.AdditiveBlending,
    side: THREE.DoubleSide,
    depthWrite: false
  });
  const tail = new THREE.Mesh(tailGeom, tailMat);
  cometGroup.add(tail);

  // Add comet to orbit scene (it will be reparented between scenes during render)
  orbitScene.add(cometGroup);

  // State and update logic
  const orbitParams = {
    earth: { a: 4.0, e: 0.0167 },
    comet: { a: 14.4, e: 0.432 }
  };

  const tmpDir = new THREE.Vector3();
  function mapRange(v, a1, a2, b1, b2){
    const t = (v - a1) / (a2 - a1);
    return b1 + (b2 - b1) * t;
  }

  function update(){
    // Earth along its orbit
    const thetaE = deg2rad(parseFloat(sliderEarth.value));
    const earthPos = ellipsePoint(orbitParams.earth.a, orbitParams.earth.e, thetaE);
    earth.position.copy(earthPos);

    // Telescope camera from Earth's position (slight elevation)
    telescopeCamera.position.copy(earthPos.clone().add(new THREE.Vector3(0, 0.6, 0)));

    // Comet along its orbit
    const thetaC = deg2rad(parseFloat(sliderComet.value));
    const cometPos = ellipsePoint(orbitParams.comet.a, orbitParams.comet.e, thetaC);
    cometGroup.position.copy(cometPos);

    // Tail orientation: away from Sun (from comet toward same direction as vector from Sun to comet)
    tmpDir.copy(cometPos).normalize(); // direction away from Sun
    const q = new THREE.Quaternion().setFromUnitVectors(new THREE.Vector3(0,1,0), tmpDir);
    tail.setRotationFromQuaternion(q);

    // Coma scale and brightness
    const comaScale = mapRange(parseFloat(sliderComa.value), 0, 100, 0.01, 4.0);
    coma.scale.set(comaScale, comaScale, comaScale);
    coma.material.opacity = mapRange(parseFloat(sliderComa.value), 0, 100, 0.15, 0.65);

    // Tail length and spread
    const lenScale = mapRange(parseFloat(sliderTail.value), 0, 100, 0.001, 5.0);
    const radialScale = mapRange(parseFloat(sliderSpread.value), 0, 100, 0.15, 3.0);
    tail.scale.set(radialScale, lenScale, radialScale);
    tail.visible = lenScale > 0.002;

    // Telescope camera target: comet nucleus
    telescopeCamera.lookAt(cometPos);
    orbitCamera.lookAt(0,0,0);
  }

  // Resize handling
  function resizeRenderers(){
    const resizeRendererToDisplaySize = (renderer, canvas) => {
      const width = Math.floor(canvas.clientWidth);
      const height = Math.floor(canvas.clientHeight);
      if (canvas.width !== width || canvas.height !== height){
        renderer.setSize(width, height, false);
        return {width, height};
      }
      return null;
    };

    const telSize = resizeRendererToDisplaySize(telRenderer, telCanvas);
    if (telSize){
      telescopeCamera.aspect = telSize.width / telSize.height;
      telescopeCamera.updateProjectionMatrix();
    }
    const orbSize = resizeRendererToDisplaySize(orbRenderer, orbCanvas);
    if (orbSize){
      orbitCamera.aspect = orbSize.width / orbSize.height;
      orbitCamera.updateProjectionMatrix();
    }
  }
  window.addEventListener('resize', resizeRenderers);

  // Animate loop: render both scenes, reparenting the same comet Group
  function animate(){
    requestAnimationFrame(animate);
    resizeRenderers();

    // Render telescopic scene with the shared comet instance
    if (cometGroup.parent !== telescopeScene) telescopeScene.add(cometGroup);
    telRenderer.render(telescopeScene, telescopeCamera);

    // Reparent to orbit scene and render the orbit view
    if (cometGroup.parent !== orbitScene) orbitScene.add(cometGroup);
    orbRenderer.render(orbitScene, orbitCamera);
  }

  // Event listeners
  [sliderEarth, sliderComet, sliderComa, sliderTail, sliderSpread].forEach(sl => {
    sl.addEventListener('input', update);
  });

  // Initial setup and start
  update();
  resizeRenderers();
  animate();
  </script>
</body>
</html>
