    }

    sliderM.addEventListener('input', updateLabels);
    sliderX.addEventListener('input', updateLabels);
    sliderY.addEventListener('input', updateLabels);

    // Initialize labels once DOM is ready
    updateLabels();

    // ---- p5.js sketch (global mode) ----
    let leftAnchor, rightAnchor;
    const L = 10;      // logical distance between posts
    const g = 9.81;    // gravity m/s^2

    function setup() {
      const cnv = createCanvas(800, 500);
      cnv.parent('viz');
      textFont('Arial, Helvetica, sans-serif');
      textSize(14);
