      // Geometry mapping from logical (x,y) to pixels
      const scaleX = (rightAnchor.x - leftAnchor.x) / L; // 600 / 10 = 60 px/unit
      const scaleY = 60;                                  // px per unit vertically

      // Mass node (rope junction) in pixel coords
      const mx = leftAnchor.x + x * scaleX;
      const my = leftAnchor.y + y * scaleY;

      // Physics
      const theta1 = Math.atan(y / Math.max(x, 1e-6));
      const theta2 = Math.atan(y / Math.max(L - x, 1e-6));
      const Fg = m * g;
      const denom = Math.max(Math.sin(theta1 + theta2), 1e-9);
      const T1 = (Fg * Math.cos(theta2)) / denom;
      const T2 = (Fg * Math.cos(theta1)) / denom;

      // Visual elements
      drawBackdrop();
      drawDashedLine(leftAnchor.x, leftAnchor.y, rightAnchor.x, rightAnchor.y, 8, 6);

      // Ropes
      stroke(0);
      strokeWeight(1.6);
      line(leftAnchor.x, leftAnchor.y, mx, my);
      line(rightAnchor.x, rightAnchor.y, mx, my);

      // Short vertical string and mass
      const stringLen = 28;
      line(mx, my, mx, my + stringLen);
      noStroke();
      fill(0);
      const rMass = 14;
      ellipse(mx, my + stringLen + rMass, rMass * 2, rMass * 2);

      // Tension labels near the ropes
      fill(0);
      noStroke();
      textAlign(CENTER, BOTTOM);
      const ltx = lerp(leftAnchor.x, mx, 0.45);
      const lty = lerp(leftAnchor.y, my, 0.45) - 8;
      text('T₁', ltx, lty);
      const rtx = lerp(rightAnchor.x, mx, 0.50);
      const rty = lerp(rightAnchor.y, my, 0.50) - 8;
      text('T₂', rtx, rty);

      // Angle arcs
      stroke(0);
      noFill();
      strokeWeight(1.4);
      const rArc = 30;

      // Orientation angles for arcs in screen coordinates (p5 angles increase clockwise)
      const phi1 = Math.atan2(my - leftAnchor.y,  mx - leftAnchor.x);  // near 0..PI/2
      const phi2 = Math.atan2(my - rightAnchor.y, mx - rightAnchor.x); // near ~PI- small

      // Left arc: horizontal to right (0) to rope direction (phi1)
      arc(leftAnchor.x, leftAnchor.y, rArc * 2, rArc * 2, 0, phi1);
      // Right arc: rope direction (phi2) to horizontal to left (PI)
      arc(rightAnchor.x, rightAnchor.y, rArc * 2, rArc * 2, phi2, Math.PI);

      // Angle labels positioned along arcs
      noStroke();
      fill(0);
      textAlign(CENTER, CENTER);
      const mid1 = phi1 / 2;
      const ax1 = leftAnchor.x + (rArc + 12) * Math.cos(mid1);
      const ay1 = leftAnchor.y + (rArc + 12) * Math.sin(mid1);
      text('θ₁', ax1, ay1);

      const mid2 = (phi2 + Math.PI) / 2;
      const ax2 = rightAnchor.x + (rArc + 12) * Math.cos(mid2);
      const ay2 = rightAnchor.y + (rArc + 12) * Math.sin(mid2);
      text('θ₂', ax2, ay2);

      // Tension values at top center
      fill(70);
      textAlign(CENTER, TOP);
      textSize(16);
      const tLine = `T₁ = ${T1.toFixed(4)} N  |  T₂ = ${T2.toFixed(4)} N`;
