      // Helper to draw posts after ropes so they stay behind endpoints visually
      function drawBackdrop() {
        // Posts
        noStroke();
        fill(0);
        const topY = 90;
        const postH = height - topY - 60;
        const postW = 10;
        rect(leftAnchor.x - 28, topY, postW, postH, 2);
        rect(rightAnchor.x + 18, topY, postW, postH, 2);

        // Subtle background border line near top area
        stroke(235);
        strokeWeight(1);
        line(8, topY + 0.5, width - 8, topY + 0.5);
        noStroke();
      }
    }

    // Dashed line helper
    function drawDashedLine(x1, y1, x2, y2, dash = 6, gap = 4) {
      stroke(0);
      strokeWeight(1);
      const dx = x2 - x1;
      const dy = y2 - y1;
      const dist = Math.hypot(dx, dy);
      const vx = dx / dist;
      const vy = dy / dist;
      let traveled = 0;
      let cx = x1, cy = y1;
      while (traveled <= dist) {
        const nx = cx + vx * dash;
        const ny = cy + vy * dash;
        line(cx, cy, nx, ny);
        cx = nx + vx * gap;
        cy = ny + vy * gap;
        traveled += dash + gap;
      }
    }
  </script>
