<script>
(() => {
  // -------------------------------
  // State
  // -------------------------------
  let time = 0;               // seconds
  let isRunning = false;
  let animationFrameId = null;
  let lastTs = null;

  // Simulation params and solution object
  let solution = null;        // will hold { x(t), v(t), type, etc. }
  let tSamples = [];          // 0..10
  const T_MAX = 10;

  // DOM refs
  const refs = {
    sliders: {
      x0: document.getElementById('slider-displacement'),
      v0: document.getElementById('slider-velocity'),
      k: document.getElementById('slider-stiffness'),
      m: document.getElementById('slider-mass'),
      c: document.getElementById('slider-damping'),
      Kp: document.getElementById('slider-proportional-gain'),
      Kd: document.getElementById('slider-derivative-gain'),
    },
    values: {
      x0: document.getElementById('value-displacement'),
      v0: document.getElementById('value-velocity'),
      k: document.getElementById('value-stiffness'),
      m: document.getElementById('value-mass'),
      c: document.getElementById('value-damping'),
      Kp: document.getElementById('value-proportional-gain'),
      Kd: document.getElementById('value-derivative-gain'),
    },
    buttons: {
      play: document.getElementById('btn-play'),
      pause: document.getElementById('btn-pause'),
      reset: document.getElementById('btn-reset'),
    },
    plots: {
      x: document.getElementById('plot-displacement'),
      v: document.getElementById('plot-velocity')
    },
    sceneContainer: document.getElementById('scene-container'),
  };

  // Formatting helper
  const fmt = (val) => {
    const num = Number(val);
    const abs = Math.abs(num);
    const digits = abs < 1 ? 2 : 2; // keep two decimals for consistency
    return num.toFixed(digits).replace(/\.?0+$/,''); // trim trailing zeros
  };

  // Read parameters
  function getParams(){
    const m = parseFloat(refs.sliders.m.value);
    const c = parseFloat(refs.sliders.c.value);
    const k = parseFloat(refs.sliders.k.value);
    const Kp = parseFloat(refs.sliders.Kp.value);
    const Kd = parseFloat(refs.sliders.Kd.value);
    const x0 = parseFloat(refs.sliders.x0.value);
    const v0 = parseFloat(refs.sliders.v0.value);

    const k_eff = k + Kp;
    const c_eff = c + Kd;

    return { m, c, k, Kp, Kd, x0, v0, k_eff, c_eff };
  }

  // Analytical solution builder for m x'' + c_eff x' + k_eff x = 0
  function buildSolution(params){
    const {m, k_eff, c_eff, x0, v0} = params;
    const omega_n = Math.sqrt(k_eff / m);
    const zeta = c_eff / (2 * Math.sqrt(m * k_eff));

    // Guard for numerical issues
    const eps = 1e-8;

    if (zeta < 1 - 1e-8) {
      // Underdamped
      const omega_d = omega_n * Math.sqrt(Math.max(0, 1 - zeta*zeta));
      const A = x0;
      const B = (v0 + zeta*omega_n*x0) / omega_d;
      const x = (t) => {
        const E = Math.exp(-zeta*omega_n*t);
        return E * (A * Math.cos(omega_d*t) + B * Math.sin(omega_d*t));
      };
      const v = (t) => {
        const E = Math.exp(-zeta*omega_n*t);
        const cos = Math.cos(omega_d*t);
        const sin = Math.sin(omega_d*t);
        return E * ( -A*omega_d*sin + B*omega_d*cos - zeta*omega_n*(A*cos + B*sin) );
      };
      return {type:'under', omega_n, zeta, omega_d, x, v};
    } else if (Math.abs(zeta - 1) <= 1e-8) {
      // Critically damped
      const x = (t) => {
        return (x0 + (v0 + omega_n*x0)*t) * Math.exp(-omega_n*t);
      };
      const v = (t) => {
        return Math.exp(-omega_n*t) * ( v0 - omega_n*x0 - omega_n*(v0 + omega_n*x0)*t );
      };
      return {type:'critical', omega_n, zeta, x, v};
    } else {
      // Overdamped
      const s = Math.sqrt(zeta*zeta - 1);
      const s1 = -omega_n*(zeta - s);
      const s2 = -omega_n*(zeta + s);
      const denom = (s1 - s2);
      const C1 = (v0 - s2*x0)/denom;
      const C2 = (s1*x0 - v0)/denom;
      const x = (t) => C1*Math.exp(s1*t) + C2*Math.exp(s2*t);
      const v = (t) => s1*C1*Math.exp(s1*t) + s2*C2*Math.exp(s2*t);
      return {type:'over', omega_n, zeta, s1, s2, x, v};
    }
  }

  // Generate samples for plotting
  function computeSamples(sol){
    const N = 1001;
    tSamples = new Array(N);
    const xs = new Array(N);
    const vs = new Array(N);
    for (let i=0;i<N;i++){
      const t = i*(T_MAX/(N-1));
      tSamples[i] = t;
      xs[i] = sol.x(t);
      vs[i] = sol.v(t);
    }
    return {t: tSamples, x: xs, v: vs};
  }

  // -------------------------------
  // Plotly setup
  // -------------------------------
  function drawPlots(samples){
    const lineStyle = {color:'#274b87', width:3};
    const layoutBase = {
      margin:{l:40,r:10,t:10,b:30},
      xaxis:{range:[0, T_MAX], title:'Time (s)'},
      showlegend:false
    };

    Plotly.react(refs.plots.x, [
      {x:samples.t, y:samples.x, mode:'lines', line: lineStyle, hoverinfo:'skip'},
      {x:[0], y:[samples.x[0]], mode:'markers', marker:{size:12, color:'#1f77b4'}, hoverinfo:'skip'}
    ], {
      ...layoutBase,
      yaxis:{range:[-3,3], title:'Displacement (m)'}
    }, {displayModeBar:false, responsive:true});

    Plotly.react(refs.plots.v, [
      {x:samples.t, y:samples.v, mode:'lines', line: lineStyle, hoverinfo:'skip'},
      {x:[0], y:[samples.v[0]], mode:'markers', marker:{size:12, color:'#2ca02c'}, hoverinfo:'skip'}
    ], {
      ...layoutBase,
      yaxis:{range:[-3,3], title:'Velocity (m/s)'}
    }, {displayModeBar:false, responsive:true});
  }

  function updateMarkers(t, x, v){
    Plotly.restyle(refs.plots.x, {'x': [[t]], 'y': [[x]]}, [1]);
    Plotly.restyle(refs.plots.v, {'x': [[t]], 'y': [[v]]}, [1]);
  }

  // -------------------------------
  // THREE.js scene
  // -------------------------------
  let renderer, scene, camera;
  let massMesh, baseMesh, springMesh, boxWire, damperHousing, damperPiston;

  // scene scale and geometry parameters
  const massSize = 0.6;
  const groundY = -0.35;
  const wallX = -1.1;          // fixed wall where spring/damper attach
  const baseSpringLen = 1.0;   // nominal distance from wall to mass left face when x=0
  const springRadius = 0.12;

  // Create a helix curve class along X axis
  class HelixCurve extends THREE.Curve {
    constructor(len=1, radius=0.12, turns=8){ super(); this.len=len; this.radius=radius; this.turns=turns; }
    getPoint(t){
      const x = t*this.len;
      const angle = t * this.turns * Math.PI * 2;
      const y = this.radius * Math.cos(angle);
      const z = this.radius * Math.sin(angle);
      return new THREE.Vector3(x, y, z);
    }
  }

  function initThree(){
    scene = new THREE.Scene();
    scene.background = new THREE.Color(0xffffff);

    const width = refs.sceneContainer.clientWidth;
    const height = refs.sceneContainer.clientHeight;
    camera = new THREE.PerspectiveCamera(35, width/height, 0.1, 100);
    camera.position.set(3.6, 2.6, 3.2);
    camera.lookAt(0, 0, 0);

    renderer = new THREE.WebGLRenderer({antialias:true});
    renderer.setPixelRatio(Math.min(2, window.devicePixelRatio));
    renderer.setSize(width, height);
    refs.sceneContainer.appendChild(renderer.domElement);

    // Lights
    scene.add(new THREE.AmbientLight(0xffffff, 0.6));
    const dir = new THREE.DirectionalLight(0xffffff, 0.7);
    dir.position.set(3,5,4);
    scene.add(dir);

    // Base (green)
    const baseGeom = new THREE.BoxGeometry(3.2, 0.25, 2.0);
    const baseMat = new THREE.MeshStandardMaterial({color:0x2ecc71});
    baseMesh = new THREE.Mesh(baseGeom, baseMat);
    baseMesh.position.set(0, groundY - 0.25/2 - 0.05, 0);
    scene.add(baseMesh);

    // Raised platform
    const platformGeom = new THREE.BoxGeometry(2.2, 0.25, 1.2);
    const platformMat = new THREE.MeshStandardMaterial({color:0x27ae60});
    const platform = new THREE.Mesh(platformGeom, platformMat);
    platform.position.set(0.1, groundY - 0.25/2 + 0.15, 0);
    scene.add(platform);

    // Mass block (yellow)
    const massGeom = new THREE.BoxGeometry(massSize, massSize, massSize);
    const massMat = new THREE.MeshStandardMaterial({color:0xf1c40f});
    massMesh = new THREE.Mesh(massGeom, massMat);
    massMesh.position.set(0, groundY + massSize/2, 0);
    scene.add(massMesh);

    // Spring (red)
    const helixCurve = new HelixCurve(1.0, springRadius, 8);
    const tubeGeom = new THREE.TubeGeometry(helixCurve, 180, 0.04, 12, false);
    const springMat = new THREE.MeshStandardMaterial({color:0xe74c3c, metalness:0.2, roughness:0.4});
    springMesh = new THREE.Mesh(tubeGeom, springMat);
    // Anchor at wallX, align along x:
    springMesh.position.set(wallX, groundY + 0.15, 0);
    scene.add(springMesh);

    // Damper (blue) - housing and piston along x
    const housingGeom = new THREE.CylinderGeometry(0.09, 0.09, 0.6, 24);
    const pistonGeom = new THREE.CylinderGeometry(0.05, 0.05, 0.8, 20);
    const damperMat = new THREE.MeshStandardMaterial({color:0x3498db, metalness:0.1, roughness:0.6});
    damperHousing = new THREE.Mesh(housingGeom, damperMat);
    damperPiston = new THREE.Mesh(pistonGeom, damperMat);
    damperHousing.rotation.z = Math.PI/2;
    damperPiston.rotation.z = Math.PI/2;
    damperHousing.position.set(wallX + 0.15, groundY + 0.0, -0.35);
    damperPiston.position.set(wallX + 0.5, groundY + 0.0, -0.35);
    scene.add(damperHousing);
    scene.add(damperPiston);

    // Wireframe enclosure
    const boxGeom = new THREE.BoxGeometry(3.0, 1.8, 2.0);
    const boxMat = new THREE.MeshBasicMaterial({color:0x888888, wireframe:true, transparent:true, opacity:0.7});
    boxWire = new THREE.Mesh(boxGeom, boxMat);
    boxWire.position.set(0, groundY + 0.5, 0);
    scene.add(boxWire);

    window.addEventListener('resize', onWindowResize);
    renderThree();
  }

  function onWindowResize(){
    const width = refs.sceneContainer.clientWidth;
    const height = refs.sceneContainer.clientHeight;
    camera.aspect = width/height;
    camera.updateProjectionMatrix();
    renderer.setSize(width, height);
    renderThree();

    // Force plotly to relayout (it is responsive but we ensure)
    Plotly.Plots.resize(refs.plots.x);
    Plotly.Plots.resize(refs.plots.v);
  }

  function renderThree(){
    renderer.render(scene, camera);
  }

  // Update 3D elements based on displacement x (meters)
  function update3D(x){
    // Move mass
    massMesh.position.x = x;

    // Update spring length by scaling along x to distance from wall to mass left face
    const leftFace = massMesh.position.x - massSize/2;
    const currentLen = Math.max(0.15, baseSpringLen + leftFace - wallX); // avoid negative or too small
    const scaleX = currentLen / 1.0; // our helix curve base len = 1
    springMesh.scale.set(scaleX, 1, 1);
    // Place spring so its start remains at the wall
    // After scaling, the spring's local origin remains at its start (x=0), so we don't need to shift.

    // Damper piston should connect to mass left face
    damperPiston.position.x = leftFace - 0.1; // slight embed
    renderThree();
  }

  // -------------------------------
  // UI handlers
  // -------------------------------
  function setValueDisplays(){
    const p = getParams();
    refs.values.x0.textContent = fmt(p.x0);
    refs.values.v0.textContent = fmt(p.v0);
    refs.values.k.textContent  = fmt(p.k);
    refs.values.m.textContent  = fmt(p.m);
    refs.values.c.textContent  = fmt(p.c);
    refs.values.Kp.textContent = fmt(p.Kp);
    refs.values.Kd.textContent = fmt(p.Kd);
  }

  function recomputeAll(){
    pauseAnim();
    time = 0;

    const params = getParams();
    solution = buildSolution(params);
    const samples = computeSamples(solution);
    drawPlots(samples);
    updateMarkers(0, samples.x[0], samples.v[0]);
    update3D(samples.x[0]);
  }

  Object.values(refs.sliders).forEach(sl => {
    sl.addEventListener('input', () => {
      setValueDisplays();
      recomputeAll();
    });
  });

  refs.buttons.play.addEventListener('click', () => {
    if (!isRunning){
      isRunning = true;
      lastTs = null;
      animationFrameId = requestAnimationFrame(loop);
    }
  });

  refs.buttons.pause.addEventListener('click', pauseAnim);

  function pauseAnim(){
    isRunning = false;
    if (animationFrameId !== null) cancelAnimationFrame(animationFrameId);
    animationFrameId = null;
    lastTs = null;
  }

  refs.buttons.reset.addEventListener('click', () => {
    pauseAnim();
    time = 0;
    const x = solution ? solution.x(0) : 0;
    const v = solution ? solution.v(0) : 0;
    updateMarkers(0, x, v);
    update3D(x);
  });

  function loop(ts){
    if (!lastTs) lastTs = ts;
    const dt = Math.min(0.05, (ts - lastTs)/1000); // clamp dt
    lastTs = ts;

    time += dt;
    if (time > T_MAX){
      time = T_MAX;
      isRunning = false;
    }

    const x = solution.x(time);
    const v = solution.v(time);
    updateMarkers(time, x, v);
    update3D(x);

    if (isRunning){
      animationFrameId = requestAnimationFrame(loop);
    }
  }

  // -------------------------------
  // Initialize everything
  // -------------------------------
  function init(){
    initThree();
    // Initial displays
    setValueDisplays();
    solution = buildSolution(getParams());
    const samples = computeSamples(solution);
    drawPlots(samples);
    update3D(samples.x[0]);
    updateMarkers(0, samples.x[0], samples.v[0]);
  }

  init();

})();
</script>
</body>
</html>
