    <div id="displacement-plot-container"></div>
    <div id="velocity-plot-container"></div>
  </div>
</div>

<script>
  // Global simulation state
  let isAnimating = false;
  let t = 0;                // current time (seconds), wraps in [0,5]
  const tMax = 5.0;
  const plotDt = 0.01;      // precomputed time step for plots
  let tArray = [];          // [0..5] samples

  // Physical and modal data
  let params = { T: 50, x: [0.1, 0.1, 0.0], m: [1.0, 1.0, 1.0] };
  let M, K, A, c, w;        // M (3x3), K (3x3), mode shapes A (3x3), participation c (3), angular freqs w (3)

  // Precomputed for plots
  let dispData = { y1: [], y2: [], y3: [] };
  let velData  = { v1: [], v2: [], v3: [] };

  // Color palette consistent across views
  const colors = ['#1f77b4', '#ff7f0e', '#2ca02c'];

  // Utility: create diagonal matrix from [a,b,c]
  function diag3(a, b, c) {
    return [[a,0,0],[0,b,0],[0,0,c]];
  }

  // Utility: get column i of matrix A (3x3) as vector length 3
  function col(A, i) {
    return [A[0][i], A[1][i], A[2][i]];
  }

  // Utility: vector add: v + s * w
  function vAdd(a, b) { return [a[0]+b[0], a[1]+b[1], a[2]+b[2]]; }
  function vScale(s, v) { return [s*v[0], s*v[1], s*v[2]]; }

  function readSliders() {
    params.T = parseFloat(document.getElementById('slider-tension').value);
    params.x = [
      parseFloat(document.getElementById('slider-x1').value),
      parseFloat(document.getElementById('slider-x2').value),
      parseFloat(document.getElementById('slider-x3').value)
    ];
    params.m = [
      parseFloat(document.getElementById('slider-m1').value),
      parseFloat(document.getElementById('slider-m2').value),
      parseFloat(document.getElementById('slider-m3').value)
    ];
    updateBadges(); // update numerical badges next to sliders
  }

  function updateBadges() {
    const map = {
      'slider-tension': params.T.toFixed(0),
      'slider-x1': params.x[0].toFixed(2),
      'slider-x2': params.x[1].toFixed(2),
      'slider-x3': params.x[2].toFixed(2),
      'slider-m1': params.m[0].toFixed(2),
      'slider-m2': params.m[1].toFixed(2),
      'slider-m3': params.m[2].toFixed(2),
    };
    document.querySelectorAll('.value-badge').forEach(el => {
      const key = el.getAttribute('data-for');
      if (map[key] !== undefined) el.textContent = map[key];
    });
  }

  // Solve generalized eigenproblem K a = w^2 M a using numeric.js
  function computeModes() {
    M = diag3(params.m[0], params.m[1], params.m[2]);
    const Kbase = [[ 2, -1,  0],
                   [-1,  2, -1],
                   [ 0, -1,  2]];
    K = numeric.mul(params.T, Kbase);

    // Transform to standard eigenproblem: C z = lambda z with C = M^(-1/2) K M^(-1/2)
    const MinvHalf = diag3(1/Math.sqrt(params.m[0]), 1/Math.sqrt(params.m[1]), 1/Math.sqrt(params.m[2]));
    const C = numeric.dot(numeric.dot(MinvHalf, K), MinvHalf);

    const eig = numeric.eig(C);
    let evals = eig.lambda.x.slice();      // array of 3 eigenvalues (real)
    let V = eig.E.x.map(row => row.slice()); // 3x3 eigenvectors (columns)

    // Sort modes by ascending eigenvalue
    const idx = [0,1,2].sort((i,j) => evals[i] - evals[j]);
    evals = idx.map(i => evals[i]);
    V = V.map(row => idx.map(j => row[j]));

    // Convert back to original coordinates: A = M^(-1/2) * V
    A = numeric.dot(MinvHalf, V);

    // Angular frequencies
    w = evals.map(lam => Math.sqrt(Math.max(0, lam)));

    // Participation factors c from y(0) = A * c (solve linear system)
    const y0 = params.x.slice();
    c = numeric.solve(A, y0);
  }

  function precomputeTimeSeries() {
    // Build tArray from 0 to tMax inclusive
    tArray = [];
    for (let tt = 0; tt <= tMax + 1e-9; tt += plotDt) tArray.push(parseFloat(tt.toFixed(6)));

    dispData = { y1: [], y2: [], y3: [] };
    velData  = { v1: [], v2: [], v3: [] };

    for (const tt of tArray) {
      const y = currentDisplacement(tt);
      const v = currentVelocity(tt);
      dispData.y1.push(y[0]); dispData.y2.push(y[1]); dispData.y3.push(y[2]);
      velData.v1.push(v[0]);  velData.v2.push(v[1]);  velData.v3.push(v[2]);
    }
  }

  function currentDisplacement(tt) {
    // y(tt) = sum_i c_i * A[:,i] * cos(w_i * tt)
    let y = [0,0,0];
    for (let i=0;i<3;i++) {
      const Ai = col(A, i);
      const factor = c[i] * Math.cos(w[i]*tt);
      y = vAdd(y, vScale(factor, Ai));
    }
    return y;
  }

  function currentVelocity(tt) {
    // v(tt) = sum_i -c_i * w_i * A[:,i] * sin(w_i * tt)
    let v = [0,0,0];
    for (let i=0;i<3;i++) {
      const Ai = col(A, i);
      const factor = -c[i] * w[i] * Math.sin(w[i]*tt);
      v = vAdd(v, vScale(factor, Ai));
    }
    return v;
  }

  // Plotly: draw or update both plots
  function drawPlots() {
    const dispDiv = document.getElementById('displacement-plot-container');
    const velDiv  = document.getElementById('velocity-plot-container');

    const dispTraces = [
      { x: tArray, y: dispData.y1, mode: 'lines', name: 'y₁(t)', line: {color: colors[0]} },
      { x: tArray, y: dispData.y2, mode: 'lines', name: 'y₂(t)', line: {color: colors[1]} },
      { x: tArray, y: dispData.y3, mode: 'lines', name: 'y₃(t)', line: {color: colors[2]} },
    ];
    const velTraces = [
      { x: tArray, y: velData.v1, mode: 'lines', name: 'v₁(t)', line: {color: colors[0]} },
      { x: tArray, y: velData.v2, mode: 'lines', name: 'v₂(t)', line: {color: colors[1]} },
      { x: tArray, y: velData.v3, mode: 'lines', name: 'v₃(t)', line: {color: colors[2]} },
    ];

    const commonLayout = (title) => ({
      title: {text: title, font: {size: 13}},
      margin: {l: 50, r: 10, t: 25, b: 35},
      xaxis: {title: 'time t', range: [0, tMax], zeroline: false},
      yaxis: {zeroline: true},
      showlegend: true,
      legend: {orientation: 'h', x: 0, y: 1.1},
      shapes: [{
        type: 'line',
        x0: t, x1: t,
        y0: 0, y1: 1,
        xref: 'x',
        yref: 'paper',
        line: {color: 'rgba(0,0,0,0.5)', width: 2, dash: 'dot'}
      }],
    });

    Plotly.react(dispDiv, dispTraces, commonLayout('displacement'), {responsive: true});
    Plotly.react(velDiv,  velTraces,  commonLayout('velocity'),     {responsive: true});
  }

  function updateTimeMarkers() {
    const dispDiv = document.getElementById('displacement-plot-container');
    const velDiv  = document.getElementById('velocity-plot-container');

    // Update shape 0 (vertical line) x0/x1 to current t
    Plotly.relayout(dispDiv, {'shapes[0].x0': t, 'shapes[0].x1': t});
    Plotly.relayout(velDiv,  {'shapes[0].x0': t, 'shapes[0].x1': t});
  }

  // Reset logic: pause, t=0, recompute physics and plots, redraw initial canvas
  function resetSimulation() {
    isAnimating = false;
    t = 0;
    document.getElementById('btn-play-pause').textContent = '▶';

    readSliders();
    computeModes();
    precomputeTimeSeries();
    drawPlots();

    if (p5Sketch) {
      p5Sketch.noLoop();
      p5Sketch.redraw(); // draw initial state
    }
  }

  // Hook up slider changes to reset
  const sliderIds = [
    'slider-tension', 'slider-x1', 'slider-x2', 'slider-x3',
    'slider-m1', 'slider-m2', 'slider-m3'
  ];
  sliderIds.forEach(id => {
    document.addEventListener('input', (ev) => {
      if (ev.target && ev.target.id === id) {
        resetSimulation();
      }
    });
  });

  // Buttons
  document.getElementById('btn-play-pause').addEventListener('click', () => {
    isAnimating = !isAnimating;
    document.getElementById('btn-play-pause').textContent = isAnimating ? '❚❚' : '▶';
    if (isAnimating) {
      if (p5Sketch) p5Sketch.loop();
    } else {
      if (p5Sketch) p5Sketch.noLoop();
    }
  });
  document.getElementById('btn-reset').addEventListener('click', resetSimulation);

  // p5.js sketch for main animation
  let p5Sketch = null;
  const sketch = (p) => {
    let W = 800, H = 360;
    const margin = {left: 48, right: 18, top: 18, bottom: 40};

    function resizeCanvasToContainer() {
      const container = document.getElementById('main-animation-container');
      W = container.clientWidth;
      H = container.clientHeight;
      p.resizeCanvas(W, H);
    }

    function x2px(x) {
      const innerW = W - margin.left - margin.right;
      return margin.left + (x / 4.0) * innerW;
    }
    function y2px(y) {
      // world y in [-1,1], pixel with origin at top-left
      const innerH = H - margin.top - margin.bottom;
      return margin.top + (1 - (y + 1) / 2) * innerH;
    }

    function drawGrid() {
      p.push();
      p.stroke(220);
      p.strokeWeight(1);
      // Vertical grid at x=0..4
      for (let i=0;i<=4;i++) {
        const x = x2px(i);
        p.line(x, y2px(-1), x, y2px(1));
      }
      // Horizontal grid at y=-1,-0.5,0,0.5,1
      for (let j=-1; j<=1; j+=0.5) {
        const y = y2px(j);
        p.line(x2px(0), y, x2px(4), y);
      }
      p.pop();

      // Axes
      p.push();
      p.stroke(0);
      p.strokeWeight(1.5);
      // x-axis y=0
      p.line(x2px(0), y2px(0), x2px(4), y2px(0));
      // y axis ticks
      p.fill(0);
      p.noStroke();
      p.textSize(11);
      for (let i=0;i<=4;i++) {
        const x = x2px(i);
        p.stroke(0);
        p.line(x, y2px(0)-4, x, y2px(0)+4);
        p.noStroke();
        p.textAlign(p.CENTER, p.TOP);
        p.text(i, x, y2px(-1)+2);
      }
      for (let j=-1; j<=1; j+=0.5) {
        const y = y2px(j);
        p.stroke(0);
        p.line(x2px(0)-4, y, x2px(0)+4, y);
        p.noStroke();
        p.textAlign(p.RIGHT, p.CENTER);
        p.text(j.toFixed(1), x2px(0)-6, y);
      }
      p.pop();
    }

    p.setup = function() {
      const container = document.getElementById('main-animation-container');
      p.createCanvas(container.clientWidth, container.clientHeight).parent('main-animation-container');
      p.pixelDensity(1);
      p.noLoop(); // start paused
    };

    p.windowResized = function() {
      resizeCanvasToContainer();
      p.redraw();
    };

    p.draw = function() {
      // Time update if animating
      if (isAnimating) {
        t += p.deltaTime / 1000;
        while (t > tMax) t -= tMax;
        updateTimeMarkers();
      }

      // Current displacements
      const y = currentDisplacement(t);

      p.background(255);
      drawGrid();

      // Draw string: (0,0) -> (1,y1) -> (2,y2) -> (3,y3) -> (4,0)
      p.push();
      p.stroke(0);
      p.strokeWeight(2);
      p.noFill();

      const pts = [
        [0,0],
        [1,y[0]],
        [2,y[1]],
        [3,y[2]],
        [4,0]
      ];
      for (let i=0; i<pts.length-1; i++) {
        p.line(x2px(pts[i][0]), y2px(pts[i][1]), x2px(pts[i+1][0]), y2px(pts[i+1][1]));
      }
      p.pop();

      // Draw masses at x=1,2,3 with radius proportional to sqrt(m)
      const rScale = 12; // pixels per sqrt(kg) approx
      const masses = params.m;
      const xs = [1,2,3];
      p.noStroke();
      p.fill(220,0,0);
      for (let i=0;i<3;i++) {
        const r = rScale * Math.sqrt(masses[i]);
        p.circle(x2px(xs[i]), y2px(y[i]), r);
      }
    };
  };

  // Instantiate p5 sketch
  p5Sketch = new p5(sketch);

  // Initialize everything on first load
  window.addEventListener('load', () => {
    updateBadges();
    resetSimulation();
  });
</script>

</body>
</html>
