    }

    #explanation-panel {
      min-height: 60px;
      padding: 12px 6px;
      color: #222;
    }
    #explanation-text {
      white-space: pre-line;
      margin: 0;
    }
  </style>
</head>
<body>
  <div id="app-container">
    <div id="header-controls">
      <button id="btn-move" class="active">move the points</button>
      <button id="btn-start-algo">start the algorithm</button>
    </div>

    <div id="step-controls">
      <div class="step-group">
        <button disabled>step 1</button>
        <button id="btn-step1-explain" disabled>explain</button>
        <button id="btn-step1-do" disabled>do it</button>
      </div>
      <div class="step-group">
        <button disabled>step 2</button>
        <button id="btn-step2-explain" disabled>explain</button>
        <button id="btn-step2-do" disabled>do it</button>
      </div>
      <div class="step-group" id="step3-controls-container">
        <button disabled>step 3</button>
        <button id="btn-step3-explain" disabled>explain</button>
        <div id="step3-actions" style="display: inline-block;"></div>
      </div>
    </div>

    <div id="canvas-container"></div>

    <div id="explanation-panel">
      <p id="explanation-text"></p>
    </div>
  </div>

  <script>
    // Global state variables (as required)
    let mode = 'move'; // 'move' or 'algorithm'
    let points = []; // array of p5.Vector (initialized in setup)
    let draggedPoint = null;

    let algorithmState = {
      step: 0,
      hullPointsIndices: [],
      fullHull: [],
      leftmostPointIndex: null,
      currentPointIndex: null,
      candidateLines: [],
      explanationSteps: [],
      hullClosed: false
    };

    // Initial coordinates to match the spec (within a 700x500 canvas)
    const initialCoords = [
      {x: 150, y: 350},
      {x: 250, y: 150},
      {x: 350, y: 250},
      {x: 550, y: 180},
      {x: 480, y: 320},
      {x: 420, y: 400}
    ];

    // UI elements
    const btnMove = document.getElementById('btn-move');
    const btnStart = document.getElementById('btn-start-algo');

    const btnStep1Explain = document.getElementById('btn-step1-explain');
    const btnStep1Do = document.getElementById('btn-step1-do');

    const btnStep2Explain = document.getElementById('btn-step2-explain');
    const btnStep2Do = document.getElementById('btn-step2-do');

    const btnStep3Explain = document.getElementById('btn-step3-explain');
    const step3Actions = document.getElementById('step3-actions');

    const explanationText = document.getElementById('explanation-text');

    function resetAlgorithmState() {
      algorithmState = {
        step: 0,
        hullPointsIndices: [],
        fullHull: [],
        leftmostPointIndex: null,
        currentPointIndex: null,
        candidateLines: [],
        explanationSteps: [],
        hullClosed: false
      };
      updateExplanationText();
    }

    function setHeaderActive(modeName) {
      if (modeName === 'move') {
        btnMove.classList.add('active');
        btnStart.classList.remove('active');
      } else {
        btnStart.classList.add('active');
        btnMove.classList.remove('active');
      }
    }

    function disableAllStepButtons() {
      btnStep1Explain.disabled = true;
      btnStep1Do.disabled = true;
      btnStep2Explain.disabled = true;
      btnStep2Do.disabled = true;
      btnStep3Explain.disabled = true;
      // Clear dynamic step 3 buttons
      step3Actions.innerHTML = '';
    }

    function enableStep1() {
      btnStep1Explain.disabled = false;
      btnStep1Do.disabled = false;
      btnStep2Explain.disabled = true;
      btnStep2Do.disabled = true;
      btnStep3Explain.disabled = true;
      // Step 3 dynamic buttons will be handled separately
    }

    function enableStep2() {
      btnStep1Explain.disabled = true;
      btnStep1Do.disabled = true;
      btnStep2Explain.disabled = false;
      btnStep2Do.disabled = false;
      btnStep3Explain.disabled = true;
    }

    function enableStep3() {
      btnStep1Explain.disabled = true;
      btnStep1Do.disabled = true;
      btnStep2Explain.disabled = true;
      btnStep2Do.disabled = true;
      btnStep3Explain.disabled = false;
      // Step 3 action buttons: only the first ("do it") enabled initially
      const first = document.getElementById('btn-step3-do');
      if (first) first.disabled = false;
      // All repeats disabled initially
      const repeats = step3Actions.querySelectorAll('button[id^="btn-step3-repeat-"]');
      repeats.forEach(btn => btn.disabled = true);
    }

    function updateExplanationText() {
      explanationText.textContent = algorithmState.explanationSteps.join('\n');
    }

    // Event handlers for header controls
    btnMove.addEventListener('click', () => {
      mode = 'move';
      setHeaderActive('move');
      resetAlgorithmState();
      disableAllStepButtons();
    });

    btnStart.addEventListener('click', () => {
      mode = 'algorithm';
      setHeaderActive('algorithm');
      resetAlgorithmState();
      // Compute convex hull silently using Jarvis March
      algorithmState.fullHull = computeConvexHullIndices(points);
      // Prepare dynamic Step 3 buttons
      buildStep3Actions();
      enableStep1();
    });

    // Build Step 3 action buttons dynamically:
    // one "do it", then (fullHull.length - 2) "repeat" buttons
    function buildStep3Actions() {
      step3Actions.innerHTML = '';
      if (!algorithmState.fullHull || algorithmState.fullHull.length < 2) return;

      // First action button for step 3
      const first = document.createElement('button');
      first.id = 'btn-step3-do';
      first.textContent = 'do it';
      first.disabled = true; // will be enabled when Step 3 starts
      first.addEventListener('click', () => {
        // k = 2 (third vertex)
        const nextId = algorithmState.fullHull.length - 2 >= 1 ? 'btn-step3-repeat-1' : null;
        handleStep3Action(2, 'btn-step3-do', nextId);
      });
      step3Actions.appendChild(first);

      // Remaining repeat buttons
      const countRepeats = Math.max(algorithmState.fullHull.length - 2, 0);
      for (let i = 1; i <= countRepeats; i++) {
        const b = document.createElement('button');
        b.id = `btn-step3-repeat-${i}`;
        b.textContent = 'repeat';
        b.disabled = true;
        const k = 2 + i; // which vertex in fullHull this button adds
        const nextId = (i + 1) <= countRepeats ? `btn-step3-repeat-${i + 1}` : null;
        b.addEventListener('click', () => handleStep3Action(k, b.id, nextId));
        step3Actions.appendChild(b);
      }
    }

    // Step buttons logic
    btnStep1Explain.addEventListener('click', () => {
      algorithmState.explanationSteps.push('Step 1. Find the leftmost point.');
      updateExplanationText();
    });

    btnStep1Do.addEventListener('click', () => {
      // Find leftmost point
      const leftIdx = findLeftmostIndex(points);
      algorithmState.leftmostPointIndex = leftIdx;
      algorithmState.currentPointIndex = leftIdx;
      algorithmState.hullPointsIndices = [leftIdx];
      algorithmState.step = 1;
      enableStep2();
    });

    btnStep2Explain.addEventListener('click', () => {
      algorithmState.explanationSteps.push(
        'Step 2. Find the point clockwise furthest to the right relative to the leftmost point.'
      );
      updateExplanationText();
    });

    btnStep2Do.addEventListener('click', () => {
      // Create candidate lines from current to all others
      const cur = algorithmState.currentPointIndex;
      algorithmState.candidateLines = [];
      for (let i = 0; i < points.length; i++) {
        if (i !== cur) algorithmState.candidateLines.push([cur, i]);
      }

      // Identify the second point from precomputed hull
      const secondIdx = algorithmState.fullHull[1];

      // Show candidate lines briefly, then commit selection
      setTimeout(() => {
        // Add hull edge and point
        algorithmState.hullPointsIndices.push(secondIdx);
        algorithmState.currentPointIndex = secondIdx;
        algorithmState.candidateLines = [];
        algorithmState.step = 2;
        enableStep3();
      }, 350);
    });

    btnStep3Explain.addEventListener('click', () => {
      algorithmState.explanationSteps.push(
        'Step 3. Repeat finding the point clockwise furthest to the right relative to the previously found point, until the leftmost point is reached again.'
      );
      updateExplanationText();
    });

    // Step 3 action handler (for "do it" and "repeat" buttons)
    function handleStep3Action(k, clickedButtonId, nextButtonId) {
      const cur = algorithmState.currentPointIndex;
      // Candidate lines from current to all points not already on the hull
      const onHull = new Set(algorithmState.hullPointsIndices);
      algorithmState.candidateLines = [];
      for (let i = 0; i < points.length; i++) {
        if (i !== cur && !onHull.has(i)) algorithmState.candidateLines.push([cur, i]);
      }

      const nextHullIndex = algorithmState.fullHull[k];

      setTimeout(() => {
        // Commit: add new hull vertex and edge
        algorithmState.hullPointsIndices.push(nextHullIndex);
        algorithmState.currentPointIndex = nextHullIndex;
        algorithmState.candidateLines = [];
        // Manage buttons
        const clicked = document.getElementById(clickedButtonId);
        if (clicked) clicked.disabled = true;
        if (nextButtonId) {
          const nextBtn = document.getElementById(nextButtonId);
          if (nextBtn) nextBtn.disabled = false;
        } else {
          // No next button: this was the last vertex. Close the hull.
          algorithmState.hullClosed = true;
          // Disable any remaining step 3 action buttons if they exist
          const rem = step3Actions.querySelectorAll('button');
          rem.forEach(b => b.disabled = true);
        }
      }, 350);
    }

    // p5.js sketch
    let sketch = (p) => {
      p.setup = function() {
        const c = p.createCanvas(700, 500);
        c.parent('canvas-container');
        // Initialize points as p5.Vector
        points = initialCoords.map(pt => p.createVector(pt.x, pt.y));
        p.frameRate(60);
      };

      p.draw = function() {
        p.background(255);
        // draw canvas border
        p.push();
        p.noFill();
        p.stroke(220);
        p.rect(0.5, 0.5, p.width - 1, p.height - 1);
        p.pop();

        // Draw candidate lines (teal)
        drawCandidateLines(p);

        // Draw hull edges (dark blue)
        drawHullEdges(p);

        // Draw points (default or hull style)
        drawPoints(p);
      };

      p.mousePressed = function() {
        if (mode !== 'move') return;
        for (let i = 0; i < points.length; i++) {
          const d = p.dist(p.mouseX, p.mouseY, points[i].x, points[i].y);
          if (d <= 10) {
            draggedPoint = i;
            break;
          }
        }
      };

      p.mouseDragged = function() {
        if (mode !== 'move') return;
        if (draggedPoint !== null) {
          points[draggedPoint].x = p.constrain(p.mouseX, 5, p.width - 5);
          points[draggedPoint].y = p.constrain(p.mouseY, 5, p.height - 5);
        }
      };

      p.mouseReleased = function() {
        draggedPoint = null;
      };
    };

    new p5(sketch);

    // Drawing helpers
    function drawPoints(p) {
      const hullSet = new Set(algorithmState.hullPointsIndices);
      for (let i = 0; i < points.length; i++) {
        const pt = points[i];
        p.push();
        p.stroke(0);
        if (hullSet.has(i)) {
          p.fill(45, 60, 110); // dark blue for hull points
        } else {
          p.fill(200, 220, 100); // light green default
        }
        p.ellipse(pt.x, pt.y, 12, 12);
        // Crosshair
        p.stroke(0);
        p.strokeWeight(1);
        p.line(pt.x - 3, pt.y, pt.x + 3, pt.y);
        p.line(pt.x, pt.y - 3, pt.x, pt.y + 3);
        p.pop();
      }
    }

    function drawCandidateLines(p) {
      if (!algorithmState.candidateLines || algorithmState.candidateLines.length === 0) return;
      p.push();
      p.stroke(60, 150, 140); // teal
      p.strokeWeight(2);
      algorithmState.candidateLines.forEach(pair => {
        const a = points[pair[0]];
        const b = points[pair[1]];
        p.line(a.x, a.y, b.x, b.y);
      });
      p.pop();
    }

    function drawHullEdges(p) {
      const hull = algorithmState.hullPointsIndices;
      if (!hull || hull.length < 2) return;
      p.push();
      p.stroke(45, 60, 110); // dark blue
      p.strokeWeight(3);
      for (let i = 0; i < hull.length - 1; i++) {
        const a = points[hull[i]];
        const b = points[hull[i + 1]];
        p.line(a.x, a.y, b.x, b.y);
      }
      // Close the hull if completed
      if (algorithmState.hullClosed && hull.length >= 2) {
        const first = points[hull[0]];
        const last = points[hull[hull.length - 1]];
        p.line(last.x, last.y, first.x, first.y);
      }
      p.pop();
    }

    // Geometry helpers and Jarvis March
    function findLeftmostIndex(pts) {
      let idx = 0;
      for (let i = 1; i < pts.length; i++) {
        if (pts[i].x < pts[idx].x || (pts[i].x === pts[idx].x && pts[i].y < pts[idx].y)) {
          idx = i;
        }
      }
      return idx;
    }

    function cross(o, a, b) {
      return (a.x - o.x) * (b.y - o.y) - (a.y - o.y) * (b.x - o.x);
    }

    function dist2(a, b) {
      const dx = a.x - b.x, dy = a.y - b.y;
      return dx * dx + dy * dy;
    }

    // Jarvis March (Gift Wrapping) returning indices in clockwise order starting at leftmost point
    function computeConvexHullIndices(pts) {
      const n = pts.length;
      if (n < 3) return Array.from({length: n}, (_, i) => i);

      const leftIdx = findLeftmostIndex(pts);
      const hull = [];
      let current = leftIdx;

      do {
        hull.push(current);
        let next = (current + 1) % n;
        for (let i = 0; i < n; i++) {
          if (i === current) continue;
          const c = cross(pts[current], pts[next], pts[i]);
          // Choose the most clockwise (rightmost turn): cross < 0
          if (next === current || c < 0 || (c === 0 && dist2(pts[current], pts[i]) > dist2(pts[current], pts[next]))) {
            next = i;
          }
        }
        current = next;
      } while (current !== leftIdx && hull.length <= n + 1); // safe guard

      return hull;
    }
  </script>
