        <input id="slider-feed-rate" type="range" min="0.0" max="2.0" step="0.01" value="0.2">
        <span class="val" id="slider-feed-rate-value">0.20</span>
      </div>
    </div>

    <div class="control">
      <label for="slider-substrate-feed-conc">substrate feed concentration [mol/m³]</label>
      <div class="slider-row">
        <input id="slider-substrate-feed-conc" type="range" min="10" max="200" step="1" value="100">
        <span class="val" id="slider-substrate-feed-conc-value">100</span>
      </div>
    </div>

    <div class="control">
      <label for="slider-feed-time">feed time (h)</label>
      <div class="slider-row">
        <input id="slider-feed-time" type="range" min="0" max="60" step="1" value="35">
        <span class="val" id="slider-feed-time-value">35</span>
      </div>
      <div class="footer-note">Note: This slider's maximum equals the run time maximum.</div>
    </div>

    <div class="control">
      <label for="select-plot">plot</label>
      <select id="select-plot">
        <option value="fed batch" selected>fed batch</option>
        <option value="α">α</option>
      </select>
    </div>
  </div>

  <div id="visualization">
    <div id="plot-top"></div>
    <div id="plot-bottom"></div>
  </div>
</div>

<script>
/* ---------- Global state ---------- */
let currentData = null; // time-series from the latest simulation

/* ---------- Utility formatting ---------- */
function fmt(val, step){
  const s = Number(step);
  if (s >= 1) return Math.round(val).toString();
  if (s >= 0.1) return Number(val).toFixed(1);
  return Number(val).toFixed(2);
}

/* ---------- Parameters / initial conditions ---------- */
const IC = { // at t=0
  X: 0.1,     // mol/m^3
  Glu: 60.0,  // mol/m^3
  Eta: 0.0,   // mol/m^3
  E0: 1.0,    // mol/m^3
  E1: 0.0,    // mol/m^3
  E2: 0.0,    // mol/m^3
  V: 1.0      // m^3
};

// Fixed kinetic constants (chosen to produce reasonable diauxic dynamics)
const CONSTS = {
  nHill: 4,
  Ks: 5.0,          // Monod const for glucose [mol/m^3]
  Keta: 3.0,        // Monod const for ethanol [mol/m^3]
  mu_f: 0.45,       // max fermentative growth rate [1/h]
  mu_r: 0.28,       // max respiratory growth rate [1/h]
  YXG: 0.5,         // biomass yield on glucose [mol X / mol Glu]
  YXE: 0.4,         // biomass yield on ethanol [mol X / mol Eta]
  Y_EG: 0.8,        // ethanol formation yield from glucose [mol Eta / mol Glu]
  kdeg: 0.20,       // enzyme degradation/dilution base [1/h]
  ksyn0: 0.20,      // E0 synthesis [mol/(m^3 h)], gives steady ~1 with kdeg=0.2
  ksyn1: 1.20,      // E1 synthesis strength (induced by glucose)
  ksyn2: 1.00       // E2 synthesis strength (repressed by glucose)
};

/* ---------- UI helpers ---------- */
function readUI(){
  const p = {
    alpha_max: parseFloat(document.getElementById('slider-alpha-max').value),
    t_min: parseFloat(document.getElementById('slider-t-min').value), // 0 (disabled)
    t_max: parseFloat(document.getElementById('slider-t-max').value),
    KI: parseFloat(document.getElementById('slider-enzyme-control').value),
    F_in: parseFloat(document.getElementById('slider-feed-rate').value), // m3/h
    G_in: parseFloat(document.getElementById('slider-substrate-feed-conc').value), // mol/m3
    t_feed: parseFloat(document.getElementById('slider-feed-time').value) // h
  };
  return p;
}

function updateSliderUI(id){
  const el = document.getElementById(id);
  const span = document.getElementById(id + '-value');
  if (span) span.textContent = fmt(el.value, el.step);
}

/* ---------- ODE system and RK4 solver ---------- */
function derivs(t, y, p){
  const { nHill, Ks, Keta, mu_f, mu_r, YXG, YXE, Y_EG, kdeg, ksyn0, ksyn1, ksyn2 } = CONSTS;
  let [X, G, Et, E0, E1, E2, V] = y;

  G = Math.max(G, 0);  // avoid negative in Hill functions
  Et = Math.max(Et, 0);

  // Fraction of respiring biomass α (glucose-repressed)
  const KI = p.KI;
  const powG = Math.pow(G, nHill);
  const powKI = Math.pow(KI, nHill);
  const alpha = p.alpha_max * (powKI / (powKI + powG + 1e-12));

  // Feed / dilution (only for t <= t_feed)
  const feedOn = (t <= p.t_feed) ? 1 : 0;
  const F = feedOn ? p.F_in : 0.0;      // m^3/h
  const D = V > 0 ? F / V : 0.0;        // dilution rate [1/h]
  const G_in = feedOn ? p.G_in : 0.0;   // mol/m^3

  // Enzyme regulation by glucose
  const hill_on = powG / (powKI + powG + 1e-12);  // high when G is high
  const hill_off = powKI / (powKI + powG + 1e-12);// high when G is low

  // Enzyme dynamics (with dilution)
  const dE0 = ksyn0 - kdeg*E0 - D*E0;
  const dE1 = ksyn1*hill_on - kdeg*E1 - D*E1;   // induced by glucose
  const dE2 = ksyn2*hill_off - kdeg*E2 - D*E2;  // repressed by glucose

  // Enzyme activity saturation factors (avoid zero activity entirely)
  const act1 = 0.2 + 0.8*(E1/(1.0 + E1));
  const act2 = 0.2 + 0.8*(E2/(1.0 + E2));

  // Specific growth rates
  const muf = mu_f * (G/(Ks + G + 1e-12)) * (1 - alpha) * act1;
  const mur = mu_r * (Et/(Keta + Et + 1e-12)) * alpha * act2;
  const mu = muf + mur;

  // Biomass
  const dX = mu*X - D*X;

  // Substrate and product balances via yields
  const rG = (muf / YXG) * X;      // glucose consumption rate (mol/m^3/h)
  const rEtaCons = (mur / YXE) * X;// ethanol consumption rate (mol/m^3/h)

  const dG = -rG - D*G + D*G_in;
  const dEt = (Y_EG * rG) - rEtaCons - D*Et;

  // Volume
  const dV = F;

  return { dydt: [dX, dG, dEt, dE0, dE1, dE2, dV], alpha };
}

function rk4Step(t, y, dt, p){
  const k1 = derivs(t, y, p).dydt;

  const y2 = y.map((yi,i)=> yi + 0.5*dt*k1[i]);
  const k2 = derivs(t + 0.5*dt, y2, p).dydt;

  const y3 = y.map((yi,i)=> yi + 0.5*dt*k2[i]);
  const k3 = derivs(t + 0.5*dt, y3, p).dydt;

  const y4 = y.map((yi,i)=> yi + dt*k3[i]);
  const k4 = derivs(t + dt, y4, p).dydt;

  const yNext = y.map((yi,i)=> yi + (dt/6)*(k1[i] + 2*k2[i] + 2*k3[i] + k4[i]));
  return yNext;
}

function simulate(p){
  const t0 = 0.0, t1 = p.t_max;
  // adaptive-ish fixed small dt for stability
  const dt = Math.min(0.05, Math.max(0.01, (t1-t0)/2400)); // ~2000-5000 steps
  let y = [IC.X, IC.Glu, IC.Eta, IC.E0, IC.E1, IC.E2, IC.V];

  const T=[], X=[], G=[], Et=[], E0=[], E1=[], E2=[], V=[], A=[];
  for(let t=t0; t<=t1 + 1e-9; t += dt){
    // Record current state
    const alpha_now = derivs(t, y, p).alpha;
    T.push(t);
    X.push(Math.max(y[0],0));
    G.push(Math.max(y[1],0));
    Et.push(Math.max(y[2],0));
    E0.push(Math.max(y[3],0));
    E1.push(Math.max(y[4],0));
    E2.push(Math.max(y[5],0));
    V.push(Math.max(y[6],1e-9));
    A.push(Math.max(0, Math.min(1.2, alpha_now)));

    // Step
    y = rk4Step(t, y, dt, p);
  }
  return {T, X, G, Et, E0, E1, E2, V, A};
}

/* ---------- Plotting ---------- */
function drawPlots(){
  if (!currentData) return;
  const mode = document.getElementById('select-plot').value;
  const t = currentData.T;

  const commonLayout = {
    margin:{l:60,r:10,t:32,b:50},
    xaxis:{title:'time (h)', gridcolor:'#ddd', zeroline:false, mirror:true, linecolor:'#444'},
    yaxis:{gridcolor:'#ddd', zeroline:false, mirror:true, linecolor:'#444'},
    legend:{orientation:'h', x:0, y:1.12},
    paper_bgcolor:'#fff', plot_bgcolor:'#fff',
    font:{size:12}
  };
  const config = {responsive:true, displayModeBar:false};

  if (mode === 'fed batch'){
    // Top: X, Glu, Eta
    const tracesTop = [
      {x:t, y:currentData.X, name:'X(t)', mode:'lines', line:{color:'#1f77b4', width:2}},
      {x:t, y:currentData.G, name:'Glu(t)', mode:'lines', line:{color:'#d62728', width:2}},
      {x:t, y:currentData.Et, name:'Eta(t)', mode:'lines', line:{color:'#2ca02c', width:2}}
    ];
    const layoutTop = Object.assign({}, commonLayout, {
      title:'diauxic behavior',
      yaxis:Object.assign({}, commonLayout.yaxis, {title:'concentration [mol/m³]'})
    });

    // Bottom: E2, E1, E0
    const tracesBottom = [
      {x:t, y:currentData.E2, name:'E₂(t)', mode:'lines', line:{color:'#000', width:2}},
      {x:t, y:currentData.E1, name:'E₁(t)', mode:'lines', line:{color:'#e377c2', width:2}},
      {x:t, y:currentData.E0, name:'E₀(t)', mode:'lines', line:{color:'#ff7f0e', width:2}}
    ];
    const layoutBottom = Object.assign({}, commonLayout, {
      title:'enzyme concentration',
      yaxis:Object.assign({}, commonLayout.yaxis, {title:'enzyme concentration [mol/m³]'})
    });

    Plotly.react('plot-top', tracesTop, layoutTop, config);
    Plotly.react('plot-bottom', tracesBottom, layoutBottom, config);
  } else {
    // "α" plot set
    const tracesTop = [
      {x:t, y:currentData.G, name:'Glu(t)', mode:'lines', line:{color:'#d62728', width:2}}
    ];
    const layoutTop = Object.assign({}, commonLayout, {
      title:'glucose concentration',
      yaxis:Object.assign({}, commonLayout.yaxis, {title:'glucose concentration [mol/m³]'})
    });

    const tracesBottom = [
      {x:t, y:currentData.A, name:'α', mode:'lines', line:{color:'#1f77b4', width:2}}
    ];
    const layoutBottom = Object.assign({}, commonLayout, {
      title:'fraction of respiring biomass',
      yaxis:Object.assign({}, commonLayout.yaxis, {title:'α', range:[0,1.05]})
    });

    Plotly.react('plot-top', tracesTop, layoutTop, config);
    Plotly.react('plot-bottom', tracesBottom, layoutBottom, config);
  }
}

/* ---------- Recompute simulation when sliders change ---------- */
function recompute(){
  // keep feed-time slider synchronized with t-max
  const tmaxEl = document.getElementById('slider-t-max');
  const feedEl = document.getElementById('slider-feed-time');
  const newMax = parseFloat(tmaxEl.value);
  if (parseFloat(feedEl.max) !== newMax){
    feedEl.max = newMax;
  }
  if (parseFloat(feedEl.value) > newMax){
    feedEl.value = String(newMax);
    updateSliderUI('slider-feed-time');
  }

  const params = Object.assign({}, CONSTS, readUI());
  currentData = simulate(params);
  drawPlots();
}

/* ---------- Initialization ---------- */
window.addEventListener('DOMContentLoaded', () => {
  // Wire up sliders
  const sliderIds = [
    'slider-alpha-max',
    'slider-t-min',
    'slider-t-max',
    'slider-enzyme-control',
    'slider-feed-rate',
    'slider-substrate-feed-conc',
    'slider-feed-time'
  ];
  sliderIds.forEach(id=>{
    updateSliderUI(id);
    const el = document.getElementById(id);
    el.addEventListener('input', ()=>{
      updateSliderUI(id);
      recompute();
    });
  });

  // Keep dropdown changing plots only (no recompute)
  document.getElementById('select-plot').addEventListener('change', drawPlots);

  // Ensure feed-time max initially matches t-max
  document.getElementById('slider-feed-time').max = document.getElementById('slider-t-max').value;

  // Initial simulation & plots
  recompute();
});
</script>
</body>
</html>
