    // Earth's axis line
    const axisGeom = new THREE.BufferGeometry().setFromPoints([new THREE.Vector3(0, -EARTH_RADIUS*1.2, 0), new THREE.Vector3(0, EARTH_RADIUS*1.2, 0)]);
    const axisLine = new THREE.Line(axisGeom, new THREE.LineDashedMaterial({color: 0xffffff, dashSize: 0.5, gapSize: 0.25}));
    axisLine.computeLineDistances();
    earthSpinGroup.add(axisLine);

    // Terminator plane (day/night division)
    const planeGeom = new THREE.PlaneGeometry(EARTH_RADIUS*3.5, EARTH_RADIUS*3.5);
    const planeMat = new THREE.MeshBasicMaterial({color: 0x8b5e3c, transparent: true, opacity: 0.25, side: THREE.DoubleSide});
    const terminatorPlane = new THREE.Mesh(planeGeom, planeMat);
    spaceGroup.add(terminatorPlane);

    // Latitude parallel (complete circle)
    const parallelMat = new THREE.LineBasicMaterial({color: 0x00ff66});
    const parallelGeom = new THREE.BufferGeometry();
    const latitudeParallel = new THREE.LineLoop(parallelGeom, parallelMat);
    earthSpinGroup.add(latitudeParallel);

    // Daylit portion of the latitude parallel
    const daylitMat = new THREE.LineBasicMaterial({color: 0xffff00, linewidth: 3});
    const daylitGeom = new THREE.BufferGeometry();
    const daylitLine = new THREE.Line(daylitGeom, daylitMat);
    earthSpinGroup.add(daylitLine);

    // Constellations in space view (bigger sphere, white faint lines)
    const constellationsSpaceGroup = new THREE.Group();
    spaceGroup.add(constellationsSpaceGroup);

    // --------- Constellations (simple demo set, re-used in both views) ----------
    function addConstellationLines(targetGroup, radius, color, opacity){
      const mat = new THREE.LineBasicMaterial({color, transparent: true, opacity});
      // Define a few constellations in Right Ascension/Declination (degrees), very rough outlines
      const constellations = [
        // Orion belt + shoulders + feet
        [[83,-1],[85,-1],[88,-1],[83,-1],[78,7],[83,-1],[87,7],[83,-1],[83,-8],[86,-9]],
        // Big Dipper (Ursa Major)
        [[165,56],[168,54],[177,54],[183,57],[192,58],[194,62],[206,62]],
        // Cassiopeia
        [[10,60],[28,57],[46,60],[63,56],[80,60]],
        // Scorpius tail
        [[240,-20],[245,-30],[252,-34],[258,-37],[266,-37]]
      ];
      const lines = new THREE.Group();
      const toCartesian = (raDeg, decDeg) => {
        const ra = raDeg * DEG;
        const dec = decDeg * DEG;
        const x = radius * Math.cos(dec) * Math.cos(ra);
        const y = radius * Math.sin(dec);
        const z = radius * Math.cos(dec) * Math.sin(ra);
        return new THREE.Vector3(x,y,z);
      };
      for(const path of constellations){
        const pts = path.map(p => toCartesian(p[0], p[1]));
        const g = new THREE.BufferGeometry().setFromPoints(pts);
        const line = new THREE.Line(g, mat);
        lines.add(line);
      }
      targetGroup.add(lines);
    }
    addConstellationLines(constellationsSpaceGroup, 30, 0xffffff, 0.5);
    addConstellationLines(constellationsDayGroup,   14.5, 0xffffff, 0.35);

    // ---------- Camera presets ----------
    function setCameraForGround(){
      camera.position.set(0, 7, 18);
      controls.target.set(0, 0, 0);
      controls.update();
    }
    function setCameraForSpace(){
      camera.position.set(0, 0, 30);
      controls.target.set(0, 0, 0);
      controls.update();
    }

    // ---------- Simulation State ----------
    let dirty = true;
    let lambdaEcl = 0;           // Sun ecliptic longitude (radians)
    let sunDirWorld = new THREE.Vector3(1,0,0); // updated from date
    const tmpVec = new THREE.Vector3();
    const tmpQuat = new THREE.Quaternion();

    // ---------- Formatting helpers ----------
    const monthNames = ["January","February","March","April","May","June","July","August","September","October","November","December"];
    const monthDays = [31,28,31,30,31,30,31,31,30,31,30,31]; // ignore leap year for simplicity

    function doyToMonthDay(doy){
      let d = Math.max(1, Math.min(365, Math.round(doy)));
      let m = 0;
      while(d > monthDays[m]){
        d -= monthDays[m]; m++;
      }
      return {month: monthNames[m], day: d};
    }

    function formatH(h){
      if(isNaN(h)) return "N/A";
      let hr = Math.floor(h);
      let min = Math.round((h-hr)*60);
      if(min===60){ hr+=1; min=0; }
      const hh = String(hr).padStart(2,'0');
      const mm = String(min).padStart(2,'0');
      return `${hh}:${mm}`;
    }
    function formatDuration(h){
      if(!isFinite(h)) return "N/A";
      const hr = Math.floor(h);
      const min = Math.round((h - hr)*60);
      return `${hr} h ${min} min`;
    }
    function clamp(x, a, b){ return Math.max(a, Math.min(b, x)); }

    // ---------- Astronomy core ----------
    function updateSunFromDate(){
      // Ecliptic longitude lambda: 0 at March 20 (doy ~79)
      const doy = Number(sliderDate.value);
      lambdaEcl = 2*Math.PI * ( (doy - 79) / 365.2422 );
      // Sun direction in ecliptic frame (Z is ecliptic north)
      const sEcl = new THREE.Vector3(Math.cos(lambdaEcl), Math.sin(lambdaEcl), 0);
      // Earth axis is tilted by OBLIQUITY about X in our scene (earthTiltGroup)
      // To convert Sun dir from ecliptic frame to world (equatorial) frame, we rotate it by -OBLIQUITY about X.
      const rot = new THREE.Quaternion().setFromAxisAngle(new THREE.Vector3(1,0,0), -OBLIQUITY);
      sunDirWorld.copy(sEcl).applyQuaternion(rot).normalize();

      // Place sun in space view and orient light
      sunSpace.position.copy(sunDirWorld).multiplyScalar(SUN_DIST);
      sunLight.position.copy(sunDirWorld).multiplyScalar(-1); // shine toward Earth
      // Terminator plane: align plane normal to sunDirWorld
      const q = new THREE.Quaternion().setFromUnitVectors(new THREE.Vector3(0,0,1), sunDirWorld);
      terminatorPlane.quaternion.copy(q);

      dirty = true;
    }

    // Calculate solar declination from geometry (angle between sunDir and Earth equatorial plane)
    function currentDeclination(){
      // Earth spin group Y-axis is Earth's rotation axis in world coordinates after tilt:
      earthSpinGroup.getWorldQuaternion(tmpQuat);
      // The world direction of the local +Y axis:
      const earthAxisWorld = new THREE.Vector3(0,1,0).applyQuaternion(tmpQuat).normalize();
      const sinDec = sunDirWorld.dot(earthAxisWorld);
      return Math.asin(clamp(sinDec, -1, 1)); // in radians
    }

    // Solar altitude/azimuth for given latitude and hour angle using standard spherical astronomy
    function altAz(phi, delta, H){
      const sinphi = Math.sin(phi), cosphi = Math.cos(phi);
      const sind = Math.sin(delta), cosd = Math.cos(delta);
      const cosH = Math.cos(H), sinH = Math.sin(H);
      const sinAlt = sinphi*sind + cosphi*cosd*cosH;
      const alt = Math.asin(clamp(sinAlt, -1, 1));
      const y = -cosd*sinH;
      const x = cosphi*sind - sinphi*cosd*cosH;
      let A = Math.atan2(y, x); // from North, clockwise
      if(A < 0) A += 2*Math.PI;
      return {alt, az: A};
    }

    // Readouts and dependent geometry
    function updateReadoutsAndGeometry(){
      // Update date label
      const md = doyToMonthDay(Number(sliderDate.value));
      labelDate.textContent = `${md.month} ${md.day}`;

      // Update latitude label
      const lat = Number(sliderLat.value);
      labelLat.textContent = `${lat >= 0 ? lat : -lat}°${lat>=0?'':' S'.trim()}`.replace('° S','°S');

      // Declination from sun direction
      const delta = currentDeclination(); // radians
      const phi = lat * DEG;

      // Day length via hour angle at sunrise/sunset: cosH0 = -tan(phi)*tan(delta)
      const x = -Math.tan(phi) * Math.tan(delta);
      let dayLenH, sunriseH, sunsetH, H0;
      let sunriseAz = NaN, sunsetAz = NaN;
      if(x <= -1){ // Sun above horizon all day
        dayLenH = 24;
        sunriseH = NaN; sunsetH = NaN; H0 = Math.PI;
      }else if(x >= 1){ // Sun below horizon all day
        dayLenH = 0;
        sunriseH = NaN; sunsetH = NaN; H0 = 0;
      }else{
        H0 = Math.acos(x);
        dayLenH = 2*H0*RAD2DEG/15;
        sunriseH = 12 - dayLenH/2;
        sunsetH  = 12 + dayLenH/2;
        // Directions at sunrise/sunset using altAz with H = +/- H0
        const azSunrise = altAz(phi, delta, -H0).az * RAD2DEG;
        const azSunset  = altAz(phi, delta,  H0).az * RAD2DEG;
        sunriseAz = azSunrise; sunsetAz = azSunset;
      }
      const nightLenH = 24 - dayLenH;

      readDayLen.textContent = formatDuration(dayLenH);
      readNightLen.textContent = formatDuration(nightLenH);
      readSunriseT.textContent = isFinite(sunriseH) ? formatH(sunriseH) : "N/A";
      readSunsetT.textContent  = isFinite(sunsetH)  ? formatH(sunsetH)  : "N/A";
      readSunriseD.textContent = isFinite(sunriseAz) ? `${Math.round(sunriseAz)}°` : "N/A";
      readSunsetD.textContent  = isFinite(sunsetAz)  ? `${Math.round(sunsetAz)}°`  : "N/A";

      // Update Sun path for ground view
      updateSunPathGround(phi, delta, H0, dayLenH);

      // Update celestial dome opacity depending on day/night at selected time
      const time = Number(sliderTime.value);
      const H = (time - 12) * 15 * DEG;
      const altCurrent = altAz(phi, delta, H).alt;
      domeMat.opacity = altCurrent > 0 ? 0.35 : 0.55;

      // Update Earth's latitude circle and daylit arc in space view
      updateLatitudeParallel(lat);
      dirty = true;
    }

    function updateSunPathGround(phi, delta, H0, dayLenH){
      const points = [];
      const r = 12.0;
      // If polar day -> draw full 24h path; if polar night -> still draw full path (below horizon)
      let Hmin, Hmax, steps;
      if(dayLenH === 0 || dayLenH === 24){
        Hmin = -Math.PI; Hmax = Math.PI; steps = 240;
      }else{
        Hmin = -H0; Hmax = H0; steps = 120;
      }
      for(let i=0;i<=steps;i++){
        const H = Hmin + (Hmax - Hmin)*i/steps;
        const {alt, az} = altAz(phi, delta, H);
        const x = r * Math.sin(az) * Math.cos(alt);
        const z = r * Math.cos(az) * Math.cos(alt);
        const y = r * Math.sin(alt);
        points.push(new THREE.Vector3(x,y,z));
      }
      sunPathGeom.setFromPoints(points);
      sunPathLine.visible = cbSunPath.checked;

      // Place the "ground" Sun according to time slider
      updateSunPositionGround();
    }

    function updateSunPositionGround(){
      const lat = Number(sliderLat.value)*DEG;
      const delta = currentDeclination();
      const time = Number(sliderTime.value);
      labelTime.textContent = formatH(time);
      const H = (time - 12) * 15 * DEG;
      const r = 12.0;
      const {alt, az} = altAz(lat, delta, H);
      const x = r * Math.sin(az) * Math.cos(alt);
      const z = r * Math.cos(az) * Math.cos(alt);
      const y = r * Math.sin(alt);
      sunGround.position.set(x,y,z);
      dirty = true;
    }

    // Latitude parallel and daylit part on Earth (space view)
    function updateLatitudeParallel(latDeg){
      const latRad = latDeg * DEG;
      // Full parallel line points (in Earth local coordinates, before tilt / spin)
      const points = [];
      const r = EARTH_RADIUS;
      const cosphi = Math.cos(latRad);
      const sinphi = Math.sin(latRad);
      const steps = 256;
      for(let i=0;i<steps;i++){
        const theta = i/steps * Math.PI*2;
        const x = r * cosphi * Math.cos(theta);
        const y = r * sinphi;
        const z = r * cosphi * Math.sin(theta);
        points.push(new THREE.Vector3(x,y,z));
      }
      parallelGeom.setFromPoints(points);

      // Daylit part: Evaluate dot(n, sunDir) in Earth-local frame
      // Compute sunDir in Earth local coordinates (spin group)
      const sunLocal = sunDirWorld.clone();
      earthSpinGroup.getWorldQuaternion(tmpQuat);
      const inv = tmpQuat.clone().invert();
      sunLocal.applyQuaternion(inv).normalize();

      const dayPts = [];
      let collecting = false;
      function isDay(x,y,z){
        const n = new THREE.Vector3(x,y,z).normalize();
        return n.dot(sunLocal) > 0;
      }
      for(let i=0;i<=steps;i++){
        const theta = i/steps * Math.PI*2;
        const x = r * cosphi * Math.cos(theta);
        const y = r * sinphi;
        const z = r * cosphi * Math.sin(theta);
        const day = isDay(x,y,z);
        if(day){
          dayPts.push(new THREE.Vector3(x,y,z));
          collecting = true;
        }else if(collecting){
          // close the current segment by duplicating last point; break to keep a single arc
          break;
        }
      }
      daylitGeom.setFromPoints(dayPts);
      latitudeParallel.visible = true;
      daylitLine.visible = cbDayOnPar.checked;
      dirty = true;
    }

    // ---------- View switching ----------
    function applyView(){
      const view = selectView.value;
      const isGround = (view === 'ground');
      groundGroup.visible = isGround;
      spaceGroup.visible = !isGround;
      optGround.style.display = isGround ? 'block' : 'none';
      optSpace.style.display  = isGround ? 'none'  : 'block';

      if(isGround){
        setCameraForGround();
      }else{
        setCameraForSpace();
      }

      // Apply checkbox visibilities to scene elements
      sunPathLine.visible = cbSunPath.checked;
      constellationsDayGroup.visible = isGround ? cbConsDay.checked : false;

      constellationsSpaceGroup.visible = !isGround && cbConsSpace.checked;
      terminatorPlane.visible = !isGround && cbTerm.checked;
      daylitLine.visible = !isGround && cbDayOnPar.checked;

      dirty = true;
    }

    // ---------- Checkbox handlers ----------
    cbSunPath.addEventListener('change', ()=>{ sunPathLine.visible = cbSunPath.checked; dirty = true; });
    cbConsDay.addEventListener('change', ()=>{ if(selectView.value==='ground'){ constellationsDayGroup.visible = cbConsDay.checked; } dirty = true; });
    cbConsSpace.addEventListener('change', ()=>{ if(selectView.value==='space'){ constellationsSpaceGroup.visible = cbConsSpace.checked; } dirty = true; });
    cbTerm.addEventListener('change', ()=>{ if(selectView.value==='space'){ terminatorPlane.visible = cbTerm.checked; } dirty = true; });
    cbDayOnPar.addEventListener('change', ()=>{ if(selectView.value==='space'){ daylitLine.visible = cbDayOnPar.checked; } dirty = true; });

    // ---------- Controls events ----------
    sliderDate.addEventListener('input', ()=>{
      updateSunFromDate();
      updateReadoutsAndGeometry();
    });

    sliderLat.addEventListener('input', ()=>{
      labelLat.textContent = `${sliderLat.value >= 0 ? sliderLat.value : -sliderLat.value}°${sliderLat.value>=0?'':'S'}`;
      updateReadoutsAndGeometry();
    });

    sliderTime.addEventListener('input', ()=>{
      updateSunPositionGround();
      // Earth rotation unless stationary
      if(!cbStationary.checked){
        const ang = (Number(sliderTime.value) - 12) * 15 * DEG;
        earthSpinGroup.rotation.y = ang;
        updateLatitudeParallel(Number(sliderLat.value)); // refresh daylit arc as spin changes
      }
      dirty = true;
    });

    cbStationary.addEventListener('change', ()=>{
      if(cbStationary.checked){
        // freeze at current orientation; no extra action
      }else{
        // when unfreezing, set rotation to reflect current time
        const ang = (Number(sliderTime.value) - 12) * 15 * DEG;
        earthSpinGroup.rotation.y = ang;
      }
      updateLatitudeParallel(Number(sliderLat.value));
      dirty = true;
    });

    selectView.addEventListener('change', applyView);

    // ---------- Initial setup ----------
    function init(){
      // Initial camera
      setCameraForGround();

      // Space view initial rotation (according to time)
      earthSpinGroup.rotation.y = (Number(sliderTime.value) - 12) * 15 * DEG;

      // Initial sun from date
      updateSunFromDate();

      // Initial readouts and geometry
      updateReadoutsAndGeometry();

      // Initial checkbox-based visibility
      applyView();

      // Fit renderer on resize
      window.addEventListener('resize', onResize);
      onResize();

      animate();
    }

    function onResize(){
      const w = container.clientWidth;
      const h = container.clientHeight;
      renderer.setSize(w,h);
      camera.aspect = w/h;
      camera.updateProjectionMatrix();
      dirty = true;
    }

    // ---------- Animation loop ----------
    function animate(){
      requestAnimationFrame(animate);
      controls.update();
      if(dirty){
        renderer.render(scene, camera);
        dirty = false;
      }
    }

    // ---------- Kick off ----------
    init();

  })();
  </script>
</body>
</html>
