<body>
  <div id="main-container">
    <h1>Unsteady-State Diffusion in a Slab</h1>
    <p>
      Explore how solute concentration evolves inside a one-dimensional slab after its
      surface concentration is suddenly fixed. Use the sliders to set the elapsed time
      and the diffusivity. The plot shows concentration versus position across a slab of
      thickness 0.004 m. Initial concentration varies linearly from 0.001 to 0.002,
      the surface concentration is 0.004, and the interior profile is computed from a
      Fourier series solution to the diffusion equation.
    </p>

    <div id="control-panel">
      <div class="control-row">
        <label for="time-slider">time</label>
        <input type="range" id="time-slider" min="0" max="2500" step="1" value="100">
        <span id="time-value" class="value-box">100.</span>
      </div>
      <div class="control-row">
        <label for="diffusivity-slider">diffusivity in m<sup>2</sup>/s</label>
        <input type="range" id="diffusivity-slider" min="1e-10" max="1e-8" step="1e-10" value="4e-9">
        <span id="diffusivity-value" class="value-box">4.0 × 10⁻⁹</span>
      </div>
    </div>

    <div id="plot-div"></div>
  </div>

  <script>
    (function () {
      // DOM elements
      const timeSlider = document.getElementById('time-slider');
      const timeValue = document.getElementById('time-value');
      const dSlider = document.getElementById('diffusivity-slider');
      const dValue = document.getElementById('diffusivity-value');
      const plotDiv = document.getElementById('plot-div');

      // Constants
      const L = 0.004;         // m (slab thickness)
      const c_s = 4.0e-3;      // surface concentration
      const N = 100;           // number of series terms
      const NX = 200;          // number of spatial points

      // Precompute wave numbers and coefficients independent of t and D
      const k = new Array(N);
      const k2 = new Array(N);
      const B = new Array(N);
      const pi = Math.PI;
      for (let n = 0; n < N; n++) {
        const kn = ((n + 0.5) * pi) / L;
        k[n] = kn;
        k2[n] = kn * kn;
        // B_n = (2/L) * 1e-3 * [ (250*(-1)^n)/k_n^2 - 3/k_n ]
        const term1 = 250 * ((n % 2 === 0) ? 1 : -1) / (kn * kn);
        const term2 = 3 / kn;
        B[n] = (2 / L) * 1e-3 * (term1 - term2);
      }

      // Generate x-grid once
      const xs = [];
      for (let i = 0; i < NX; i++) {
        xs.push((L * i) / (NX - 1));
      }

      // Utilities
      function superscriptExponent(e) {
        const map = { '-': '⁻', '0': '⁰', '1': '¹', '2': '²', '3': '³', '4': '⁴', '5': '⁵', '6': '⁶', '7': '⁷', '8': '⁸', '9': '⁹' };
        return String(e).split('').map(ch => map[ch] || ch).join('');
      }

      function formatSci(val) {
        if (val === 0) return '0';
        const sign = val < 0 ? -1 : 1;
        val = Math.abs(val);
        let exp = Math.floor(Math.log10(val));
        let mant = sign * (val / Math.pow(10, exp));
        // Round mantissa to one decimal and fix potential 10.0 case
        mant = Number(mant.toFixed(1));
        if (Math.abs(mant) >= 10) {
          mant = mant / 10;
          exp += 1;
        }
        return `${mant.toFixed(1)} × 10${superscriptExponent(exp)}`;
      }

      // Initialize displayed values from defaults
      timeSlider.value = 100;
      timeValue.textContent = '100.'; // per spec initial value
      dSlider.value = 4e-9;
      dValue.textContent = formatSci(Number(dSlider.value));

      // Plot setup
      let revision = 0;
      const layout = {
        margin: { l: 80, r: 20, t: 20, b: 50 },
        xaxis: {
          title: 'position',
          range: [0, L],
          tickformat: '.3f',
          dtick: 0.001,
          showgrid: true,
          minor: { showgrid: true }
        },
        yaxis: {
          title: 'concentration',
          range: [0.001, 0.0042],
          tickformat: '.4f',
          dtick: 0.0005,
          showgrid: true,
          minor: { showgrid: true }
        },
        showlegend: false,
        hovermode: 'closest',
        datarevision: revision
      };

      function computeProfile(t, D) {
        const c = new Array(xs.length);

        if (t <= 0) {
          for (let i = 0; i < xs.length; i++) {
            const x = xs[i];
            c[i] = (1.0 + 250 * x) * 1e-3; // initial linear profile
          }
          return c;
        }

        // Series solution for t > 0
        for (let i = 0; i < xs.length; i++) {
          const x = xs[i];
          let u = 0;
          for (let n = 0; n < N; n++) {
            u += B[n] * Math.sin(k[n] * x) * Math.exp(-k2[n] * D * t);
          }
          c[i] = u + c_s;
        }
        return c;
      }

      function updatePlot() {
        const t = Number(timeSlider.value);
        const D = Number(dSlider.value);

        // Update numeric readouts
        timeValue.textContent = t.toFixed(1);
        dValue.textContent = formatSci(D);

        const ys = computeProfile(t, D);

        const trace = {
          x: xs,
          y: ys,
          type: 'scatter',
          mode: 'lines',
          line: { color: '#1f77b4', width: 2 },
          hovertemplate: 'position=%{x:.4f}<br>concentration=%{y:.4f}<extra></extra>'
        };

        layout.datarevision = ++revision;
        Plotly.react(plotDiv, [trace], layout, { displayModeBar: false, responsive: true });
      }

      // Initial render
      updatePlot();

      // Event listeners
      timeSlider.addEventListener('input', updatePlot);
      dSlider.addEventListener('input', updatePlot);

      // Resize handling
      window.addEventListener('resize', () => {
        try { Plotly.Plots.resize(plotDiv); } catch (e) { /* ignore */ }
      });
    })();
  </script>
</body>
</html>
