        <button class="type-btn" data-type="underestimate">underestimate</button>
        <button class="type-btn" data-type="trapezoidal">trapezoidal</button>
      </div>

      <div id="slider-a-controls">
        <label for="slider-a">upper limit a</label>
        <input type="range" id="slider-a" min="0.1" max="8" step="0.01" value="5">
        <span id="value-a">5</span>
      </div>

      <div id="slider-n-controls">
        <label for="slider-n">number of quadrilaterals</label>
        <input type="range" id="slider-n" min="1" max="50" step="1" value="3">
        <span id="value-n">3</span>
      </div>
    </div>

    <div id="canvas-container"><!-- p5.js canvas will be appended here --></div>
  </div>

<script>
  // Central state
  let state = { funcKey: 'x', type: 'right', a: 5, n: 3 };

  // Function and antiderivative definitions
  const funcDefs = {
    x: {
      label: 'x',
      f: x => x,
      F: x => 0.5 * x * x,
      domain: { min: -Infinity } // for plotting
    },
    sqshift: {
      label: '(x-3)²',
      f: x => (x - 3) * (x - 3),
      F: x => Math.pow(x - 3, 3) / 3,
      domain: { min: -Infinity }
    },
    cubeshift: {
      label: '(x-3)³+27',
      f: x => Math.pow(x - 3, 3) + 27,
      F: x => Math.pow(x - 3, 4) / 4 + 27 * x,
      domain: { min: -Infinity }
    },
    sqrt: {
      label: '√x',
      f: x => (x >= 0 ? Math.sqrt(x) : NaN),
      F: x => (x >= 0 ? (2 / 3) * Math.pow(x, 1.5) : NaN),
      domain: { min: 0 }
    },
    sinplus1: {
      label: 'sin(x)+1',
      f: x => Math.sin(x) + 1,
      F: x => -Math.cos(x) + x,
      domain: { min: -Infinity }
    },
    exp3: {
      label: 'e^(x/3)',
      f: x => Math.exp(x / 3),
      F: x => 3 * Math.exp(x / 3),
      domain: { min: -Infinity }
    }
  };

  // UI wiring
  document.querySelectorAll('.function-btn').forEach(btn => {
    btn.addEventListener('click', () => {
      state.funcKey = btn.getAttribute('data-function');
      document.querySelectorAll('.function-btn').forEach(b => b.classList.remove('active'));
      btn.classList.add('active');
      if (p5Instance) p5Instance.redraw();
    });
  });

  document.querySelectorAll('.type-btn').forEach(btn => {
    btn.addEventListener('click', () => {
      state.type = btn.getAttribute('data-type');
      document.querySelectorAll('.type-btn').forEach(b => b.classList.remove('active'));
      btn.classList.add('active');
      if (p5Instance) p5Instance.redraw();
    });
  });

  const sliderA = document.getElementById('slider-a');
  const valueA  = document.getElementById('value-a');
  sliderA.addEventListener('input', () => {
    state.a = parseFloat(sliderA.value);
    valueA.textContent = state.a.toFixed(2);
    if (p5Instance) p5Instance.redraw();
  });

  const sliderN = document.getElementById('slider-n');
  const valueN  = document.getElementById('value-n');
  sliderN.addEventListener('input', () => {
    state.n = parseInt(sliderN.value, 10);
    valueN.textContent = String(state.n);
    if (p5Instance) p5Instance.redraw();
  });

  // p5 sketch
  let p5Instance;
  (function() {
    const s = (p) => {
      // Layout for plotting area inside canvas
      const margins = { left: 48, right: 16, top: 22, bottom: 42 };
      // Logical x-range (fixed)
      const xView = { min: -1, max: 8.5 };

      let yView = { min: -1, max: 10 }; // dynamic, updated per draw
      let canvasW = 900, canvasH = 560;

      function getFunc() {
        return funcDefs[state.funcKey] || funcDefs.x;
      }

      function computeYRange() {
        const def = getFunc();
        const f = def.f;

        const xmin = xView.min, xmax = xView.max;
        const steps = 700;
        let minY = Infinity, maxY = -Infinity;

        for (let i = 0; i <= steps; i++) {
          const x = xmin + (i / steps) * (xmax - xmin);
          let y = f(x);
          if (!Number.isFinite(y)) continue;
          if (y < minY) minY = y;
          if (y > maxY) maxY = y;
        }

        // Always include 0 to see the x-axis reference
        if (!Number.isFinite(minY)) { minY = 0; maxY = 1; }
        minY = Math.min(minY, 0);
        maxY = Math.max(maxY, 1e-3);

        // Padding
        const range = Math.max(1e-6, maxY - minY);
        const pad = 0.10 * range;
        yView.min = minY - pad;
        yView.max = maxY + pad;
      }

      // Mapping functions
      function xToPx(x) {
        return p.map(x, xView.min, xView.max, margins.left, canvasW - margins.right);
      }
      function yToPy(y) {
        return p.map(y, yView.min, yView.max, canvasH - margins.bottom, margins.top);
      }

      // Draw axes and ticks
      function drawAxes() {
        p.push();
        p.stroke('#cccccc');
        p.strokeWeight(1);

        // X axis (y=0)
        const y0 = yToPy(0);
        if (y0 >= margins.top && y0 <= canvasH - margins.bottom) {
          p.line(margins.left, y0, canvasW - margins.right, y0);
        }

        // Y axis (x=0)
        const x0 = xToPx(0);
        if (x0 >= margins.left && x0 <= canvasW - margins.right) {
          p.line(x0, margins.top, x0, canvasH - margins.bottom);
        }

        // X ticks every 1 from 0..8 (and maybe -1..0)
        p.fill('#888');
        p.noStroke();
        p.textAlign(p.CENTER, p.TOP);
        for (let t = Math.ceil(xView.min); t <= Math.floor(xView.max); t++) {
          if (t % 1 !== 0) continue;
          const px = xToPx(t);
          const py = canvasH - margins.bottom;
          // tick
          p.stroke('#cccccc'); p.strokeWeight(1);
          p.line(px, py, px, py + 6);
          // grid line subtle
          p.stroke(240); p.strokeWeight(1);
          p.line(px, margins.top, px, canvasH - margins.bottom);
          // label
          p.noStroke();
          p.fill('#777');
          if (t >= 0 && t <= 8) p.text(t, px, py + 8);
        }

        // Y ticks: choose nice step
        const yRange = yView.max - yView.min;
        const rawStep = yRange / 8;
        const step = niceStep(rawStep);
        p.textAlign(p.RIGHT, p.CENTER);
        for (let v = Math.ceil(yView.min / step) * step; v <= yView.max; v += step) {
          const py = yToPy(v);
          // tick
          p.stroke('#cccccc'); p.strokeWeight(1);
          p.line(margins.left - 6, py, margins.left, py);
          // grid
          p.stroke(240);
          p.line(margins.left, py, canvasW - margins.right, py);
          // label
          p.noStroke(); p.fill('#777');
          p.text(formatTick(v), margins.left - 8, py);
        }
        p.pop();
      }

      function niceStep(step) {
        // return a "nice" step size around the given step
        const pow10 = Math.pow(10, Math.floor(Math.log10(step)));
        const scaled = step / pow10;
        let nice;
        if (scaled < 1.5) nice = 1;
        else if (scaled < 3) nice = 2;
        else if (scaled < 7) nice = 5;
        else nice = 10;
        return nice * pow10;
      }

      function formatTick(v) {
        const abs = Math.abs(v);
        if (abs >= 1000 || (abs > 0 && abs < 0.01)) return v.toExponential(0);
        if (abs < 1) return v.toFixed(2);
        if (abs < 10) return v.toFixed(1);
        return Math.round(v).toString();
      }

      function drawFunctionCurve() {
        const def = getFunc();
        const f = def.f;

        p.push();
        p.noFill();
        p.stroke(0);
        p.strokeWeight(3);

        const steps = 900;
        let prevValid = false;

        p.beginShape();
        for (let i = 0; i <= steps; i++) {
          const x = xView.min + (i / steps) * (xView.max - xView.min);
          const y = f(x);
          const valid = Number.isFinite(y);
          if (!valid) {
            if (prevValid) {
              p.endShape();
              p.beginShape();
            }
          } else {
            p.vertex(xToPx(x), yToPy(y));
          }
          prevValid = valid;
        }
        p.endShape();
        p.pop();
      }

      function drawUpperLimit() {
        const px = xToPx(state.a);
        p.push();
        p.stroke(220, 40, 40);
        p.strokeWeight(3);
        p.line(px, margins.top, px, canvasH - margins.bottom);
        p.pop();
      }

      // Utility: min/max sampling on interval
      function intervalMinMax(f, xl, xr) {
        let minV = Infinity, maxV = -Infinity;
        const steps = 50;
        for (let i = 0; i <= steps; i++) {
          const x = xl + (xr - xl) * (i / steps);
          const y = f(x);
          if (!Number.isFinite(y)) continue;
          if (y < minV) minV = y;
          if (y > maxV) maxV = y;
        }
        if (!Number.isFinite(minV)) minV = 0;
        if (!Number.isFinite(maxV)) maxV = 0;
        return { min: minV, max: maxV };
      }

      function drawEstimationShapes() {
        const def = getFunc();
        const f = def.f;
        const F = def.F;

        const n = Math.max(1, state.n|0);
        const a = Math.max(0.0001, Math.min(8, state.a));
        const dx = a / n;

        let totalEstimated = 0;

        p.push();
        p.stroke(85); // dark grey outline
        p.strokeWeight(1);

        for (let i = 0; i < n; i++) {
          const xL = i * dx;
          const xR = (i + 1) * dx;

          const yL = f(xL);
          const yR = f(xR);
          const xM = (xL + xR) / 2;
          const yM = f(xM);

          let height;
          let estArea;

          if (state.type === 'left') {
            height = yL;
            estArea = height * dx;
            drawRectArea(xL, xR, height, F);
          } else if (state.type === 'right') {
            height = yR;
            estArea = height * dx;
            drawRectArea(xL, xR, height, F);
          } else if (state.type === 'midpoint') {
            height = yM;
            estArea = height * dx;
            drawRectArea(xL, xR, height, F);
          } else if (state.type === 'trapezoidal') {
            estArea = 0.5 * (yL + yR) * dx;
            drawTrapezoid(xL, yL, xR, yR, F);
          } else if (state.type === 'overestimate' || state.type === 'underestimate') {
            const { min, max } = intervalMinMax(f, xL, xR);
            height = (state.type === 'overestimate') ? max : min;
            estArea = height * dx;
            drawRectArea(xL, xR, height, F);
          } else {
            // default to right
            height = yR;
            estArea = height * dx;
            drawRectArea(xL, xR, height, F);
          }

          totalEstimated += estArea;
        }

        p.pop();

        // overlay text
        const trueIntegral = getTrueIntegral(F, a);
        p.noStroke();
        p.fill('#666');
        p.textAlign(p.CENTER, p.TOP);
        p.textSize(14);
        const overlayY = margins.top + 8;
        p.text(`estimated area: ${totalEstimated.toFixed(5)}`, (margins.left + canvasW - margins.right) / 2, overlayY);
        p.text(`integral: ${trueIntegral.toFixed(5)}`, (margins.left + canvasW - margins.right) / 2, overlayY + 18);
      }

      function getTrueIntegral(F, a) {
        // From 0 to a
        const A = F(a);
        const B = F(0);
        return A - B;
      }

      function drawRectArea(xL, xR, height, F) {
        const trueSub = getTrueSubIntegral(F, xL, xR);
        const estArea = height * (xR - xL);
        const over = estArea > trueSub;

        const col = over ? [173, 216, 230, 180] : [144, 238, 144, 180]; // blue vs green
        p.fill(...col);

        const pxL = xToPx(xL), pxR = xToPx(xR);
        const py0 = yToPy(0), pyH = yToPy(height);

        p.beginShape();
        p.vertex(pxL, py0);
        p.vertex(pxL, pyH);
        p.vertex(pxR, pyH);
        p.vertex(pxR, py0);
        p.endShape(p.CLOSE);
      }

      function drawTrapezoid(xL, yL, xR, yR, F) {
        const trueSub = getTrueSubIntegral(F, xL, xR);
        const estArea = 0.5 * (yL + yR) * (xR - xL);
        const over = estArea > trueSub;
        const col = over ? [173, 216, 230, 180] : [144, 238, 144, 180];

        p.fill(...col);

        const pxL = xToPx(xL), pxR = xToPx(xR);
        const py0 = yToPy(0), pyL = yToPy(yL), pyR = yToPy(yR);

        p.beginShape();
        p.vertex(pxL, py0);
        p.vertex(pxL, pyL);
        p.vertex(pxR, pyR);
        p.vertex(pxR, py0);
        p.endShape(p.CLOSE);
      }

      function getTrueSubIntegral(F, xL, xR) {
        const a = F(xR);
        const b = F(xL);
        return a - b;
      }

      function setCanvasSize() {
        const container = document.getElementById('canvas-container');
        // Keep a pleasant aspect ratio
        const w = container.clientWidth;
        const h = Math.max(360, Math.round(w * 0.6));
        canvasW = w;
        canvasH = h;
        p.resizeCanvas(canvasW, canvasH);
      }

      p.setup = function() {
        const container = document.getElementById('canvas-container');
        const w = container.clientWidth || 900;
        const h = Math.max(360, Math.round(w * 0.6));
        canvasW = w; canvasH = h;
        const cnv = p.createCanvas(canvasW, canvasH);
        cnv.parent('canvas-container');
        p.noLoop(); // Redraw on demand
      };

      p.windowResized = function() {
        setCanvasSize();
        p.redraw();
      };

      p.draw = function() {
        p.background(248, 248, 248);

        computeYRange();
        drawAxes();
        drawFunctionCurve();
        drawEstimationShapes();
        drawUpperLimit();

        // frame border subtle
        p.noFill();
        p.stroke('#ddd');
        p.rect(0.5, 0.5, canvasW - 1, canvasH - 1);
      };
    };

    p5Instance = new p5(s);
  })();
</script>
</body>
</html>
