    current: document.getElementById('slider-current'),
    earth: document.getElementById('slider-earth-field'),
    radius: document.getElementById('slider-radius'),
    turns: document.getElementById('slider-turns'),
    vCurrent: document.getElementById('value-current'),
    vEarth: document.getElementById('value-earth-field'),
    vRadius: document.getElementById('value-radius'),
    vTurns: document.getElementById('value-turns'),
    reset: document.getElementById('btn-reset')
  };

  const params = {
    current: parseFloat(el.current.value),
    earth: parseFloat(el.earth.value),
    radius: parseFloat(el.radius.value),
    turns: parseInt(el.turns.value, 10)
  };

  // Formatting helpers
  function formatFixed(x, digits = 3) {
    // Trim trailing zeros
    const s = x.toFixed(digits);
    return s.replace(/\.?0+$/,'');
  }
  function formatEarthField(x) {
    const ax = Math.abs(x);
    if (ax !== 0 && ax < 1e-5) {
      // Show as scientific notation like 9e-6
      const exp = x.toExponential(0); // 9e-6 style
      return exp.replace(/e\+?/,'e');
    } else {
      // Show up to 7 decimals but trimmed
      return x.toFixed(7).replace(/\.?0+$/,'');
    }
  }

  function updateDisplays() {
    el.vCurrent.textContent = formatFixed(params.current, 3);
    el.vEarth.textContent = formatEarthField(params.earth);
    el.vRadius.textContent = formatFixed(params.radius, 2);
    el.vTurns.textContent = String(params.turns);
  }

  // Bind slider events
  ['input','change'].forEach(evt => {
    el.current.addEventListener(evt, () => {
      params.current = parseFloat(el.current.value);
      el.vCurrent.textContent = formatFixed(params.current, 3);
      if (window.redrawSketch) window.redrawSketch();
    });
    el.earth.addEventListener(evt, () => {
      params.earth = parseFloat(el.earth.value);
      el.vEarth.textContent = formatEarthField(params.earth);
      if (window.redrawSketch) window.redrawSketch();
    });
    el.radius.addEventListener(evt, () => {
      params.radius = parseFloat(el.radius.value);
      el.vRadius.textContent = formatFixed(params.radius, 2);
      if (window.redrawSketch) window.redrawSketch();
    });
    el.turns.addEventListener(evt, () => {
      params.turns = parseInt(el.turns.value, 10);
      el.vTurns.textContent = String(params.turns);
      if (window.redrawSketch) window.redrawSketch();
    });
  });

  el.reset.addEventListener('click', () => {
    // Reset sliders and params
    el.current.value = initialParams.current;
    el.earth.value = initialParams.earth;
    el.radius.value = initialParams.radius;
    el.turns.value = initialParams.turns;

    params.current = initialParams.current;
    params.earth = initialParams.earth;
    params.radius = initialParams.radius;
    params.turns = initialParams.turns;

    updateDisplays();
    if (window.redrawSketch) window.redrawSketch();
  });

  updateDisplays();

  // p5.js Sketch
  let sketch = (p) => {
    const worldMin = -0.5;
    const worldMax =  0.5;
    const margin = 60; // space for axis labels
    let plotLeft, plotRight, plotTop, plotBottom, plotW, plotH;
    let canvas;

    p.setup = function() {
      // Remove placeholder canvas so p5 can create one with the same id
      const placeholder = document.getElementById('p5-canvas');
      if (placeholder && placeholder.parentNode) {
        placeholder.parentNode.removeChild(placeholder);
      }
      canvas = p.createCanvas(640, 640);
      canvas.parent('viz-container');
      canvas.elt.id = 'p5-canvas';

      p.pixelDensity(1);
      p.colorMode(p.HSB, 360, 100, 100);
      p.noLoop();

      computePlotRect();
      window.addEventListener('resize', () => {
        // Keep fixed canvas size for simplicity; recompute rect anyway
        computePlotRect();
        p.redraw();
      });

      // Expose redraw function to DOM listeners
      window.redrawSketch = () => p.redraw();
    };

    function computePlotRect() {
      plotLeft = margin;
      plotRight = p.width - margin;
      plotTop = margin;
      plotBottom = p.height - margin;
      plotW = plotRight - plotLeft;
      plotH = plotBottom - plotTop;
    }

    function w2s(y, z) {
      const xPix = p.map(y, worldMin, worldMax, plotLeft, plotRight);
      const yPix = p.map(z, worldMax, worldMin, plotTop, plotBottom); // invert vertical
      return [xPix, yPix];
    }

    function insideWorld(y, z) {
      return (y >= worldMin && y <= worldMax && z >= worldMin && z <= worldMax);
    }

    // Magnetic field from a single circular loop (N turns)
    const MU0 = 4 * Math.PI * 1e-7;

    // Compute complete elliptic integrals K(m), E(m) via AGM; m in [0,1)
    function ellipticKE(m) {
      // Clamp
      if (m < 0) m = 0;
      if (m > 1) m = 1 - 1e-12;
      const k = Math.sqrt(m);
      let a = 1.0;
      let b = Math.sqrt(1 - k*k);
      let c = k;
      let sum = 0;
      let pow2 = 1;
      for (let i = 0; i < 40; i++) {
        const an = (a + b) * 0.5;
        const bn = Math.sqrt(a * b);
        const cn = (a - b) * 0.5;
        sum += pow2 * cn * cn;
        pow2 *= 2;
        a = an; b = bn;
        if (Math.abs(cn) < 1e-15) break;
      }
      const K = Math.PI / (2 * a);
      const E = K * (1 - sum);
      return {K, E};
    }

    function coilField(y, z, R, I, N, z_offset) {
      const zp = z - z_offset;
      const rho = Math.abs(y);
      const sgn = (y >= 0) ? 1 : -1;

      // On the axis (rho -> 0): By = 0, Bz has a simple closed form
      if (rho < 1e-10) {
        const Bz_axis = (MU0 * I * N * R * R) / (2 * Math.pow(R*R + zp*zp, 1.5));
        return {By: 0, Bz: Bz_axis};
      }

      const Rp = R + rho;
      const D = Math.sqrt(Rp*Rp + zp*zp);
      const m = (4 * R * rho) / (Rp*Rp + zp*zp); // parameter m = k^2
      const {K, E} = ellipticKE(m);

      const common1 = (MU0 * I * N) / (2 * Math.PI);
      const common2 = 1 / D;
      const q = (R - rho)*(R - rho) + zp*zp;

      const B_rho = common1 * (zp / rho) * common2 * (((R*R + rho*rho + zp*zp) / q) * E - K);
      const B_z   = common1 * common2 * (K - ((R*R - rho*rho - zp*zp) / q) * E);

      return {By: sgn * B_rho, Bz: B_z};
    }

    function helmholtzField(y, z) {
      const I = params.current;
      const R = params.radius;
      const N = params.turns;
      const earthZ = params.earth;

      const f1 = coilField(y, z, R, I, N, -R/2);
      const f2 = coilField(y, z, R, I, N,  R/2);
      return {By: f1.By + f2.By, Bz: f1.Bz + f2.Bz + earthZ};
    }

    // Compute magnitude range over a grid for color mapping
    function computeMagRange(gridN = 25) {
      let minMag = Infinity, maxMag = -Infinity;
      for (let i = 0; i < gridN; i++) {
        const y = p.map(i + 0.5, 0, gridN, worldMin, worldMax);
        for (let j = 0; j < gridN; j++) {
          const z = p.map(j + 0.5, 0, gridN, worldMin, worldMax);
          const B = helmholtzField(y, z);
          const mag = Math.hypot(B.By, B.Bz);
          if (mag < minMag) minMag = mag;
          if (mag > maxMag) maxMag = mag;
        }
      }
      if (!isFinite(minMag) || !isFinite(maxMag) || minMag === maxMag) {
        minMag = 0;
        maxMag = 1e-12;
      }
      return {minMag, maxMag};
    }

    function drawAxes() {
      p.push();
      p.strokeWeight(1.2);
      p.stroke(255, 0, 0);
      p.fill(255, 0, 0);
      // Axis lines
      // Y-axis (horizontal) at z = 0
      let [x0, y0] = w2s(worldMin, 0);
      let [x1, y1] = w2s(worldMax, 0);
      p.line(x0, y0, x1, y1);
      // Z-axis (vertical) at y = 0
      let [x2, y2] = w2s(0, worldMin);
      let [x3, y3] = w2s(0, worldMax);
      p.line(x2, y2, x3, y3);

      // Ticks and labels every 0.1, label every 0.2
      const step = 0.1;
      for (let v = worldMin; v <= worldMax + 1e-9; v += step) {
        // Horizontal axis ticks (Y axis line)
        let [xt, yt] = w2s(v, 0);
        p.line(xt, yt - 4, xt, yt + 4);
        if (Math.abs((v/0.2) - Math.round(v/0.2)) < 1e-6 && Math.abs(v) > 1e-8) {
          p.noStroke(); p.textAlign(p.CENTER, p.TOP); p.textSize(12);
          p.text(formatFixed(v, 1), xt, yt + 6);
          p.stroke(255,0,0);
        }
        // Vertical axis ticks (Z axis line)
        let [xv, yv] = w2s(0, v);
        p.line(xv - 4, yv, xv + 4, yv);
        if (Math.abs((v/0.2) - Math.round(v/0.2)) < 1e-6 && Math.abs(v) > 1e-8) {
          p.noStroke(); p.textAlign(p.RIGHT, p.CENTER); p.textSize(12);
          p.text(formatFixed(v, 1), xv - 6, yv);
          p.stroke(255,0,0);
        }
      }
      // Axis labels
      p.noStroke();
      p.textSize(14);
      p.textAlign(p.CENTER, p.TOP);
      p.text('Y', (plotLeft + plotRight)/2, plotBottom + 20);
      p.push();
      p.translate(plotLeft - 28, (plotTop + plotBottom)/2);
      p.rotate(-Math.PI/2);
      p.text('Z', 0, 0);
      p.pop();

      // Draw a subtle plot border
      p.noFill(); p.stroke(180); p.strokeWeight(1);
      p.rect(plotLeft, plotTop, plotW, plotH);
      p.pop();
    }

    function drawArrowhead(x1, y1, x2, y2, size = 5) {
      // Draws an arrow head at (x2, y2) pointing from (x1, y1) -> (x2, y2)
      const angle = Math.atan2(y2 - y1, x2 - x1);
      const a1 = angle + 3.14159 - 0.7;
      const a2 = angle + 3.14159 + 0.7;
      p.line(x2, y2, x2 + size * Math.cos(a1), y2 + size * Math.sin(a1));
      p.line(x2, y2, x2 + size * Math.cos(a2), y2 + size * Math.sin(a2));
    }

    function drawStreamline(y0, z0, colorMap, stepSize = 0.01, steps = 110, arrowEvery = 12) {
      // Draw both forward and backward from the seed point
      function trace(dir) {
        let y = y0, z = z0;
        for (let i = 0; i < steps; i++) {
          const B = helmholtzField(y, z);
          const mag = Math.hypot(B.By, B.Bz);
          if (mag < 1e-14) break;
          const dy = (B.By / mag) * stepSize * dir;
          const dz = (B.Bz / mag) * stepSize * dir;

          const yNext = y + dy;
          const zNext = z + dz;
          if (!insideWorld(yNext, zNext)) break;

          const hue = p.map(mag, colorMap.minMag, colorMap.maxMag, 60, 270); // low -> yellow, high -> blue/purple
          p.stroke(hue, 100, 100);
          p.strokeWeight(1.4);

          const [x1, y1] = w2s(y, z);
          const [x2, y2] = w2s(yNext, zNext);
          p.line(x1, y1, x2, y2);

          if (i % arrowEvery === 0) {
            drawArrowhead(x1, y1, x2, y2, 4.5);
          }

          y = yNext; z = zNext;
        }
      }
      trace(1);
      trace(-1);
    }

    p.draw = function() {
      p.background(255);
      drawAxes();

      // Prepare magnitude range
      const magRange = computeMagRange(26);

      // Seeds grid
      const gridN = 23; // 23x23 seeds
      for (let i = 0; i < gridN; i++) {
        const y = p.map(i + 0.5, 0, gridN, worldMin, worldMax);
        for (let j = 0; j < gridN; j++) {
          const z = p.map(j + 0.5, 0, gridN, worldMin, worldMax);
          drawStreamline(y, z, magRange, 0.010, 105, 10);
        }
      }
    };
  };

  new p5(sketch);
</script>
</body>
</html>
