<script>
  // Shared State
  let currentMessage = "";
  let currentCiphertext = "";

  // Constants
  const ALPHABET = Array.from({length:26}, (_,i)=>String.fromCharCode(65+i));
  const WORD_LIST = ['THE','IDES','OF','MARCH','ARE','COME','EVEN','YOU','BRUTUS','CAESAR','CIPHER','HELLO','WORLD','SECRET','MESSAGE','ATTACK','DAWN','I','A']
    .sort((a,b)=>b.length-a.length);

  // Elements - Encoding
  const btnToggleEncoding = document.getElementById('btn-toggle-encoding');
  const btnToggleDecoding = document.getElementById('btn-toggle-decoding');
  const panelEncoding = document.getElementById('panel-encoding');
  const panelDecoding = document.getElementById('panel-decoding');

  const selectMessage = document.getElementById('select-message');
  const btnClearEncoding = document.getElementById('btn-clear-encoding');
  const sliderEncoding = document.getElementById('slider-encoding-offset');
  const spanEncodingValue = document.getElementById('span-encoding-offset-value');
  const keyboardEncoding = document.getElementById('keyboard-encoding');

  const displayMessage = document.getElementById('display-message');
  const displayPlaintext = document.getElementById('display-plaintext');
  const displayAlphabetFromEnc = document.getElementById('display-encoding-alphabet-from');
  const displayAlphabetToEnc = document.getElementById('display-encoding-alphabet-to');
  const displayCiphertext = document.getElementById('display-ciphertext');

  // Elements - Decoding
  const btnIntercept = document.getElementById('btn-intercept');
  const btnClearDecoding = document.getElementById('btn-clear-decoding');
  const sliderDecoding = document.getElementById('slider-decoding-offset');
  const spanDecodingValue = document.getElementById('span-decoding-offset-value');
  const keyboardDecoding = document.getElementById('keyboard-decoding');

  const displayIntercept = document.getElementById('display-intercept');
  const displayAlphabetFromDec = document.getElementById('display-decoding-alphabet-from');
  const displayAlphabetToDec = document.getElementById('display-decoding-alphabet-to');
  const tableDecoding = document.getElementById('table-decoding-output').querySelector('tbody');

  // Helpers
  const spacedAlphabet = () => ALPHABET.join(' ');
  function shiftAlphabet(shift){
    shift = ((shift % 26) + 26) % 26;
    const shifted = ALPHABET.map((_,i)=>ALPHABET[(i+shift)%26]);
    return shifted.join(' ');
  }
  function encodeText(plaintext, offset){
    return plaintext.replace(/[A-Z]/g, c => {
      const code = c.charCodeAt(0) - 65;
      return String.fromCharCode(((code + offset) % 26) + 65);
    });
  }
  function decodeText(cipher, offset){
    return cipher.replace(/[A-Z]/g, c => {
      const code = c.charCodeAt(0) - 65;
      const v = ((code - offset) % 26 + 26) % 26;
      return String.fromCharCode(v + 65);
    });
  }
  function highlightWords(s){
    let out = '';
    for(let i=0;i<s.length;){
      let found = null;
      for(const w of WORD_LIST){
        if(s.startsWith(w,i)){
          found = w; break;
        }
      }
      if(found){
        out += `<span class="highlighted">${found}</span>`;
        i += found.length;
      }else{
        out += s[i];
        i++;
      }
    }
    return out;
  }

  // Initialization of keyboards
  function buildKeyboard(container, opts){
    // opts: {space:boolean, backspace:boolean}
    ALPHABET.forEach(letter=>{
      const btn = document.createElement('button');
      btn.textContent = letter;
      btn.className = 'key';
      btn.dataset.key = letter;
      container.appendChild(btn);
    });
    if(opts.space){
      const sp = document.createElement('button');
      sp.textContent = 'SPACE';
      sp.className = 'key key-wide';
      sp.dataset.key = 'SPACE';
      container.appendChild(sp);
    }
    if(opts.backspace){
      const bk = document.createElement('button');
      bk.textContent = 'BACKSPACE';
      bk.className = 'key key-wide';
      bk.dataset.key = 'BACKSPACE';
      container.appendChild(bk);
    }
  }

  buildKeyboard(keyboardEncoding, {space:true, backspace:true});
  buildKeyboard(keyboardDecoding, {space:false, backspace:true});

  // Tab controls
  btnToggleEncoding.addEventListener('click', ()=>{
    panelEncoding.style.display = '';
    panelDecoding.style.display = 'none';
    btnToggleEncoding.classList.add('active');
    btnToggleDecoding.classList.remove('active');
  });
  btnToggleDecoding.addEventListener('click', ()=>{
    panelEncoding.style.display = 'none';
    panelDecoding.style.display = '';
    btnToggleDecoding.classList.add('active');
    btnToggleEncoding.classList.remove('active');
  });

  // Encoding logic
  function updateEncoding(){
    const offset = parseInt(sliderEncoding.value,10) || 0;
    spanEncodingValue.textContent = String(offset);

    // Message display and plaintext
    const msgUpper = (currentMessage || "").toUpperCase();
    displayMessage.textContent = msgUpper;

    const plaintext = msgUpper.replace(/ /g, 'X');
    displayPlaintext.textContent = plaintext;

    // Alphabet mapping
    displayAlphabetFromEnc.textContent = spacedAlphabet();
    displayAlphabetToEnc.textContent = shiftAlphabet(offset);

    // Ciphertext
    const ciphertext = encodeText(plaintext, offset);
    displayCiphertext.textContent = ciphertext;
    currentCiphertext = ciphertext;
  }

  selectMessage.addEventListener('change', ()=>{
    currentMessage = selectMessage.value || "";
    updateEncoding();
  });

  sliderEncoding.addEventListener('input', updateEncoding);

  keyboardEncoding.addEventListener('click', (e)=>{
    const b = e.target.closest('button');
    if(!b) return;
    const key = b.dataset.key;
    if(key === 'BACKSPACE'){
      currentMessage = currentMessage.slice(0,-1);
    }else if(key === 'SPACE'){
      if(currentMessage.length < 32) currentMessage += ' ';
    }else if(/^[A-Z]$/.test(key)){
      if(currentMessage.length < 32) currentMessage += key;
    }
    // Reflect selection dropdown reset to default "Choose a message"
    if(selectMessage.value !== currentMessage){ selectMessage.selectedIndex = 0; }
    updateEncoding();
  });

  btnClearEncoding.addEventListener('click', ()=>{
    currentMessage = "";
    currentCiphertext = "";
    selectMessage.selectedIndex = 0;
    sliderEncoding.value = 0;
    spanEncodingValue.textContent = "0";
    displayMessage.textContent = "";
    displayPlaintext.textContent = "";
    displayCiphertext.textContent = "";
    updateEncoding();
  });

  // Decoding logic
  function updateDecoding(){
    const sliderVal = parseInt(sliderDecoding.value,10) || 0;
    spanDecodingValue.textContent = String(sliderVal);

    // Alphabet mapping (from = shifted by -offset, to = standard)
    const shift = ((-sliderVal % 26) + 26) % 26;
    displayAlphabetFromDec.textContent = shiftAlphabet(shift);
    displayAlphabetToDec.textContent = spacedAlphabet();

    // Generate decoding table
    tableDecoding.innerHTML = "";
    const intercept = displayIntercept.textContent.toUpperCase();

    const offsets = [sliderVal-2, sliderVal-1, sliderVal, sliderVal+1, sliderVal+2];
    for(const d of offsets){
      const tr = document.createElement('tr');
      const tdOffset = document.createElement('td'); tdOffset.textContent = String(d);
      const tdText = document.createElement('td');

      const decoded = decodeText(intercept, d);
      tdText.innerHTML = highlightWords(decoded);

      tr.appendChild(tdOffset); tr.appendChild(tdText);
      tableDecoding.appendChild(tr);
    }
  }

  btnIntercept.addEventListener('click', ()=>{
    displayIntercept.textContent = (currentCiphertext || "").toUpperCase();
    updateDecoding();
  });

  sliderDecoding.addEventListener('input', updateDecoding);

  keyboardDecoding.addEventListener('click', (e)=>{
    const b = e.target.closest('button');
    if(!b) return;
    const key = b.dataset.key;
    let text = displayIntercept.textContent.toUpperCase();
    if(key === 'BACKSPACE'){
      text = text.slice(0,-1);
    }else if(/^[A-Z]$/.test(key)){
      if(text.length < 32) text += key;
    }
    displayIntercept.textContent = text;
    updateDecoding();
  });

  btnClearDecoding.addEventListener('click', ()=>{
    displayIntercept.textContent = "";
    sliderDecoding.value = 0;
    spanDecodingValue.textContent = "0";
    tableDecoding.innerHTML = "";
    updateDecoding();
  });

  // Initial fill and render
  displayAlphabetFromEnc.textContent = spacedAlphabet();
  displayAlphabetToEnc.textContent = spacedAlphabet();
  displayAlphabetFromDec.textContent = spacedAlphabet();
  displayAlphabetToDec.textContent = spacedAlphabet();
  updateEncoding();
  updateDecoding();
</script>
</body>
</html>
