      <span class="value-badge" id="val-convolver">2</span>
    </div>

    <div class="row">
      <label class="title" for="slider-image-size">image size</label>
      <button class="mini-btn btn-minus" data-target="slider-image-size" aria-label="decrease image size">−</button>
      <input type="range" id="slider-image-size" min="10" max="40" step="1" value="24" />
      <button class="mini-btn" id="image-size-plus-btn" aria-label="increase image size">+</button>
      <span class="value-badge" id="val-image-size">24</span>
    </div>

    <div class="row">
      <label class="title" for="input-letter">pick a letter:</label>
      <input type="text" id="input-letter" class="text-input" maxlength="1" value="a" />
    </div>

    <div class="row">
      <label class="title">filter</label>
      <div class="radio-group">
        <input type="radio" name="filter" id="radio-shadow" value="shadow" checked />
        <label for="radio-shadow">shadow</label>
        <input type="radio" name="filter" id="radio-blur" value="blur" />
        <label for="radio-blur">blur</label>
      </div>
    </div>
  </div>

  <div id="visualization-area">
    <div class="images">
      <div class="image-block">
        <p>before</p>
        <div id="canvas-before-container"></div>
      </div>
      <div class="image-block">
        <p>after</p>
        <div id="canvas-after-container"></div>
      </div>
    </div>
  </div>

<script>
  // Globals referencing controls
  const sliderConvolver = document.getElementById('slider-convolver');
  const sliderImageSize = document.getElementById('slider-image-size');
  const inputLetter = document.getElementById('input-letter');
  const radioShadow = document.getElementById('radio-shadow');
  const radioBlur = document.getElementById('radio-blur');

  const valConvolver = document.getElementById('val-convolver');
  const valImageSize = document.getElementById('val-image-size');

  // Keep references to the two p5 instances
  let p5Before = null;
  let p5After = null;
  let sketchesReady = 0;

  // Helper: clamp to [a,b]
  const clamp = (x, a, b) => Math.max(a, Math.min(b, x));

  // Build the "before" p5 sketch (renders the binary grid)
  const beforeSketch = (p) => {
    p.grid = null;
    p.N = 0;

    p.setup = function () {
      p.pixelDensity(1);
      const cnv = p.createCanvas(200, 200);
      cnv.parent('canvas-before-container');
      p.noLoop();
      sketchesReady++;
      maybeInitialRender();
    };

    p.setData = function (grid, N) {
      p.grid = grid;
      p.N = N;
      p.redraw();
    };

    p.draw = function () {
      p.background(255);
      if (!p.grid) return;
      const N = p.N;
      const cell = p.width / N;
      p.noStroke();
      for (let j = 0; j < N; j++) {
        for (let i = 0; i < N; i++) {
          const v = p.grid[j][i]; // 0 or 1
          p.fill(v ? 0 : 255);
          p.rect(Math.round(i * cell), Math.round(j * cell), Math.ceil(cell), Math.ceil(cell));
        }
      }
    };
  };

  // Build the "after" p5 sketch (renders the convolved grid in grayscale)
  const afterSketch = (p) => {
    p.grid = null;
    p.N = 0;

    p.setup = function () {
      p.pixelDensity(1);
      const cnv = p.createCanvas(200, 200);
      cnv.parent('canvas-after-container');
      p.noLoop();
      sketchesReady++;
      maybeInitialRender();
    };

    p.setData = function (grid, N) {
      p.grid = grid;
      p.N = N;
      p.redraw();
    };

    p.draw = function () {
      p.background(255);
      if (!p.grid) return;
      const N = p.N;
      const cell = p.width / N;
      p.noStroke();
      for (let j = 0; j < N; j++) {
        for (let i = 0; i < N; i++) {
          const v = clamp(p.grid[j][i], 0, 1);
          const g = 255 * (1 - v);
          p.fill(g);
          p.rect(Math.round(i * cell), Math.round(j * cell), Math.ceil(cell), Math.ceil(cell));
        }
      }
    };
  };

  // Create instances
  p5Before = new p5(beforeSketch);
  p5After = new p5(afterSketch);

  // Once both sketches are ready, render the initial state
  function maybeInitialRender() {
    if (sketchesReady >= 2) {
      updateValueBadges();
      updateVisualization();
    }
  }

  // Build the binary "before" grid by rendering text into an offscreen p5.Graphics
  function makeLetterGrid(letter, N, p) {
    const g = p.createGraphics(N, N);
    g.pixelDensity(1);
    g.background(255);
    g.fill(0);
    g.noStroke();
    g.textAlign(g.CENTER, g.CENTER);
    g.textSize(N * 0.8);
    g.text(letter, N / 2, N / 2);

    g.loadPixels();
    const grid = Array.from({ length: N }, () => Array(N).fill(0));
    for (let y = 0; y < N; y++) {
      for (let x = 0; x < N; x++) {
        const idx = 4 * (y * N + x);
        const r = g.pixels[idx], gg = g.pixels[idx + 1], b = g.pixels[idx + 2];
        const bright = (r + gg + b) / 3;
        grid[y][x] = bright < 128 ? 1 : 0;
      }
    }
    g.remove();
    return grid;
  }

  // Create convolution kernels
  function buildKernel(k, mode) {
    const D = 2 * k + 1;
    const K = Array.from({ length: D }, () => Array(D).fill(0));
    if (mode === 'blur') {
      const val = 1 / (D * D);
      for (let r = 0; r < D; r++) for (let c = 0; c < D; c++) K[r][c] = val;
      return K;
    }
    // "shadow": k x k block of ones offset by (1,1) from center, normalized by k*k
    const start = Math.min(D - 1, Math.floor(D / 2) + 1); // center + 1
    for (let r = 0; r < k; r++) {
      for (let c = 0; c < k; c++) {
        const rr = clamp(start + r, 0, D - 1);
        const cc = clamp(start + c, 0, D - 1);
        K[rr][cc] = 1 / (k * k);
      }
    }
    return K;
  }

  // Convolution with zero-padding
  function convolve(grid, kernel) {
    const N = grid.length;
    const D = kernel.length;
    const half = Math.floor(D / 2);
    const out = Array.from({ length: N }, () => Array(N).fill(0));
    for (let y = 0; y < N; y++) {
      for (let x = 0; x < N; x++) {
        let sum = 0;
        for (let ky = 0; ky < D; ky++) {
          for (let kx = 0; kx < D; kx++) {
            const iy = y + (ky - half);
            const ix = x + (kx - half);
            if (iy >= 0 && iy < N && ix >= 0 && ix < N) {
              sum += kernel[ky][kx] * grid[iy][ix];
            }
          }
        }
        out[y][x] = sum;
      }
    }
    return out;
  }

  // Main update function
  function updateVisualization() {
    const k = parseInt(sliderConvolver.value, 10);
    const N = parseInt(sliderImageSize.value, 10);
    let letter = (inputLetter.value || 'a').slice(0, 1);
    if (!letter.match(/[a-zA-Z]/)) letter = 'a';
    letter = letter.toLowerCase();
    if (inputLetter.value !== letter) inputLetter.value = letter;

    const filter = radioBlur.checked ? 'blur' : 'shadow';

    // Build source (before) grid using p5Before for text rendering
    const beforeGrid = makeLetterGrid(letter, N, p5Before);
    // Build kernel and convolve
    const kernel = buildKernel(k, filter);
    const afterGrid = convolve(beforeGrid, kernel);

    // Render to canvases
    p5Before.setData(beforeGrid, N);
    p5After.setData(afterGrid, N);

    updateValueBadges();
  }

  function updateValueBadges() {
    valConvolver.textContent = sliderConvolver.value;
    valImageSize.textContent = sliderImageSize.value;
  }

  // Event listeners
  sliderConvolver.addEventListener('input', () => { updateValueBadges(); updateVisualization(); });
  sliderImageSize.addEventListener('input', () => { updateValueBadges(); updateVisualization(); });
  inputLetter.addEventListener('input', () => updateVisualization());
  document.getElementById('convolver-plus-btn').addEventListener('click', () => {
    sliderConvolver.stepUp();
    sliderConvolver.dispatchEvent(new Event('input', { bubbles: true }));
  });
  document.getElementById('image-size-plus-btn').addEventListener('click', () => {
    sliderImageSize.stepUp();
    sliderImageSize.dispatchEvent(new Event('input', { bubbles: true }));
  });
  // Optional minus buttons
  document.querySelectorAll('.btn-minus').forEach(btn => {
    btn.addEventListener('click', () => {
      const targetId = btn.getAttribute('data-target');
      const slider = document.getElementById(targetId);
      if (slider) {
        slider.stepDown();
        slider.dispatchEvent(new Event('input', { bubbles: true }));
      }
    });
  });

  // Radio buttons trigger update
  radioShadow.addEventListener('input', updateVisualization);
  radioBlur.addEventListener('input', updateVisualization);

  // Up/Down arrow cycles letters
  inputLetter.addEventListener('keydown', (e) => {
    if (e.key !== 'ArrowUp' && e.key !== 'ArrowDown') return;
    e.preventDefault();
    const alphabet = 'abcdefghijklmnopqrstuvwxyz';
    let ch = (inputLetter.value || 'a').toLowerCase();
    if (!alphabet.includes(ch)) ch = 'a';
    const idx = alphabet.indexOf(ch);
    const dir = e.key === 'ArrowUp' ? 1 : -1;
    const next = alphabet[(idx + dir + 26) % 26];
    inputLetter.value = next;
    updateVisualization();
  });

  // Ensure initial render if sketches are already ready
  // (If p5 setup already ran before DOMContentLoaded)
  if (sketchesReady >= 2) {
    updateVisualization();
  }
</script>
</body>
</html>
