    S: 'SER', T: 'THR', V: 'VAL', W: 'TRP', Y: 'TYR'
  };

  function randChoice(arr) { return arr[Math.floor(Math.random()*arr.length)]; }
  function vecAdd(a,b){ return {x:a.x+b.x, y:a.y+b.y, z:a.z+b.z}; }
  function vecScale(a,s){ return {x:a.x*s, y:a.y*s, z:a.z*s}; }
  function distance(a,b){
    const dx=a.x-b.x, dy=a.y-b.y, dz=a.z-b.z;
    return Math.sqrt(dx*dx+dy*dy+dz*dz);
  }

  function generateProteinData(numResidues=400){
    // Random-walk backbone for CA atoms, approximately 3.8 Å steps
    const residues = [];
    let ca = {x:0,y:0,z:0};
    let dir = {x:1, y:0.2, z:0.1};
    const normalize = (v)=>{ const m=Math.hypot(v.x,v.y,v.z)||1; return {x:v.x/m,y:v.y/m,z:v.z/m}; };

    for(let i=1;i<=numResidues;i++){
      // random turn
      dir.x += (Math.random()-0.5)*0.6;
      dir.y += (Math.random()-0.5)*0.6;
      dir.z += (Math.random()-0.5)*0.6;
      dir = normalize(dir);
      const stepLen = 3.8 + (Math.random()-0.5)*0.4;
      ca = vecAdd(ca, vecScale(dir, stepLen));

      const aa1 = randChoice(AA1_LIST);
      const res = {
        aa1: aa1,
        resName: AA1_TO_AA3[aa1],
        resSeq: i,
        atoms: []
      };
      // place atoms around CA with simple offsets
      const offsets = {
        CA: {x:0,y:0,z:0},
        N:  {x:-1.2, y: 0.6, z: 0.0},
        C:  {x: 1.3, y: 0.5, z: 0.0},
        O:  {x: 1.9, y: 0.7, z:-0.4},
        CB: {x: 0.0, y:-1.2, z: 1.0}
      };
      for(const atomName of ['CA','N','C','O','CB']){
        const off = offsets[atomName];
        const p = { x: ca.x + off.x + (Math.random()-0.5)*0.2,
                    y: ca.y + off.y + (Math.random()-0.5)*0.2,
                    z: ca.z + off.z + (Math.random()-0.5)*0.2 };
        res.atoms.push({ atomName, x:p.x, y:p.y, z:p.z });
      }
      residues.push(res);
    }
    return residues;
  }

  // Precompute distance matrix between CA atoms
  function computeDistanceMatrix(residues){
    const N = residues.length;
    const cas = residues.map(r => {
      const a = r.atoms.find(at => at.atomName==='CA');
      return {x:a.x, y:a.y, z:a.z};
    });
    const dist = Array.from({length:N}, ()=>new Float32Array(N));
    for(let i=0;i<N;i++){
      dist[i][i]=0;
      for(let j=i+1;j<N;j++){
        const d = distance(cas[i],cas[j]);
        dist[i][j]=d; dist[j][i]=d;
      }
    }
    return dist;
  }

  function clamp(v,min,max){ return Math.max(min, Math.min(max,v)); }

  // ----------------------- Global State -----------------------
  let protein = generateProteinData(400);
  const distMatrix = computeDistanceMatrix(protein);

  // Controls state / references
  const el = {
    btn3d: document.getElementById('btn-crystallography'),
    btn2d: document.getElementById('btn-contact-map'),
    lateral: document.getElementById('checkbox-lateral-chains'),
    gray: document.getElementById('slider-gray-level'),
    grayVal: document.getElementById('gray-level-value'),
    browse: document.getElementById('slider-browse-sequence'),
    browseVal: document.getElementById('browse-sequence-value'),
    selectAA: document.getElementById('select-residue'),
    xSlider: document.getElementById('slider-residue-x'),
    xVal: document.getElementById('residue-x-value'),
    ySlider: document.getElementById('slider-residue-y'),
    yVal: document.getElementById('residue-y-value'),
    rmsd: document.getElementById('slider-rmsd'),
    rmsdVal: document.getElementById('rmsd-value'),
    plotSize: document.getElementById('slider-plot-size'),
    plotSizeVal: document.getElementById('plot-size-value'),
    info: document.getElementById('info-display'),
    canvasContainer: document.getElementById('canvas-container'),
    resetBtn: document.getElementById('btn-reset')
  };

  // Populate select with 20 amino acids
  function initResidueSelect(){
    AA1_LIST.forEach(code=>{
      const opt = document.createElement('option');
      opt.value = code; opt.textContent = code;
      el.selectAA.appendChild(opt);
    });
    el.selectAA.value = 'A';
  }
  initResidueSelect();

  // Keep span values synced
  function syncValueSpans(){
    el.grayVal.textContent = (+el.gray.value).toFixed(2);
    el.browseVal.textContent = el.browse.value;
    el.xVal.textContent = el.xSlider.value;
    el.yVal.textContent = el.ySlider.value;
    el.rmsdVal.textContent = (+el.rmsd.value).toFixed(1);
    el.plotSizeVal.textContent = el.plotSize.value;
  }
  syncValueSpans();

  // ----------------------- Info Display -----------------------
  function aaLabel(idx){
    const res = protein[idx];
    return res ? `${res.resName}${res.resSeq}` : '';
  }
  function updateInfo(){
    const Aidx = +el.browse.value - 1;
    const Xidx = +el.xSlider.value - 1;
    const Yidx = +el.ySlider.value - 1;
    el.info.textContent = `Res A: ${aaLabel(Aidx)} | Res X: ${aaLabel(Xidx)} | Res Y: ${aaLabel(Yidx)}`;
  }

  // ----------------------- View Switch Logic -----------------------
  let currentView = '3d';
  function setActiveButton(){
    if(currentView==='3d'){
      el.btn3d.classList.add('active'); el.btn2d.classList.remove('active');
    }else{
      el.btn2d.classList.add('active'); el.btn3d.classList.remove('active');
    }
  }

  function clearCanvasContainer(){
    while(el.canvasContainer.firstChild){
      el.canvasContainer.removeChild(el.canvasContainer.firstChild);
    }
  }

  // ----------------------- THREE.JS 3D VIEW -----------------------
  let three = {
    renderer: null, scene: null, camera: null, controls: null,
    atomMeshes: [],   // per residue: {CA:mesh, N:mesh, C:mesh, O:mesh, CB:mesh}
    group: null, bboxHelper: null,
    animationId: null,
    initialCamPos: null, initialTarget: new THREE.Vector3()
  };

  function createThreeView(){
    destroyP5(); // ensure 2D is off
    clearCanvasContainer();
    const size = +el.plotSize.value;
    el.canvasContainer.style.width = size+'px';
    el.canvasContainer.style.height = size+'px';

    const renderer = new THREE.WebGLRenderer({antialias:true});
    renderer.setSize(size, size);
    el.canvasContainer.appendChild(renderer.domElement);

    const scene = new THREE.Scene();
    scene.background = new THREE.Color(0xffffff);

    const camera = new THREE.PerspectiveCamera(45, 1, 0.1, 5000);
    const controls = new THREE.OrbitControls(camera, renderer.domElement);

    // Lighting
    scene.add(new THREE.AmbientLight(0xffffff, 0.6));
    const dirLight = new THREE.DirectionalLight(0xffffff, 0.6);
    dirLight.position.set(1,1,1);
    scene.add(dirLight);

    // Spheres
    const group = new THREE.Group();
    const sphereGeo = new THREE.SphereGeometry(0.6, 16, 16); // Å (arbitrary scale)
    three.atomMeshes = [];
    for(const res of protein){
      const mset = {};
      for(const atom of res.atoms){
        const mat = new THREE.MeshLambertMaterial({color: 0x0000ff});
        const mesh = new THREE.Mesh(sphereGeo, mat);
        mesh.position.set(atom.x, atom.y, atom.z);
        mset[atom.atomName] = mesh;
        group.add(mesh);
      }
      three.atomMeshes.push(mset);
    }
    scene.add(group);
    three.group = group;

    // Bounding box helper
    const box = new THREE.Box3().setFromObject(group);
    const sizeVec = new THREE.Vector3();
    box.getSize(sizeVec);
    const center = new THREE.Vector3();
    box.getCenter(center);
    const bboxHelper = new THREE.Box3Helper(box, new THREE.Color(0xaaaaaa));
    scene.add(bboxHelper);
    three.bboxHelper = bboxHelper;

    // Camera position and controls
    const maxDim = Math.max(sizeVec.x, sizeVec.y, sizeVec.z);
    const camDist = maxDim * 1.8;
    camera.position.set(center.x + camDist, center.y + camDist, center.z + camDist);
    controls.target.copy(center);
    controls.update();

    three.initialCamPos = camera.position.clone();
    three.initialTarget = center.clone();

    three.renderer = renderer; three.scene = scene; three.camera = camera; three.controls = controls;

    updateThreeVisibility();
    updateThreeColors(); // apply initial colors

    function animate(){
      if(currentView!=='3d') return; // stop rendering when switched
      three.animationId = requestAnimationFrame(animate);
      renderer.render(scene, camera);
    }
    animate();
  }

  function destroyThree(){
    if(three.animationId) cancelAnimationFrame(three.animationId);
    if(three.renderer){
      three.renderer.dispose();
      clearCanvasContainer();
    }
    three = {renderer:null, scene:null, camera:null, controls:null, atomMeshes:[], group:null, bboxHelper:null, animationId:null, initialCamPos:null, initialTarget:new THREE.Vector3()};
  }

  function updateThreeVisibility(){
    if(!three.group) return;
    const showAll = el.lateral.checked;
    for(const mset of three.atomMeshes){
      // CA is always visible
      if(mset.CA) mset.CA.visible = true;
      for(const key of ['N','C','O','CB']){
        if(mset[key]) mset[key].visible = !!showAll;
      }
    }
  }

  function mainChainColorFromGray(g){
    // interpolate from black to bright blue
    const c0 = new THREE.Color(0x000000);
    const c1 = new THREE.Color(0x0000ff);
    return c0.lerp(c1, clamp(g,0,1));
  }

  function updateThreeColors(){
    if(!three.group) return;
    const g = parseFloat(el.gray.value);
    const baseColor = mainChainColorFromGray(g);
    const A = (+el.browse.value)-1;
    const X = (+el.xSlider.value)-1;
    const Y = (+el.ySlider.value)-1;

    for(let i=0;i<three.atomMeshes.length;i++){
      const mset = three.atomMeshes[i];
      for(const key of ['CA','N','C','O','CB']){
        const mesh = mset[key]; if(!mesh) continue;
        // default base color
        mesh.material.color.copy(baseColor);
      }
    }
    // Highlight A (all atoms yellow)
    if(three.atomMeshes[A]){
      for(const key of ['CA','N','C','O','CB']){
        const mesh = three.atomMeshes[A][key]; if(mesh) mesh.material.color.set(0xffff00);
      }
    }
    // X and Y CA atoms red
    if(three.atomMeshes[X] && three.atomMeshes[X].CA) three.atomMeshes[X].CA.material.color.set(0xff0000);
    if(three.atomMeshes[Y] && three.atomMeshes[Y].CA) three.atomMeshes[Y].CA.material.color.set(0xff0000);
  }

  function resizeThree(){
    if(!three.renderer) return;
    const size = +el.plotSize.value;
    el.canvasContainer.style.width = size+'px';
    el.canvasContainer.style.height = size+'px';
    three.renderer.setSize(size, size);
    if(three.camera){
      three.camera.aspect = 1;
      three.camera.updateProjectionMatrix();
    }
  }

  function resetThreeCamera(){
    if(!three.camera || !three.controls || !three.initialCamPos) return;
    three.camera.position.copy(three.initialCamPos);
    three.controls.target.copy(three.initialTarget);
    three.controls.update();
  }

  // ----------------------- P5.JS 2D CONTACT MAP -----------------------
  let p5Instance = null;

  function createP5View(){
    destroyThree();
    clearCanvasContainer();
    const size = +el.plotSize.value;
    el.canvasContainer.style.width = size+'px';
    el.canvasContainer.style.height = size+'px';

    const N = protein.length;
    const getRMSD = ()=> +el.rmsd.value;
    const getX = ()=> +el.xSlider.value;
    const getY = ()=> +el.ySlider.value;

    const sketch = (p)=>{
      let margin = 40;

      p.setup = function(){
        const c = p.createCanvas(size, size);
        c.parent('canvas-container');
        p.noLoop();
        p.pixelDensity(1);
      };

      p.windowResized = function(){ /* handled by external resize */ };

      function mapIndexToPixel(i){
        // i in [1,N] -> pixel in [margin, width-margin]
        return p.map(i, 1, N, margin, p.width - margin);
      }

      function drawAxes(){
        p.push();
        p.stroke(180); p.strokeWeight(1);
        // outer frame
        p.noFill();
        p.rect(margin, margin, p.width-2*margin, p.height-2*margin);
        // ticks
        const ticks = [1,100,200,300,400];
        p.fill(100); p.noStroke(); p.textSize(10);
        p.textAlign(p.CENTER, p.TOP);
        for(const t of ticks){
          const x = mapIndexToPixel(t);
          p.stroke(200); p.line(x, p.height-margin, x, p.height-margin+5);
          p.noStroke(); p.text(t, x, p.height-margin+6);
        }
        p.textAlign(p.RIGHT, p.CENTER);
        for(const t of ticks){
          const y = mapIndexToPixel(t);
          p.stroke(200); p.line(margin-5, y, margin, y);
          p.noStroke(); p.text(t, margin-7, y);
        }
        // mirrored labels
        p.textAlign(p.CENTER, p.BOTTOM);
        for(const t of ticks){
          const x = mapIndexToPixel(t);
          p.noStroke(); p.text(t, x, margin-6);
        }
        p.textAlign(p.LEFT, p.CENTER);
        for(const t of ticks){
          const y = mapIndexToPixel(t);
          p.noStroke(); p.text(t, p.width-margin+6, y);
        }
        p.pop();
      }

      function drawContacts(){
        const thr = getRMSD();
        p.push();
        p.noStroke();
        p.fill(0);
        // iterate all pairs; sparse draw can be rectangles
        for(let i=1;i<=N;i++){
          const xi = mapIndexToPixel(i);
          for(let j=1;j<=N;j++){
            if(distMatrix[i-1][j-1] <= thr){
              const yj = mapIndexToPixel(j);
              p.rect(xi, yj, 1, 1);
            }
          }
        }
        p.pop();
      }

      function drawCrosshairs(){
        const xv = mapIndexToPixel(getX());
        const yv = mapIndexToPixel(getY());
        p.push();
        p.stroke(255,0,0); p.strokeWeight(1.5);
        p.line(xv, margin, xv, p.height-margin);
        p.line(margin, yv, p.width-margin, yv);
        p.pop();
      }

      p.draw = function(){
        p.background(255);
        drawAxes();
        drawContacts();
        drawCrosshairs();
      };

      p.updatePlotSize = function(newSize){
        p.resizeCanvas(newSize, newSize);
        p.redraw();
      };

      p.triggerRedraw = function(){
        p.redraw();
      };
    };

    p5Instance = new p5(sketch);
  }

  function destroyP5(){
    if(p5Instance){
      p5Instance.remove();
      p5Instance = null;
    }
  }

  function resizeP5(){
    if(!p5Instance) return;
    const size = +el.plotSize.value;
    el.canvasContainer.style.width = size+'px';
    el.canvasContainer.style.height = size+'px';
    if(p5Instance.updatePlotSize) p5Instance.updatePlotSize(size);
  }

  function redrawP5(){
    if(p5Instance && p5Instance.triggerRedraw) p5Instance.triggerRedraw();
  }

  // ----------------------- Controls Wiring -----------------------
  function jumpToNextPrevResidueType(typeCode, fromIndex1Based, forward=true){
    const N = protein.length;
    let idx = fromIndex1Based-1;
    for(let step=1; step<=N; step++){
      idx = (idx + (forward?1:-1) + N) % N;
      if(protein[idx].aa1 === typeCode){
        return idx+1; // 1-based
      }
    }
    return fromIndex1Based; // fallback
  }

  // View switcher
  el.btn3d.addEventListener('click', ()=>{
    if(currentView==='3d') return;
    currentView = '3d';
    setActiveButton();
    createThreeView();
  });
  el.btn2d.addEventListener('click', ()=>{
    if(currentView==='2d') return;
    currentView = '2d';
    setActiveButton();
    createP5View();
    redrawP5();
  });

  // 3D specific controls
  el.lateral.addEventListener('change', ()=>{
    if(currentView==='3d') updateThreeVisibility();
  });
  el.gray.addEventListener('input', ()=>{
    syncValueSpans();
    if(currentView==='3d') updateThreeColors();
  });
  el.browse.addEventListener('input', ()=>{
    syncValueSpans();
    updateInfo();
    if(currentView==='3d') updateThreeColors(); else redrawP5();
  });

  // Select residue type: change -> jump forward to next match, key up/down -> prev/next
  el.selectAA.addEventListener('change', ()=>{
    const newIdx = jumpToNextPrevResidueType(el.selectAA.value, +el.browse.value, true);
    el.browse.value = newIdx;
    syncValueSpans();
    updateInfo();
    if(currentView==='3d') updateThreeColors(); else redrawP5();
  });
  el.selectAA.addEventListener('keydown', (e)=>{
    if(e.key==='ArrowUp' || e.key==='ArrowDown'){
      e.preventDefault();
      const forward = (e.key==='ArrowDown');
      const newIdx = jumpToNextPrevResidueType(el.selectAA.value, +el.browse.value, forward);
      el.browse.value = newIdx;
      syncValueSpans();
      updateInfo();
      if(currentView==='3d') updateThreeColors(); else redrawP5();
    }
  });

  // Shared controls
  el.xSlider.addEventListener('input', ()=>{
    syncValueSpans();
    updateInfo();
    if(currentView==='3d') updateThreeColors(); else redrawP5();
  });
  el.ySlider.addEventListener('input', ()=>{
    syncValueSpans();
    updateInfo();
    if(currentView==='3d') updateThreeColors(); else redrawP5();
  });
  el.rmsd.addEventListener('input', ()=>{
    syncValueSpans();
    if(currentView==='2d') redrawP5();
  });
  el.plotSize.addEventListener('input', ()=>{
    syncValueSpans();
    if(currentView==='3d') resizeThree(); else resizeP5();
  });

  // Reset camera button
  el.resetBtn.addEventListener('click', ()=>{
    if(currentView==='3d') resetThreeCamera();
  });

  // ----------------------- Init -----------------------
  function init(){
    // set the container square size from default plot size
    const size = +el.plotSize.value;
    el.canvasContainer.style.width = size+'px';
    el.canvasContainer.style.height = size+'px';
    updateInfo();
    createThreeView(); // default view
  }

  init();
  </script>
</body>
</html>
