  function getB() { return Number(inputB.value) || 1; }
  function getC() { return Number(sliderC.value) || 1; }

  // Slider enable/disable helper
  function setSliderEnabled(enabled) {
    sliderC.disabled = !enabled;
    displayC.classList.toggle('muted', !enabled);
  }

  // Update slider max when a/b change
  function updateSliderBounds() {
    const a = getA(), b = getB();
    const newMax = Math.max(1, a * b * 2);
    sliderC.max = String(newMax);
    if (Number(sliderC.value) > newMax) sliderC.value = String(newMax);
    if (Number(sliderC.value) < Number(sliderC.min)) sliderC.value = sliderC.min;
    displayC.textContent = sliderC.value;
  }

  // Update equation text (no drawing)
  function updateEquationText() {
    const a = getA(), b = getB(), c = getC();
    const g = gcd(a,b);
    if (g !== 1) {
      eqText.textContent = 'Numbers a and b should be coprime!';
      return;
    }
    if (checkboxDisplay.checked) {
      const F = a*b - a - b;
      eqText.textContent = `F = a*b - a - b = ${F}.`;
    } else {
      eqText.textContent = `Current equation: ${a}x+${b}y = ${c}.`;
    }
  }

  // p5 sketch (instance mode)
  const sketch = (p) => {
    const W = 550, H = 550, margin = 48;
    let stepX = 1, stepY = 1;

    p.setup = () => {
      const cnv = p.createCanvas(W, H);
      cnv.parent('canvas-container');
      p.noLoop();
      redrawAll();
    };

    // Get two intersections of line a*x + b*y = c with rectangle [0,b]x[0,a]
    function lineRectIntersections(a, b, c, xmax, ymax) {
      const pts = [];
      // x=0 => y=c/b
      if (b !== 0) {
        const y = c / b;
        if (y >= 0 && y <= ymax) pts.push([0, y]);
      }
      // y=0 => x=c/a
      if (a !== 0) {
        const x = c / a;
        if (x >= 0 && x <= xmax) pts.push([x, 0]);
      }
      // x=xmax => y=(c - a*xmax)/b
      if (b !== 0) {
        const y2 = (c - a * xmax) / b;
        if (y2 >= 0 && y2 <= ymax) pts.push([xmax, y2]);
      }
      // y=ymax => x=(c - b*ymax)/a
      if (a !== 0) {
        const x2 = (c - b * ymax) / a;
        if (x2 >= 0 && x2 <= xmax) pts.push([x2, ymax]);
      }
      // Remove duplicates by rounding
      const uniq = [];
      const seen = new Set();
      for (const [x, y] of pts) {
        const key = x.toFixed(6) + ',' + y.toFixed(6);
        if (!seen.has(key)) { seen.add(key); uniq.push([x, y]); }
      }
      return uniq.slice(0, 2);
    }

    // Compute representability up to F (for numbers mode when showing highest nonsolvable)
    function representabilitySetUpToF(a, b) {
      const F = a*b - a - b;
      const arr = new Array(F + 1).fill(false);
      // Mark 0 as representable (x=y=0)
      arr[0] = true;
      for (let n = 1; n <= F; n++) {
        let rep = false;
        for (let x = 0; x <= Math.floor(n / a); x++) {
          const rem = n - a * x;
          if (rem % b === 0) { rep = true; break; }
        }
        arr[n] = rep;
      }
      return {F, arr};
    }

    function drawAxesAndTicks(a, b) {
      // Determine scaling steps
      stepX = (p.width - 2*margin) / Math.max(1, b);
      stepY = (p.height - 2*margin) / Math.max(1, a);

      // Axes (in scaled coordinates)
      p.push();
      p.translate(margin, p.height - margin);
      p.scale(1, -1);
      p.scale(stepX, stepY);

      p.stroke(60);
      p.strokeWeight(1 / Math.max(stepX, stepY));
      p.line(0, 0, b, 0); // x axis
      p.line(0, 0, 0, a); // y axis

      p.pop();

      // Numeric labels along axes (screen coordinates)
      p.push();
      p.textAlign(p.CENTER, p.TOP);
      p.fill(30, 60, 200);
      p.noStroke();

      for (let xi = 1; xi <= b; xi++) {
        const sx = margin + xi * stepX;
        const sy = p.height - margin + 6;
        p.text(xi, sx, sy);
      }
      p.textAlign(p.RIGHT, p.CENTER);
      for (let yi = 0; yi <= a; yi++) {
        const sx = margin - 6;
        const sy = p.height - (margin + yi * stepY);
        p.text(yi, sx, sy);
      }
      p.pop();
    }

    function drawDotsMode(a, b, c, showF) {
      // Coordinate transform to grid units
      p.push();
      p.translate(margin, p.height - margin);
      p.scale(1, -1);
      p.scale(stepX, stepY);

      // All dots
      p.noStroke();
      p.fill(20);
      const r = 0.14; // in grid units
      for (let x = 0; x <= b; x++) {
        for (let y = 0; y <= a; y++) {
          p.circle(x, y, r);
        }
      }

      // Red line for ax + by = c (or F)
      const target = showF ? (a*b - a - b) : c;
      const seg = lineRectIntersections(a, b, target, b, a);
      if (seg.length === 2) {
        p.stroke(220, 40, 40);
        p.strokeWeight(2 / Math.max(stepX, stepY));
        p.line(seg[0][0], seg[0][1], seg[1][0], seg[1][1]);
      }

      // Highlight solutions if we are not in "highest nonsolvable"
      if (!showF) {
        p.noStroke();
        p.fill(40, 200, 70);
        const r2 = 0.28;
        for (let x = 0; x <= b; x++) {
          for (let y = 0; y <= a; y++) {
            if (a*x + b*y === c) {
              p.circle(x, y, r2);
            }
          }
        }
      }

      p.pop();
    }

    function drawNumbersMode(a, b, c, showF) {
      const fontPx = Math.max(10, Math.min(18, Math.floor(Math.min(stepX, stepY) * 0.55)));
      p.push();
      p.textAlign(p.CENTER, p.CENTER);
      p.textSize(fontPx);

      let repInfo = null;
      if (showF) repInfo = representabilitySetUpToF(a, b);

      for (let x = 0; x <= b; x++) {
        for (let y = 0; y <= a; y++) {
          const val = a*x + b*y;
          let col = [20, 20, 20];
          if (!showF && val === c) col = [40, 200, 70]; // green
          if (showF && val <= repInfo.F && !repInfo.arr[val]) col = [220, 50, 50]; // red for unsolvable
          p.fill(...col);
          const px = margin + x * stepX;
          const py = p.height - (margin + y * stepY);
          p.text(String(val), px, py);
        }
      }
      p.pop();
    }

    function blankCanvas() {
      p.background(255);
    }

    function drawScene() {
      const a = getA(), b = getB(), c = getC();
      const coprime = gcd(a,b) === 1;
      p.background(255);

      if (!coprime) {
        // Only clear canvas (no grid), as required
        return;
      }

      drawAxesAndTicks(a, b);

      const dotsMode = checkboxMode.checked;
      const showF = checkboxDisplay.checked;

      if (dotsMode) {
        drawDotsMode(a, b, c, showF);
      } else {
        drawNumbersMode(a, b, c, showF);
      }
    }

    function redrawAll() {
      updateSliderBounds();
      updateEquationText();
      p.redraw();
    }

    // Expose helpers for external events
    p.redrawAll = redrawAll;
    p.drawScene = drawScene;

    // p5 draw
    p.draw = () => {
      drawScene();
    };
  };

  const myp5 = new p5(sketch);

  // Event wiring
  function handleABChange() {
    const a = Math.min(Math.max(1, getA()), 20);
    const b = Math.min(Math.max(1, getB()), 20);
    inputA.value = a;
    inputB.value = b;

    updateSliderBounds();

    const g = gcd(a, b);
    if (g !== 1) {
      setSliderEnabled(!checkboxDisplay.checked); // no need to force, but keep UI consistent
    }
    updateEquationText();
    myp5.redraw();
  }

  inputA.addEventListener('input', handleABChange);
  inputB.addEventListener('input', handleABChange);

  sliderC.addEventListener('input', () => {
    displayC.textContent = sliderC.value;
    if (!checkboxDisplay.checked && gcd(getA(), getB()) === 1) {
      updateEquationText();
      myp5.redraw();
    } else {
      updateEquationText();
    }
  });

  checkboxMode.addEventListener('change', () => {
    updateEquationText();
    myp5.redraw();
  });

  checkboxDisplay.addEventListener('change', () => {
    const checked = checkboxDisplay.checked;
    setSliderEnabled(!checked);
    updateEquationText();
    myp5.redraw();
  });

  // Initialize slider bounds and text, then draw once
  updateSliderBounds();
  updateEquationText();
  setSliderEnabled(!checkboxDisplay.checked);
</script>
</body>
</html>
