    ];
    const lakesSeed = [
      {name:'Onega', lon:35.5, lat:61.7},
      {name:'Erie', lon:-81.2, lat:42.2},
      {name:'Huron', lon:-82.5, lat:45.0},
      {name:'Issyk-Kul', lon:77.3, lat:42.4},
      {name:'Michigan', lon:-87.0, lat:44.0},
      {name:'Ladoga', lon:31.5, lat:60.9},
