        if(worldImg){
          p.image(worldImg, 0, 0, p.width, p.height);
        }

        // Draw unfound markers
        if(checkShowLocations.checked){
          p.fill('red');
          p.noStroke();
          currentData.forEach((it)=>{
            if(!it.found){
              const pt = mapToPixels(it.lon, it.lat);
              p.circle(pt.x, pt.y, 10);
            }
          });
        }

        // Update hints occasionally and draw them
        if(checkShowSome.checked){
          if(p.frameCount % 60 === 0) chooseHints();
          p.fill('white');
          p.textSize(12);
          hintIndices.forEach(idx=>{
            const it = currentData[idx];
            if(!it) return;
            const pt = mapToPixels(it.lon, it.lat);
            p.text(it.name, pt.x + 8, pt.y - 8);
          });
        }

        // Cursor marker
        p.fill(173, 216, 230, 150);
        p.noStroke();
        if(p.mouseX >= -50 && p.mouseX <= p.width + 50 && p.mouseY >= -50 && p.mouseY <= p.height + 50){
          p.circle(p.mouseX, p.mouseY, 30);
        }

        // Status text update
        updatePositionText();
      };

      p.mouseClicked = function(){
        // Only process clicks inside canvas
        if(p.mouseX < 0 || p.mouseX > p.width || p.mouseY < 0 || p.mouseY > p.height) return;
        const tolerance = 15;
        for(let i=0; i<currentData.length; i++){
          const it = currentData[i];
          if(it.found) continue;
          const pt = mapToPixels(it.lon, it.lat);
          const d = p.dist(p.mouseX, p.mouseY, pt.x, pt.y);
          if(d <= tolerance){
            it.found = true;
            // Update table cell
            const cell = statusCells[i];
            if(cell){
              cell.textContent = 'True';
              cell.classList.add('found');
            }
            break; // only one find per click
          }
        }
      };

      // Expose helpers to window for debugging (optional)
      window.__geoHelpers = { mapToPixels, mapToGeo };
    };

    // Create the p5 instance and attach it
    new p5(sketch, 'canvas-container');
  </script>
