  *{box-sizing:border-box;}
  body{margin:0;font-family:system-ui,-apple-system,Segoe UI,Roboto,Ubuntu,Cantarell,"Helvetica Neue",Arial,"Noto Sans",sans-serif;background:#fafafa;color:#222;}
  #app-container{height:100vh;display:flex;flex-direction:column;}
  #control-panel{
    display:flex;align-items:center;gap:16px;flex-wrap:wrap;
    padding:10px 14px;background:var(--panel-bg);border-bottom:1px solid var(--border);
    font-size:14px;
  }
  #tiers-container{display:inline-flex;gap:6px;vertical-align:middle;}
  #tiers-container button{
    padding:4px 8px;border:1px solid var(--border);background:#fff;border-radius:6px;cursor:pointer;
  }
  #tiers-container button.active{
    background:var(--red);color:#fff;border-color:var(--red);font-weight:600;
  }
  #viz-container{
    flex:1;margin:12px;border:1px solid var(--border);border-radius:8px;background:#fff;
    display:flex;flex-direction:column;overflow:hidden;
  }
  #header-section{
    padding:10px 12px 0;text-align:center;
  }
  #original-text{margin:8px 0 0;font-weight:700;font-size:22px;letter-spacing:1px;}
  #encoded-text{margin:4px 0 8px;font-family:ui-monospace,SFMono-Regular,Menlo,Monaco,Consolas,"Liberation Mono","Courier New",monospace;}
  #main-content{flex:1;display:flex;flex-direction:row;gap:10px;padding:8px 8px 12px;}
  #info-panel{
    width:28%;min-width:220px;max-width:340px;padding:12px;
  }
  #explanation-text{
    font-size:14px;line-height:1.35;margin-top:8px;margin-bottom:14px;
  }
