<body>
<div id="app-container">
  <div id="control-panel">
    <div>
      <label>tiers</label>
      <div id="tiers-container">
        <button id="btn-tier-1">1</button>
        <button id="btn-tier-2">2</button>
        <button id="btn-tier-3">3</button>
        <button id="btn-tier-4">4</button>
        <button id="btn-tier-5">5</button>
      </div>
    </div>
    <div>
      <label>show binary</label><input type="checkbox" id="checkbox-binary">
    </div>
    <div>
      <label>characters</label>
      <input type="radio" id="radio-chars-no" name="chars-toggle" value="no"><label for="radio-chars-no">no</label>
      <input type="radio" id="radio-chars-yes" name="chars-toggle" value="yes"><label for="radio-chars-yes">yes</label>
    </div>
  </div>

  <div id="viz-container">
    <div id="header-section">
      <p id="original-text">MISSISSIPPI_RIVER</p>
      <p id="encoded-text"></p>
    </div>
    <div id="main-content">
      <div id="info-panel">
        <p id="explanation-text"></p>
        <div id="encoding-table"></div>
      </div>
      <div id="canvas-area"></div>
    </div>
  </div>
</div>

<script>
  // ----- State -----
  let currentTier = 5;          // default selected
  let showBinary = false;       // default unchecked
  let showNodeChars = true;     // default characters yes

  const encodedString = "1100000101000101001001000011111010011011110101";

  // Explanation text per tier
  const explanations = {
    1: "After determining the frequency of each letter, we draw the tree bottom up, one tier at a time.",
    2: "We continue grouping until we get to the highest tier.",
    3: "We continue grouping until we get to the highest tier.",
    4: "We continue grouping until we get to the highest tier.",
    5: "Once at the top, the total frequency becomes 17, which is the total number of characters."
  };

  // Encoding table static HTML
  const encodingHTML = [
    "I = 00",
    "S = 01",
    "P = 100",
    "R = 101",
    "M = 1100",
    "V = 1101",
    "E = 1110",
    "_ = 1111"
  ].map(s => `<p>${s}</p>`).join("");

  // ----- DOM Setup -----
  const explanationEl = document.getElementById("explanation-text");
  const encodingTableEl = document.getElementById("encoding-table");
  const encodedTextEl = document.getElementById("encoded-text");
  // Initialize default UI texts/visibility
  function updateInfoPanels() {
    explanationEl.textContent = explanations[currentTier];
    // encoding-table visibility
    if (currentTier < 2) {
      encodingTableEl.style.display = "none";
    } else {
      encodingTableEl.style.display = "block";
      encodingTableEl.innerHTML = encodingHTML;
    }
    // encoded text visibility
    if (currentTier < 5) {
      encodedTextEl.style.display = "none";
    } else {
      encodedTextEl.style.display = "block";
      encodedTextEl.textContent = encodedString;
    }
  }

  // Tier buttons setup
  const tierButtons = [1,2,3,4,5].map(n => document.getElementById(`btn-tier-${n}`));
  function setActiveTierButton(tier){
    tierButtons.forEach((btn, idx) => {
      if (idx+1 === tier) btn.classList.add("active");
      else btn.classList.remove("active");
    });
  }
  tierButtons.forEach((btn, idx) => {
    btn.addEventListener("click", () => {
      currentTier = idx + 1;
      setActiveTierButton(currentTier);
      updateInfoPanels();
      if (p5Instance) p5Instance.redraw();
    });
  });

  // Checkbox and radio controls
  const binaryCheckbox = document.getElementById("checkbox-binary");
  const radioYes = document.getElementById("radio-chars-yes");
  const radioNo  = document.getElementById("radio-chars-no");

  binaryCheckbox.addEventListener("change", () => {
    showBinary = binaryCheckbox.checked;
    if (p5Instance) p5Instance.redraw();
  });

  radioYes.addEventListener("change", () => {
    if (radioYes.checked) {
      showNodeChars = true;
      if (p5Instance) p5Instance.redraw();
    }
  });
  radioNo.addEventListener("change", () => {
    if (radioNo.checked) {
      showNodeChars = false;
      if (p5Instance) p5Instance.redraw();
    }
  });

  // Set defaults in the controls
  setActiveTierButton(5);
  radioYes.checked = true;
  binaryCheckbox.checked = false;
  updateInfoPanels();

  // ----- Tree Data -----
  // Positions are for an 800x500 canvas, as specified.
  const nodes = [
    { id: 'I',  label: 'I', freq: 5, tier: 1, pos: {x: 100, y: 450}, children: [] },
    { id: 'S',  label: 'S', freq: 4, tier: 1, pos: {x: 180, y: 450}, children: [] },
    { id: 'P',  label: 'P', freq: 2, tier: 1, pos: {x: 260, y: 450}, children: [] },
    { id: 'R',  label: 'R', freq: 2, tier: 1, pos: {x: 340, y: 450}, children: [] },
    { id: 'M',  label: 'M', freq: 1, tier: 1, pos: {x: 420, y: 450}, children: [] },
    { id: 'V',  label: 'V', freq: 1, tier: 1, pos: {x: 500, y: 450}, children: [] },
    { id: 'E',  label: 'E', freq: 1, tier: 1, pos: {x: 580, y: 450}, children: [] },
    { id: '_',  label: '_', freq: 1, tier: 1, pos: {x: 660, y: 450}, children: [] },
    { id: 'IS', label: 'IS', freq: 9, tier: 2, pos: {x: 140, y: 350}, children: ['I', 'S'] },
    { id: 'PR', label: 'PR', freq: 4, tier: 2, pos: {x: 300, y: 350}, children: ['P', 'R'] },
    { id: 'MV', label: 'MV', freq: 2, tier: 2, pos: {x: 460, y: 350}, children: ['M', 'V'] },
    { id: 'E_', label: 'E_', freq: 2, tier: 2, pos: {x: 620, y: 350}, children: ['E', '_'] },
    { id: 'MVE_', label: 'MVE_', freq: 4, tier: 3, pos: {x: 540, y: 250}, children: ['MV', 'E_'] },
    { id: 'PRMVE_', label: 'PRMVE_', freq: 8, tier: 4, pos: {x: 420, y: 150}, children: ['PR', 'MVE_'] },
    { id: 'ROOT', label: 'ISPRMVE_', freq: 17, tier: 5, pos: {x: 280, y: 50}, children: ['IS', 'PRMVE_'] },
  ];

  // Map for fast lookup and to find parents
  const nodeById = {};
  const parentOf = {};
  nodes.forEach(n => { nodeById[n.id] = n; });
  nodes.forEach(n => {
    if (n.children && n.children.length === 2) {
      n.children.forEach(ch => parentOf[ch] = n.id);
    }
  });

  // ----- p5 Sketch -----
  let p5Instance = null;
  const sketch = (p) => {
    let W = 800, H = 500;

    p.setup = function(){
      const cnv = p.createCanvas(W, H);
      cnv.parent('canvas-area');
      p.textFont('sans-serif');
      p.noLoop();
    };

    p.draw = function(){
      p.clear();
      p.background(255);

      // Edge drawing (lines first)
      p.stroke('#C70000');
      p.strokeWeight(2);
      nodes.forEach(parent => {
        if (!parent.children || parent.children.length !== 2) return;
        if (parent.tier > currentTier) return;

        parent.children.forEach((cid, idx) => {
          const child = nodeById[cid];
          if (!child || child.tier > currentTier) return;

          // draw line from parent to child
          p.line(parent.pos.x, parent.pos.y, child.pos.x, child.pos.y);

          // binary labels (0 for left, 1 for right)
          if (showBinary) {
            const midx = (parent.pos.x + child.pos.x) / 2;
            const midy = (parent.pos.y + child.pos.y) / 2;
            const label = idx === 0 ? '0' : '1';
            p.noStroke();
            p.fill(0);
            p.textSize(14);
            p.textAlign(p.CENTER, p.BOTTOM);
            // offset a little above the midpoint
            p.text(label, midx, midy - 6);
            p.stroke('#C70000');
          }
        });
      });

      // Node drawing
      nodes.forEach(n => {
        if (n.tier > currentTier) return;

        const isLeaf = !n.children || n.children.length === 0;
        let t;
        if (isLeaf) {
          t = `${n.label}(${n.freq})`;
        } else {
          t = showNodeChars ? `${n.label}(${n.freq})` : `(${n.freq})`;
        }

        p.textSize(14);
        const padX = 10, padY = 6;
        const w = Math.max(40, p.textWidth(t) + padX*2);
        const h = 26 + padY; // comfortable height

        // Node rectangle
        p.rectMode(p.CENTER);
        p.stroke('#C70000');
        p.fill('#FEFDD5');
        p.rect(n.pos.x, n.pos.y, w, h, 6);

        // Node text
        p.fill(0);
        p.noStroke();
        p.textAlign(p.CENTER, p.CENTER);
        p.text(t, n.pos.x, n.pos.y);
      });
    };
  };

  p5Instance = new p5(sketch);

</script>
</body>
</html>
