<title>Rectangles under y = 2/x and Summation Stack</title>
<script src="https://cdnjs.cloudflare.com/ajax/libs/p5.js/1.4.0/p5.js"></script>
<script src="https://cdn.jsdelivr.net/npm/mathjax@3/es5/tex-mml-chtml.js" id="MathJax-script"></script>
<style>
  :root {
    --panel-bg: #f5f5f7;
    --border-color: #dcdcdc;
    --axis-color: #333;
    --grid-color: #cccccc;
    --dash-color: #bfbfbf;
  }
  body {
    margin: 0;
    font-family: -apple-system, BlinkMacSystemFont, "Segoe UI", Roboto, Oxygen, Ubuntu, Cantarell, "Helvetica Neue", Arial, "Noto Sans", "Apple Color Emoji", "Segoe UI Emoji", "Segoe UI Symbol";
    background: #ffffff;
    color: #222;
  }
  #control-panel {
    display: flex;
    align-items: center;
    gap: 10px;
    padding: 10px 12px;
    background: var(--panel-bg);
    border-bottom: 1px solid var(--border-color);
  }
  #control-panel label {
    font-size: 14px;
    color: #444;
  }
  #slider-n {
    flex: 1;
  }
  #input-n {
    width: 60px;
  }
  #visualization-area {
    display: flex;
    flex-direction: row;
    gap: 16px;
    padding: 12px;
    border-top: 0;
  }
  #function-plot-container, #sum-plot-container {
    position: relative;
    background: #fff;
    border: 1px solid var(--border-color);
    border-radius: 4px;
    padding: 6px;
    display: flex;
    justify-content: center;
    align-items: center;
  }
  #function-plot-container { flex: 2; min-width: 380px; }
  #sum-plot-container { flex: 1; min-width: 200px; }
  /* Fixed heights so the sketches can size to container */
  #function-plot-container, #sum-plot-container { height: 360px; }
  /* The canvases will be created by p5; these placeholders satisfy the spec but are replaced at runtime */
  #function-plot-container canvas, #sum-plot-container canvas {
    display: block;
  }
  #formula-label {
    position: absolute;
    top: 10px;
    left: 18px;
    color: #0b57d0;
    pointer-events: none;
    font-size: 16px;
  }
</style>
</head>
<body>
  <div id="control-panel">
    <label for="slider-n">n</label>
    <input type="range" id="slider-n" min="1" max="20" step="1" value="1" />
    <input type="number" id="input-n" min="1" max="20" step="1" value="1" />
  </div>

  <div id="visualization-area">
    <div id="function-plot-container">
      <canvas id="canvas-function"></canvas>
      <div id="formula-label">$$y = \\frac{2}{x}$$</div>
    </div>
    <div id="sum-plot-container">
      <canvas id="canvas-sum"></canvas>
    </div>
  </div>

<script>
(function() {
  // Remove placeholder canvases; p5 will recreate them with the same IDs.
  const oldFunc = document.getElementById('canvas-function');
  if (oldFunc) oldFunc.remove();
  const oldSum = document.getElementById('canvas-sum');
  if (oldSum) oldSum.remove();

  // Global state
  const state = { n: 1 };

  // Colors (warm to cool)
  const termColors = [
    '#ffff80', // 1 yellow
    '#98fb98', // 2 pale green
    '#7fffd4', // 3 aquamarine
    '#00ffff', // 4 cyan
    '#87cefa', // 5 light sky blue
    '#6495ed', // 6 cornflower blue
    '#9370db', // 7 medium purple
    '#8a2be2', // 8 blue violet
    '#6a5acd', // 9 slate blue
    '#4169e1', // 10 royal blue
    '#1e90ff', // 11 dodger blue
    '#00bfff', // 12 deep sky blue
    '#20b2aa', // 13 light sea green
    '#3cb371', // 14 medium sea green
    '#66cdaa', // 15 medium aquamarine
    '#90ee90', // 16 light green
    '#ffd700', // 17 gold
    '#ffa500', // 18 orange
    '#ff8c00', // 19 dark orange
    '#ff69b4'  // 20 hot pink (fallback if > provided colors)
  ];

  // Controls
  const slider = document.getElementById('slider-n');
  const input = document.getElementById('input-n');

  function clampN(v) {
    const n = Math.max(parseInt(slider.min), Math.min(parseInt(slider.max), parseInt(v) || 1));
    return n;
  }

  function setN(newN, {from}={}) {
    const n = clampN(newN);
    state.n = n;
    if (from !== 'slider') slider.value = String(n);
    if (from !== 'input') input.value = String(n);
    if (p5Function && p5Sum) {
      p5Function.redraw();
      p5Sum.redraw();
    }
  }

  slider.addEventListener('input', () => setN(slider.value, {from: 'slider'}));
  input.addEventListener('input', () => setN(input.value, {from: 'input'}));

  // Utility for ticks
  function drawTicks(p, xMin, xMax, yMin, yMax, xToPx, yToPx, opts) {
    const {
      drawX = true, drawY = true, xTickStep = 1, yTickStep = 1,
      axisColor = '#333', labelColor = '#333'
    } = opts || {};

    p.push();
    p.stroke(axisColor);
    p.fill(labelColor);
    p.strokeWeight(1);

    // Axes
    p.line(xToPx(xMin), yToPx(0), xToPx(xMax), yToPx(0)); // x-axis
    p.line(xToPx(0), yToPx(yMin), xToPx(0), yToPx(yMax)); // y-axis

    p.textAlign(p.CENTER, p.TOP);
    p.textSize(11);

    if (drawX) {
      const xStart = Math.ceil(xMin);
      const xEnd = Math.floor(xMax);
      for (let xi = xStart; xi <= xEnd; xi += xTickStep) {
        const px = xToPx(xi);
        const py = yToPx(0);
        p.line(px, py - 4, px, py + 4);
        p.noStroke();
        p.text(String(xi), px, py + 6);
        p.stroke(axisColor);
      }
    }

    if (drawY) {
      const yStart = Math.ceil(yMin);
      const yEnd = Math.floor(yMax);
      p.textAlign(p.RIGHT, p.CENTER);
      for (let yi = yStart; yi <= yEnd; yi += yTickStep) {
        const py = yToPx(yi);
        const px = xToPx(0);
        p.line(px - 4, py, px + 4, py);
        p.noStroke();
        p.text(String(yi), px - 6, py);
        p.stroke(axisColor);
      }
    }
    p.pop();
  }

  // Left plot: function y = 2/x with rectangles
  const functionSketch = (p) => {
    const container = document.getElementById('function-plot-container');

    let W = container.clientWidth - 12; // account for padding
    let H = container.clientHeight - 12;

    const margin = {left: 44, right: 16, top: 10, bottom: 34};
    const world = {xMin: 0, xMax: 10, yMin: 0, yMax: 2.2};

    let canvas;

    function plotW() { return W - margin.left - margin.right; }
    function plotH() { return H - margin.top - margin.bottom; }

    function xToPx(x) { return margin.left + (x - world.xMin) / (world.xMax - world.xMin) * plotW(); }
    function yToPx(y) { return H - margin.bottom - (y - world.yMin) / (world.yMax - world.yMin) * plotH(); }

    function resize() {
      W = container.clientWidth - 12;
      H = container.clientHeight - 12;
      p.resizeCanvas(W, H);
    }

    p.setup = () => {
      canvas = p.createCanvas(W, H);
      canvas.id('canvas-function');
      canvas.parent(container);
      p.noLoop();
      p.pixelDensity(1.5);
    };

    p.windowResized = () => {
      resize();
      p.redraw();
    };

    p.draw = () => {
      p.background(255);

      // Axes and ticks
      drawTicks(p, world.xMin, world.xMax, world.yMin, world.yMax, xToPx, yToPx, {
        xTickStep: 1, yTickStep: 1
      });

      // Function y = 2/x (x > 0.5) as blue curve
      p.push();
      p.noFill();
      p.stroke('#0b57d0');
      p.strokeWeight(2);
      const startX = 0.5;
      const step = (world.xMax - startX) / 500;
      p.beginShape();
      for (let x = startX; x <= world.xMax; x += step) {
        const y = 2 / x;
        const px = xToPx(x);
        const py = yToPx(Math.min(Math.max(y, world.yMin), world.yMax));
        p.vertex(px, py);
      }
      p.endShape();
      p.pop();

      // Rectangles for k=1..n
      for (let k = 1; k <= state.n; k++) {
        const color = termColors[(k - 1) % termColors.length];
        const x1 = k;
        const x2 = k + 1;
        const yBottom = 2 / (k + 1);
        const yTop = 2 / k;

        const left = xToPx(x1);
        const right = xToPx(x2);
        const top = yToPx(yTop);
        const bottom = yToPx(yBottom);

        p.push();
        p.noStroke();
        const c = p.color(color);
        c.setAlpha(210);
        p.fill(c);
        p.rect(left, top, right - left, bottom - top);
        p.pop();

        // Vertical dashed lines for k <= 3
        if (k <= 3) {
          p.push();
          p.stroke('#bfbfbf');
          p.strokeWeight(1);
          p.drawingContext.setLineDash([5, 5]);
          p.line(xToPx(k), yToPx(0), xToPx(k), yToPx(2 / k));
          p.pop();
        }
      }
    };
  };

  // Right plot: stacked bar showing the sum
  const sumSketch = (p) => {
    const container = document.getElementById('sum-plot-container');

    let W = container.clientWidth - 12;
    let H = container.clientHeight - 12;

    const margin = {left: 44, right: 18, top: 10, bottom: 34};
    const world = {xMin: 0, xMax: 1.5, yMin: 0, yMax: 2.2};

    let canvas;

    function plotW() { return W - margin.left - margin.right; }
    function plotH() { return H - margin.top - margin.bottom; }
    function xToPx(x) { return margin.left + (x - world.xMin) / (world.xMax - world.xMin) * plotW(); }
    function yToPx(y) { return H - margin.bottom - (y - world.yMin) / (world.yMax - world.yMin) * plotH(); }

    function resize() {
      W = container.clientWidth - 12;
      H = container.clientHeight - 12;
      p.resizeCanvas(W, H);
    }

    p.setup = () => {
      canvas = p.createCanvas(W, H);
      canvas.id('canvas-sum');
      canvas.parent(container);
      p.noLoop();
      p.pixelDensity(1.5);
    };

    p.windowResized = () => {
      resize();
      p.redraw();
    };

    p.draw = () => {
      p.background(255);

      // Axes and ticks
      drawTicks(p, world.xMin, world.xMax, world.yMin, world.yMax, xToPx, yToPx, {
        xTickStep: 1, yTickStep: 1
      });

      // Draw the 1-unit-wide bar outline
      p.push();
      p.noFill();
      p.stroke('#444');
      p.strokeWeight(1);
      const barLeft = xToPx(0);
      const barRight = xToPx(1);
      const barBottom = yToPx(0);
      const barTop = yToPx(2); // just up to 2 for outline, visually neat
      p.rect(barLeft, barTop, barRight - barLeft, barBottom - barTop);
      p.pop();

      // Stack rectangles from k = n down to 1
      let y_stack = 0;
      for (let k = state.n; k >= 1; k--) {
        const h = 2 / k - 2 / (k + 1);
        const c = p.color(termColors[(k - 1) % termColors.length]);
        c.setAlpha(210);
        p.push();
        p.noStroke();
        p.fill(c);
        const left = xToPx(0);
        const right = xToPx(1);
        const top = yToPx(y_stack + h);
        const bottom = yToPx(y_stack);
        p.rect(left, top, right - left, bottom - top);
        p.pop();
        y_stack += h;
      }
    };
  };

  // Create the p5 instances in instance mode
  const p5Function = new p5(functionSketch, document.getElementById('function-plot-container'));
  const p5Sum = new p5(sumSketch, document.getElementById('sum-plot-container'));

  // Initialize MathJax typesetting (formula overlay)
  if (window.MathJax && MathJax.typesetPromise) {
    MathJax.typesetPromise();
  }

  // Initial draw
  setN(1);
})();
</script>
</body>
</html>
