      </div>

      <div class="slider-container">
        <label for="slider-km">k<sub>m</sub></label>
        <input type="range" id="slider-km" min="0.01" max="1" step="0.01" value="0.12">
        <span id="value-km"></span>
      </div>

      <div class="slider-container" id="container-k1" style="display: none;">
        <label for="slider-k1">k<sub>1</sub></label>
        <input type="range" id="slider-k1" min="0.1" max="2" step="0.0001" value="0.4545">
        <span id="value-k1"></span>
      </div>

      <div class="slider-container">
        <label for="slider-ds">D<sub>s</sub></label>
        <input type="range" id="slider-ds" min="0.01" max="1.0" step="0.01" value="0.6">
        <span id="value-ds"></span>
      </div>

      <div class="hint">Tip: Switch between models and views; drag sliders to see the diagram update in real time.</div>
    </div>

    <div id="visualization-area">
      <div id="plot-container"></div>
    </div>
  </div>

<script>
  // Global state
  let currentModel = 'monod';       // 'monod' or 'si'
  let currentView = 'bifurcation';  // 'bifurcation' or 'nontrivial'

  const params = {
    x2f: 4.0,
    umax: 0.6,
    km: 0.12,
    k1: 0.4545,
    ds: 0.6
  };

  // Utility: format value displays
  function setDisplay(id, value, digits) {
    document.getElementById(id).textContent = Number(value).toFixed(digits);
  }

  // Model functions
  function mu_monod(x, umax, km) {
    return umax * x / (km + x);
  }
  function dmu_monod(x, umax, km) {
    const den = km + x;
    return umax * km / (den * den);
  }
  function mu_si(x, umax, km, k1) {
    return umax * x / (km + x + k1 * x * x);
  }
  function dmu_si(x, umax, km, k1) {
    const den = km + x + k1 * x * x;
    return umax * (km - k1 * x * x) / (den * den);
  }

  function D_of_x2(x) {
    return currentModel === 'monod'
      ? mu_monod(x, params.umax, params.km)
      : mu_si(x, params.umax, params.km, params.k1);
  }
  function dDdx2(x) {
    return currentModel === 'monod'
      ? dmu_monod(x, params.umax, params.km)
      : dmu_si(x, params.umax, params.km, params.k1);
  }

  // Root finding utilities
  function bisect(f, lo, hi, tol = 1e-6, maxIter = 60) {
    let fLo = f(lo), fHi = f(hi);
    if (isNaN(fLo) || isNaN(fHi)) return null;
    if (fLo === 0) return lo;
    if (fHi === 0) return hi;
    if (fLo * fHi > 0) return null; // not bracketed
    for (let i = 0; i < maxIter; i++) {
      const mid = 0.5 * (lo + hi);
      const fMid = f(mid);
      if (Math.abs(fMid) < tol || (hi - lo) < tol) return mid;
      if (fLo * fMid <= 0) {
        hi = mid; fHi = fMid;
      } else {
        lo = mid; fLo = fMid;
      }
    }
    return 0.5 * (lo + hi);
  }

  function findRootsInRange(f, a, b, segments = 600) {
    const roots = [];
    const step = (b - a) / segments;
    let xPrev = a;
    let fPrev = f(xPrev);
    for (let i = 1; i <= segments; i++) {
      const x = a + i * step;
      const fx = f(x);
      if (!isFinite(fx)) { xPrev = x; fPrev = fx; continue; }
      if (fx === 0) {
        roots.push(x);
      } else if (isFinite(fPrev) && fPrev * fx < 0) {
        const r = bisect(f, xPrev, x);
        if (r !== null) roots.push(r);
      }
      xPrev = x; fPrev = fx;
    }
    // Deduplicate close roots
    const eps = 1e-4;
    return roots.filter((x, i, arr) => i === 0 || Math.abs(x - arr[i - 1]) > eps);
  }

  // Plotting
  function updatePlot() {
    if (currentView === 'bifurcation') {
      drawBifurcationDiagram();
    } else {
      drawNontrivialStatePlot();
    }
  }

  function drawBifurcationDiagram() {
    const N = 500;
    const x2max = Math.max(params.x2f, 1e-6);
    const x2 = Array.from({length: N + 1}, (_, i) => i * x2max / N);
    const Dvals = x2.map(D_of_x2);

    let traces = [];

    // Nontrivial steady state curve
    if (currentModel === 'si') {
      const xcrit = Math.sqrt(params.km / params.k1);
      const stableX2 = [], stableD = [];
      const unstableX2 = [], unstableD = [];
      for (let i = 0; i < x2.length; i++) {
        if (x2[i] <= xcrit) {
          stableX2.push(x2[i]); stableD.push(Dvals[i]);
        } else {
          unstableX2.push(x2[i]); unstableD.push(Dvals[i]);
        }
      }
      traces.push({
        x: stableD, y: stableX2,
        mode: 'lines',
        line: {color: '#1f77b4', width: 3},
        name: 'nontrivial (stable)',
        hovertemplate: 'D=%{x:.3f}<br>x₂=%{y:.3f}<extra></extra>',
        showlegend: false
      });
      if (unstableX2.length > 1) {
        traces.push({
          x: unstableD, y: unstableX2,
          mode: 'lines',
          line: {color: '#e55353', width: 3, dash: 'dash'},
          name: 'nontrivial (unstable)',
          hovertemplate: 'D=%{x:.3f}<br>x₂=%{y:.3f}<extra></extra>',
          showlegend: false
        });
      }
    } else {
      traces.push({
        x: Dvals, y: x2,
        mode: 'lines',
        line: {color: '#1f77b4', width: 3},
        name: 'nontrivial (stable)',
        hovertemplate: 'D=%{x:.3f}<br>x₂=%{y:.3f}<extra></extra>',
        showlegend: false
      });
    }

    // Trivial steady state line y = x2f, split at D_bif
    const D_bif = D_of_x2(params.x2f);
    const xEnd = 1.0;
    const leftEnd = Math.max(0, Math.min(D_bif, xEnd));
    const rightStart = Math.max(0, Math.min(D_bif, xEnd));

    // unstable segment: from 0 to D_bif
    traces.push({
      x: [0, leftEnd],
      y: [params.x2f, params.x2f],
      mode: 'lines',
      line: {color: '#e55353', width: 3, dash: 'dash'},
      name: 'trivial (unstable)',
      hoverinfo: 'skip',
      showlegend: false
    });
    // stable segment: from D_bif to 1
    if (rightStart < xEnd) {
      traces.push({
        x: [rightStart, xEnd],
        y: [params.x2f, params.x2f],
        mode: 'lines',
        line: {color: '#1f77b4', width: 3},
        name: 'trivial (stable)',
        hoverinfo: 'skip',
        showlegend: false
      });
    }

    // Intersection points: solve D(x2) = Ds within (0, x2f)
    const f = (x) => D_of_x2(x) - params.ds;
    const roots = findRootsInRange(f, 0, params.x2f, 1200);

    const stablePts = {x: [], y: []};
    const unstablePts = {x: [], y: []};
    roots.forEach(r => {
      const slope = dDdx2(r);
      if (slope >= 0) {
        stablePts.x.push(params.ds); stablePts.y.push(r);
      } else {
        unstablePts.x.push(params.ds); unstablePts.y.push(r);
      }
    });

    if (stablePts.x.length) {
      traces.push({
        x: stablePts.x, y: stablePts.y,
        mode: 'markers',
        marker: {color: 'magenta', size: 10, line: {color: '#fff', width: 1}},
        name: 'stable intersections',
        hovertemplate: 'D=%{x:.3f}<br>x₂=%{y:.3f}<extra></extra>',
        showlegend: false
      });
    }
    if (unstablePts.x.length) {
      traces.push({
        x: unstablePts.x, y: unstablePts.y,
        mode: 'markers',
        marker: {color: 'cyan', size: 10, line: {color: '#fff', width: 1}},
        name: 'unstable intersections',
        hovertemplate: 'D=%{x:.3f}<br>x₂=%{y:.3f}<extra></extra>',
        showlegend: false
      });
    }

    // Trivial solution point at (Ds, x2f)
    traces.push({
      x: [params.ds], y: [params.x2f],
      mode: 'markers',
      marker: {color: 'green', size: 10, line: {color: '#fff', width: 1}},
      name: 'trivial solution',
      hovertemplate: 'D=%{x:.3f}<br>x₂=%{y:.3f}<extra></extra>',
      showlegend: false
    });

    const layout = {
      margin: {l: 60, r: 20, t: 20, b: 60},
      xaxis: {title: 'dilution rate', range: [0, 1.0], zeroline: false, gridcolor: '#eaeaea'},
      yaxis: {title: 'x\u2082', range: [0, 6.0], zeroline: false, gridcolor: '#eaeaea'},
      showlegend: false,
      paper_bgcolor: 'white',
      plot_bgcolor: 'white',
      shapes: [
        {
          type: 'line',
          x0: params.ds, x1: params.ds, y0: 0, y1: 6.0,
          line: {color: '#555', width: 4}
        }
      ]
    };

    Plotly.react('plot-container', traces, layout, {responsive: true});
  }

  function drawNontrivialStatePlot() {
    const N = 700;
    const xmax = 7.0;
    const xs = Array.from({length: N + 1}, (_, i) => i * xmax / N);
    const ys = xs.map(x => currentModel === 'monod'
      ? mu_monod(x, params.umax, params.km)
      : mu_si(x, params.umax, params.km, params.k1)
    );

    const traces = [];

    // μ(x2) curve
    traces.push({
      x: xs,
      y: ys,
      mode: 'lines',
      line: {color: '#1f77b4', width: 3},
      name: 'μ(x₂)',
      hovertemplate: 'x₂=%{x:.3f}<br>μ=%{y:.3f}<extra></extra>',
      showlegend: false
    });

    // Horizontal line at Ds
    traces.push({
      x: [0, xmax],
      y: [params.ds, params.ds],
      mode: 'lines',
      line: {color: '#666', width: 2},
      name: 'D_s',
      hoverinfo: 'skip',
      showlegend: false
    });

    const layout = {
      margin: {l: 70, r: 20, t: 20, b: 60},
      xaxis: {title: 'substrate concentration', range: [0, xmax], gridcolor: '#eaeaea'},
      yaxis: {title: 'specific growth rate coefficient', range: [0, 1], gridcolor: '#eaeaea'},
      showlegend: false,
      paper_bgcolor: 'white',
      plot_bgcolor: 'white',
      shapes: []
    };

    if (currentModel === 'si') {
      // Bistability region
      const D_upper = params.umax / (1 + 2 * Math.sqrt(params.k1 * params.km));
      const D_lower = mu_si(params.x2f, params.umax, params.km, params.k1);
      const y0 = Math.min(D_lower, D_upper);
      const y1 = Math.max(D_lower, D_upper);

      // Shaded region
      layout.shapes.push({
        type: 'rect',
        xref: 'x', yref: 'y',
        x0: 0, x1: xmax,
        y0: y0, y1: y1,
        fillcolor: 'rgba(255,165,0,0.25)',
        line: {width: 0},
        layer: 'below'
      });

      // Dashed bounds
      traces.push({
        x: [0, xmax],
        y: [D_upper, D_upper],
        mode: 'lines',
        line: {color: '#e55353', width: 2, dash: 'dash'},
        hoverinfo: 'skip',
        showlegend: false
      });
      traces.push({
        x: [0, xmax],
        y: [D_lower, D_lower],
        mode: 'lines',
        line: {color: '#e55353', width: 2, dash: 'dash'},
        hoverinfo: 'skip',
        showlegend: false
      });
    }

    // Intersections μ(x) = Ds
    const f = (x) => (currentModel === 'monod'
      ? mu_monod(x, params.umax, params.km)
      : mu_si(x, params.umax, params.km, params.k1)) - params.ds;

    const dfunc = (x) => (currentModel === 'monod'
      ? dmu_monod(x, params.umax, params.km)
      : dmu_si(x, params.umax, params.km, params.k1));

    const roots = findRootsInRange(f, 0, xmax, 1400);
    const stable = {x: [], y: []};
    const unstable = {x: [], y: []};
    roots.forEach(r => {
      if (dfunc(r) >= 0) {
        stable.x.push(r); stable.y.push(params.ds);
      } else {
        unstable.x.push(r); unstable.y.push(params.ds);
      }
    });

    if (stable.x.length) {
      traces.push({
        x: stable.x, y: stable.y,
        mode: 'markers',
        marker: {color: 'magenta', size: 10, line: {color: '#fff', width: 1}},
        name: 'stable',
        hovertemplate: 'x₂=%{x:.3f}<br>μ=%{y:.3f}<extra></extra>',
        showlegend: false
      });
    }
    if (unstable.x.length) {
      traces.push({
        x: unstable.x, y: unstable.y,
        mode: 'markers',
        marker: {color: 'cyan', size: 10, line: {color: '#fff', width: 1}},
        name: 'unstable',
        hovertemplate: 'x₂=%{x:.3f}<br>μ=%{y:.3f}<extra></extra>',
        showlegend: false
      });
    }

    Plotly.react('plot-container', traces, layout, {responsive: true});
  }

  // Initialization and UI bindings
  function init() {
    // Initialize displays
    setDisplay('value-x2f', params.x2f, 1);
    setDisplay('value-umax', params.umax, 2);
    setDisplay('value-km', params.km, 2);
    setDisplay('value-k1', params.k1, 4);
    setDisplay('value-ds', params.ds, 2);

    const k1Container = document.getElementById('container-k1'); // hidden by default for Monod

    // Model selector
    document.getElementById('btn-monod').addEventListener('click', () => {
      currentModel = 'monod';
      document.getElementById('btn-monod').classList.add('active');
      document.getElementById('btn-si').classList.remove('active');
      // Hide k1 for monod
      k1Container.style.display = 'none';
      updatePlot();
    });
    document.getElementById('btn-si').addEventListener('click', () => {
      currentModel = 'si';
      document.getElementById('btn-si').classList.add('active');
      document.getElementById('btn-monod').classList.remove('active');
      // Show k1 for SI
      k1Container.style.display = 'grid';
      updatePlot();
    });

    // View selector
    document.getElementById('btn-bifurcation').addEventListener('click', () => {
      currentView = 'bifurcation';
      document.getElementById('btn-bifurcation').classList.add('active');
      document.getElementById('btn-nontrivial').classList.remove('active');
      updatePlot();
    });
    document.getElementById('btn-nontrivial').addEventListener('click', () => {
      currentView = 'nontrivial';
      document.getElementById('btn-nontrivial').classList.add('active');
      document.getElementById('btn-bifurcation').classList.remove('active');
      updatePlot();
    });

    // Sliders
    document.getElementById('slider-x2f').addEventListener('input', (e) => {
      params.x2f = parseFloat(e.target.value);
      setDisplay('value-x2f', params.x2f, 1);
      updatePlot();
    });
    document.getElementById('slider-umax').addEventListener('input', (e) => {
      params.umax = parseFloat(e.target.value);
      setDisplay('value-umax', params.umax, 2);
      updatePlot();
    });
    document.getElementById('slider-km').addEventListener('input', (e) => {
      params.km = parseFloat(e.target.value);
      setDisplay('value-km', params.km, 2);
      updatePlot();
    });
    document.getElementById('slider-k1').addEventListener('input', (e) => {
      params.k1 = parseFloat(e.target.value);
      setDisplay('value-k1', params.k1, 4);
      updatePlot();
    });
    document.getElementById('slider-ds').addEventListener('input', (e) => {
      params.ds = parseFloat(e.target.value);
      setDisplay('value-ds', params.ds, 2);
      updatePlot();
    });

    // Initial plot
    updatePlot();
  }

  // Start
  window.addEventListener('DOMContentLoaded', init);
</script>
</body>
</html>
