    <div id="controls-container">
      <div class="slider-group">
        <label for="slider-season">season</label>
        <input type="range" id="slider-season" min="0" max="1" step="0.001" value="0">
        <span id="value-season">0</span>
      </div>
      <div class="slider-group">
        <label for="slider-latitude">latitude</label>
        <input type="range" id="slider-latitude" min="0" max="90" step="0.01" value="51.4789">
        <span id="value-latitude">51.4789</span>
      </div>
      <div class="slider-group">
        <label for="slider-time">time of day</label>
        <input type="range" id="slider-time" min="0" max="24" step="0.01" value="12">
        <span id="value-time">12</span>
      </div>
    </div>

    <div id="main-content">
      <div id="globe-container"></div>
      <div id="plots-container">
        <div id="plot-instant"></div>
        <div id="plot-legend">
          <div class="legend-item"><div class="legend-swatch swatch-red"></div><div>equator</div></div>
          <div class="legend-item"><div class="legend-swatch swatch-black"></div><div>latitude</div></div>
          <div class="legend-item"><div class="legend-swatch swatch-blue"></div><div>north pole</div></div>
        </div>
        <div id="plot-cumulative"></div>
      </div>
    </div>
  </div>

<script>
/* ---------- Utility math ---------- */
const DEG2RAD = Math.PI/180;
function clamp(v,a,b){ return Math.max(a, Math.min(b, v)); }
function sinD(d){ return Math.sin(d*DEG2RAD); }
function cosD(d){ return Math.cos(d*DEG2RAD); }
function energyAt(latDeg, declDeg, hour){
  const HRA = 15*(hour-12); // degrees
  const s = sinD(latDeg)*sinD(declDeg) + cosD(latDeg)*cosD(declDeg)*cosD(HRA);
  const E0 = 1361; // W/m^2 solar constant
  return E0*Math.max(0, s);
}
function cumulativeArray(values, dt){
  const out=[0];
  for(let i=1;i<values.length;i++){
    out[i] = out[i-1] + values[i-1]*dt; // left Riemann sum with dt hours
  }
  return out;
}

/* ---------- Three.js setup ---------- */
let scene, camera, renderer, controls;
let earthGroup, earthMesh, wire, dirLight, ambLight;
let equatorLine, selectedLatLine;
let axisArrow, normalArrow, sunArrow;
let anchor; // local point at lon=0 on surface (moves with latitude)
const R = 1; // Earth radius

function makeProceduralEarthTexture(){
  const w=1024, h=512;
  const c = document.createElement('canvas');
  c.width=w; c.height=h;
  const g = c.getContext('2d');

  // ocean gradient
  const grd = g.createLinearGradient(0,0,0,h);
  grd.addColorStop(0, '#203a6b');
  grd.addColorStop(1, '#2b65b1');
  g.fillStyle = grd;
  g.fillRect(0,0,w,h);

  // continents (simple blobs)
  g.fillStyle = '#4c8f2f';
  g.globalAlpha = 0.95;
  function blob(cx, cy, rx, ry, rot=0, cr=20){
    g.save();
    g.translate(cx,cy);
    g.rotate(rot);
    g.beginPath();
    for(let a=0;a<2*Math.PI+0.05;a+=0.2){
      const rrx = rx*(0.9+0.2*Math.sin(a*3.1+cx*0.01));
      const rry = ry*(0.9+0.2*Math.cos(a*2.6+cy*0.01));
      const x = rrx*Math.cos(a);
      const y = rry*Math.sin(a);
      if(a===0) g.moveTo(x,y); else g.lineTo(x,y);
    }
    g.closePath();
    g.shadowColor='rgba(0,0,0,0.15)';
    g.shadowBlur=cr;
    g.fill();
    g.restore();
  }
  blob(280,300,150,95, 0.1);
  blob(380,240,110,70, -0.2);
  blob(720,260,190,110, 0.2);
  blob(850,230,120,70, -0.3);
  blob(540,360,150,80, 0.15);
  blob(180,200,160,90, -0.1);

  // ice caps
  g.globalAlpha = 0.85;
  g.fillStyle = '#dfe9f7';
  g.fillRect(0,0,w,35);
  g.fillRect(0,h-35,w,35);

  // subtle clouds
  g.globalAlpha = 0.12;
  g.fillStyle = '#ffffff';
  for(let i=0;i<120;i++){
    const x = Math.random()*w;
    const y = Math.random()*h;
    const rw = 60+Math.random()*160;
    const rh = 15+Math.random()*45;
    g.beginPath();
    g.ellipse(x,y,rw,rh, Math.random()*Math.PI, 0, 2*Math.PI);
    g.fill();
  }
  return new THREE.CanvasTexture(c);
}

function createLatitudeLine(latDeg, color='#1e66ff', dashed=true){
  const lat = latDeg*DEG2RAD;
  const y = R*Math.sin(lat);
  const r = R*Math.cos(lat);
  const pts = [];
  const segments = 256;
  for(let i=0;i<=segments;i++){
    const a = (i/segments)*Math.PI*2;
    const x = r*Math.sin(a);
    const z = r*Math.cos(a);
    pts.push(new THREE.Vector3(x,y,z));
  }
  const geom = new THREE.BufferGeometry().setFromPoints(pts);
  let mat;
  if(dashed){
    mat = new THREE.LineDashedMaterial({ color, dashSize:0.12, gapSize:0.06, linewidth:1 });
  }else{
    mat = new THREE.LineBasicMaterial({ color });
  }
  const line = new THREE.Line(geom, mat);
  if(dashed) line.computeLineDistances();
  return line;
}

function initThree(){
  scene = new THREE.Scene();
  scene.background = new THREE.Color(0xf6f6f6);

  const container = document.getElementById('globe-container');

  const aspect = container.clientWidth / container.clientHeight;
  camera = new THREE.PerspectiveCamera(35, aspect, 0.1, 100);
  camera.position.set(2.6, 1.6, 2.6);

  renderer = new THREE.WebGLRenderer({ antialias:true });
  renderer.setPixelRatio(window.devicePixelRatio);
  renderer.setSize(container.clientWidth, container.clientHeight);
  container.appendChild(renderer.domElement);

  controls = new THREE.OrbitControls(camera, renderer.domElement);
  controls.enableDamping = true;
  controls.target.set(0,0,0);

  // Lights
  dirLight = new THREE.DirectionalLight(0xffffff, 1.1);
  dirLight.position.set(0,0,-10); // temporary; updated in update()
  dirLight.target.position.set(0,0,0);
  scene.add(dirLight);
  scene.add(dirLight.target);

  ambLight = new THREE.AmbientLight(0x404040, 0.55);
  scene.add(ambLight);

  // Earth group
  earthGroup = new THREE.Group();
  scene.add(earthGroup);

  // Earth mesh with procedural texture
  const tex = makeProceduralEarthTexture();
  tex.anisotropy = 4;
  const geo = new THREE.SphereGeometry(R, 64, 32);
  const mat = new THREE.MeshPhongMaterial({ map: tex, shininess: 5 });
  earthMesh = new THREE.Mesh(geo, mat);
  earthGroup.add(earthMesh);

  // Wireframe grid overlay
  const wf = new THREE.WireframeGeometry(geo);
  wire = new THREE.LineSegments(wf, new THREE.LineBasicMaterial({ color:0xffffff, opacity:0.18, transparent:true }));
  earthGroup.add(wire);

  // Equator line (green dashed for visibility)
  equatorLine = createLatitudeLine(0, '#00aa5a', true);
  earthGroup.add(equatorLine);

  // Selected latitude line (blue dashed) placeholder, will be replaced in update()
  selectedLatLine = createLatitudeLine(51.4789, '#1e66ff', true);
  earthGroup.add(selectedLatLine);

  // Axis arrow (polar axis)
  axisArrow = new THREE.ArrowHelper(new THREE.Vector3(0,1,0), new THREE.Vector3(0,0,0), 1.5, 0x000000, 0.12, 0.08);
  scene.add(axisArrow);

  // Arrows for surface normal and sun direction
  normalArrow = new THREE.ArrowHelper(new THREE.Vector3(0,1,0), new THREE.Vector3(0,0,0), 0.75, 0x000000, 0.1, 0.07);
  scene.add(normalArrow);
  sunArrow = new THREE.ArrowHelper(new THREE.Vector3(1,0,0), new THREE.Vector3(0,0,0), 0.9, 0xdd2222, 0.1, 0.07);
  scene.add(sunArrow);

  // Anchor object at surface lat/lon that rotates with the globe
  anchor = new THREE.Object3D();
  earthGroup.add(anchor);

  // Handle resize
  window.addEventListener('resize', onWindowResize);
  function onWindowResize(){
    const w = container.clientWidth, h = container.clientHeight;
    camera.aspect = w/h;
    camera.updateProjectionMatrix();
    renderer.setSize(w,h);
    Plotly.Plots.resize('plot-instant');
    Plotly.Plots.resize('plot-cumulative');
  }

  // animate loop
  function animate(){
    controls.update();
    renderer.render(scene, camera);
    requestAnimationFrame(animate);
  }
  animate();
}

/* ---------- Plotly setup ---------- */
const hours = Array.from({length:25}, (_,i)=>i); // 0..24 inclusive

function initPlots(){
  const layoutInstant = {
    title: { text:'energy', font:{ size:14 } },
    margin: {l:50,r:10,t:24,b:40},
    xaxis: { title:'time (hr)', range:[0,24], dtick:4, mirror:true, ticks:'outside', showline:true },
    yaxis: { title:'watts/m^2', range:[0,1400], dtick:200, mirror:true, ticks:'outside', showline:true },
    showlegend:false,
    height: document.getElementById('plot-instant').clientHeight
  };
  const layoutCum = {
    title: { text:'energy', font:{ size:14 } },
    margin: {l:50,r:10,t:24,b:40},
    xaxis: { title:'time (hr)', range:[0,24], dtick:4, mirror:true, ticks:'outside', showline:true },
    yaxis: { title:'watts/m^2', range:[0,14000], dtick:2000, mirror:true, ticks:'outside', showline:true },
    showlegend:false,
    height: document.getElementById('plot-cumulative').clientHeight
  };

  // empty traces to begin with
  const dataInstant = [
    { x: hours, y: hours.map(()=>0), mode:'lines', line:{color:'#e22', width:3}, name:'equator' },
    { x: hours, y: hours.map(()=>0), mode:'lines', line:{color:'#000', width:3}, name:'latitude' },
    { x: hours, y: hours.map(()=>0), mode:'lines', line:{color:'#1e66ff', width:3}, name:'north pole' },
    { x: [12], y:[0], mode:'markers', marker:{color:'#000', size:8}, name:'current' }
  ];
  const dataCum = [
    { x: hours, y: hours.map(()=>0), mode:'lines', line:{color:'#e22', width:3}, name:'equator' },
    { x: hours, y: hours.map(()=>0), mode:'lines', line:{color:'#000', width:3}, name:'latitude' },
    { x: hours, y: hours.map(()=>0), mode:'lines', line:{color:'#1e66ff', width:3}, name:'north pole' }
  ];

  Plotly.newPlot('plot-instant', dataInstant, layoutInstant, {displayModeBar:false});
  Plotly.newPlot('plot-cumulative', dataCum, layoutCum, {displayModeBar:false});
}

/* ---------- Main update ---------- */
function update(){
  // read sliders
  const season = parseFloat(document.getElementById('slider-season').value);
  const lat = parseFloat(document.getElementById('slider-latitude').value);
  const time = parseFloat(document.getElementById('slider-time').value);

  // update readouts
  document.getElementById('value-season').textContent = season.toFixed(3);
  document.getElementById('value-latitude').textContent = lat.toFixed(4);
  document.getElementById('value-time').textContent = time.toFixed(2);

  // physics: solar declination
  const decl = 23.5 * Math.cos(2*Math.PI*season);
  const declRad = decl*DEG2RAD;

  // 3D globe updates
  // rotation around Y by time of day
  earthGroup.rotation.y = (time/24)*2*Math.PI;

  // directional light: direction vector (0, sinδ, -cosδ)
  const sunDir = new THREE.Vector3(0, Math.sin(declRad), -Math.cos(declRad)); // from Sun to Earth
  // Set light position opposite to direction so that direction points toward origin
  dirLight.position.copy(sunDir.clone().multiplyScalar(-10));
  dirLight.target.position.set(0,0,0);
  dirLight.target.updateMatrixWorld();

  // Selected latitude line
  earthGroup.remove(selectedLatLine);
  selectedLatLine.geometry.dispose();
  selectedLatLine.material.dispose();
  selectedLatLine = createLatitudeLine(lat, '#1e66ff', true);
  earthGroup.add(selectedLatLine);

  // Anchor position on surface at longitude 0
  const latRad = lat*DEG2RAD;
  const posLocal = new THREE.Vector3(0, Math.sin(latRad)*R, Math.cos(latRad)*R);
  anchor.position.copy(posLocal);

  // World position of the anchor (after Earth's rotation)
  const posWorld = anchor.getWorldPosition(new THREE.Vector3());
  const worldNormal = posWorld.clone().normalize();

  // Update surface normal arrow (black)
  normalArrow.position.copy(posWorld.clone().add(worldNormal.clone().multiplyScalar(0.02)));
  normalArrow.setDirection(worldNormal);
  normalArrow.setLength(0.75, 0.10, 0.07);

  // Update sun vector arrow (red), parallel to directional light direction
  const sunDirNorm = sunDir.clone().normalize();
  sunArrow.position.copy(posWorld.clone().add(worldNormal.clone().multiplyScalar(0.02)));
  sunArrow.setDirection(sunDirNorm);
  sunArrow.setLength(0.9, 0.1, 0.07);

  // Plots update: energies for equator, selected latitude, north pole
  const lats = [0, lat, 90];
  const colors = ['#e22', '#000', '#1e66ff'];
  const instantYs = [];
  const cumulativeYs = [];

  for(let j=0;j<lats.length;j++){
    const arr = hours.map(h => energyAt(lats[j], decl, h));
    instantYs.push(arr);
    cumulativeYs.push(cumulativeArray(arr, 1)); // dt = 1 hour between samples
  }

  // Marker at current time on "latitude" curve (index 1)
  const yMarker = energyAt(lat, decl, time);

  const dataInstant = [
    { x: hours, y: instantYs[0], mode:'lines', line:{color:colors[0], width:3}, name:'equator' },
    { x: hours, y: instantYs[1], mode:'lines', line:{color:colors[1], width:3}, name:'latitude' },
    { x: hours, y: instantYs[2], mode:'lines', line:{color:colors[2], width:3}, name:'north pole' },
    { x: [time], y:[yMarker], mode:'markers', marker:{color:'#000', size:8}, name:'current' }
  ];
  const dataCum = [
    { x: hours, y: cumulativeYs[0], mode:'lines', line:{color:colors[0], width:3}, name:'equator' },
    { x: hours, y: cumulativeYs[1], mode:'lines', line:{color:colors[1], width:3}, name:'latitude' },
    { x: hours, y: cumulativeYs[2], mode:'lines', line:{color:colors[2], width:3}, name:'north pole' }
  ];

  Plotly.react('plot-instant', dataInstant, {
    title: { text:'energy', font:{ size:14 } },
    margin: {l:50,r:10,t:24,b:40},
    xaxis: { title:'time (hr)', range:[0,24], dtick:4, mirror:true, ticks:'outside', showline:true },
    yaxis: { title:'watts/m^2', range:[0,1400], dtick:200, mirror:true, ticks:'outside', showline:true },
    showlegend:false
  }, {displayModeBar:false});

  Plotly.react('plot-cumulative', dataCum, {
    title: { text:'energy', font:{ size:14 } },
    margin: {l:50,r:10,t:24,b:40},
    xaxis: { title:'time (hr)', range:[0,24], dtick:4, mirror:true, ticks:'outside', showline:true },
    yaxis: { title:'watts/m^2', range:[0,14000], dtick:2000, mirror:true, ticks:'outside', showline:true },
    showlegend:false
  }, {displayModeBar:false});
}

/* ---------- Initialization ---------- */
function init(){
  initThree();
  initPlots();

  // slider callbacks -> update()
  document.getElementById('slider-season').addEventListener('input', update);
  document.getElementById('slider-latitude').addEventListener('input', update);
  document.getElementById('slider-time').addEventListener('input', update);

  // initial render
  update();
}
window.addEventListener('DOMContentLoaded', init);
</script>
</body>
</html>
