    display: flex;
    align-items: center;
    gap: var(--panel-gap);
  }
  #control-panel span {
    font-size: 14px;
    opacity: 0.8;
  }
  #slider-area {
    flex: 1 1 auto;
  }
  #input-area {
    width: 80px;
  }
  #canvas-container {
    width: 100%;
    border: 1px solid #ccc;
    background: #fff;
    border-radius: 4px;
    box-sizing: border-box;
    overflow: hidden;
  }
</style>
</head>
<body>
  <div id="demo-container">
    <div id="control-panel">
      <span>area</span>
      <input id="slider-area" type="range" min="1" max="101" step="1" value="13">
      <input id="input-area" type="number" min="1" max="101" step="1" value="13">
    </div>
    <div id="canvas-container"></div>
  </div>

  <!-- p5.js -->
  <script src="https://cdnjs.cloudflare.com/ajax/libs/p5.js/1.4.0/p5.js"></script>
  <script>
    // Pre-computed rational-side data for selected congruent numbers up to 101.
    // Each entry gives a, b (legs) and c (hypotenuse) as rational numbers {n, d}.
    // Dataset includes the examples highlighted in the plan/snapshots.
    const congruentData = {
      5:  { a: {n: 3, d: 2},      b: {n: 20, d: 3},        c: {n: 41, d: 6} },
      6:  { a: {n: 3, d: 1},      b: {n: 4, d: 1},         c: {n: 5, d: 1} },
      7:  { a: {n: 35, d: 12},    b: {n: 24, d: 5},        c: {n: 337, d: 60} },
      13: { a: {n: 780, d: 323},  b: {n: 323, d: 30},      c: {n: 106921, d: 9690} },
      79: { a: {n: 233126551, d: 167973000},
            b: {n: 335946000, d: 2950969},
            c: {n: 56434050774922081, d: 495683115837000} }
      // Note: Additional congruent numbers can be added in this object
      // using the same structure: { a:{n,d}, b:{n,d}, c:{n,d} }.
    };

    // DOM elements
    const slider = document.getElementById('slider-area');
    const numberInput = document.getElementById('input-area');

    // Synchronize slider and number input
    function clamp(val, min, max) { return Math.max(min, Math.min(max, val)); }

    function setArea(val) {
      const v = clamp(parseInt(val || 0, 10), parseInt(slider.min, 10), parseInt(slider.max, 10));
      slider.value = v;
      numberInput.value = v;
      if (typeof redraw === 'function') { redraw(); }
    }

    slider.addEventListener('input', e => setArea(e.target.value));
    numberInput.addEventListener('input', e => setArea(e.target.value));

    // p5.js sketch
    let cnv;
    function setup() {
      const container = document.getElementById('canvas-container');
      const desiredHeight = 460; // generous height for labels
      cnv = createCanvas(container.clientWidth, desiredHeight);
      cnv.parent('canvas-container');
      textFont('Helvetica');
      textSize(16);
      noLoop();
    }

    function windowResized() {
      const container = document.getElementById('canvas-container');
      resizeCanvas(container.clientWidth, height);
      redraw();
    }

    function draw() {
      const n = parseInt(slider.value, 10);
      background(255);
      if (congruentData.hasOwnProperty(n)) {
        drawTriangle(congruentData[n]);
      } else {
        drawNotCongruentMessage(n);
      }
    }

    // Helpers for formatting, conversions, and drawing
    function valueOfFraction(fr) {
      return fr.n / fr.d;
    }

    function formatWithSpaces(num) {
      // Insert spaces as thousand separators
      return num.toString().replace(/\B(?=(\d{3})+(?!\d))/g, " ");
    }

    // Draw a fraction at x,y with rotation (radians) and font size.
    function drawFraction(fr, x, y, rotation = 0, fontSize = 18) {
      push();
      translate(x, y);
      rotate(rotation);
      textAlign(CENTER, CENTER);
      fill('#8B5A2B');
      noStroke();
      textSize(fontSize);

      const numStr = formatWithSpaces(fr.n);
      const denStr = formatWithSpaces(fr.d);
      const w = Math.max(textWidth(numStr), textWidth(denStr)) + 4; // tiny padding

      // positions relative to center (0,0)
      const gap = fontSize * 0.25;
      const numY = - (fontSize * 0.8 + gap);
      const denY = (fontSize * 0.8 + gap);

      // draw texts
      text(numStr, 0, numY);
      text(denStr, 0, denY);

      // draw fraction bar
      stroke('#8B5A2B');
      strokeWeight(1.5);
      line(-w/2, 0, w/2, 0);

      pop();
    }

    function drawTriangle(data) {
      // Convert to decimal lengths
      const aVal = valueOfFraction(data.a);
      const bVal = valueOfFraction(data.b);

      // Padding for labels around the shape
      const padding = 160;
      const scale = Math.min((width - padding) / aVal, (height - padding) / bVal);
      const aScaled = aVal * scale;
      const bScaled = bVal * scale;

      // Place the right-angle at bottom-left of the bounding box,
      // but center the overall bounding box on the canvas.
      const x0 = (width - aScaled) / 2;
      const y0 = (height + bScaled) / 2; // bottom y of base

      const p0 = { x: x0, y: y0 };               // right angle corner
      const p1 = { x: x0 + aScaled, y: y0 };     // along base (horizontal)
      const p2 = { x: x0, y: y0 - bScaled };     // along height (vertical)

      // Triangle styling and drawing
      stroke('#000000');
      strokeWeight(2);
      fill('#FFF2E5');

      beginShape();
      vertex(p0.x, p0.y);
      vertex(p1.x, p1.y);
      vertex(p2.x, p2.y);
      endShape(CLOSE);

      // Labels:
      // a (base) - centered below base
      drawFraction(data.a, (p0.x + p1.x) / 2, p0.y + 28, 0, 18);

      // b (height) - right of vertical leg
      drawFraction(data.b, p0.x + aScaled + 28, (p0.y + p2.y) / 2, 0, 18);

      // c (hypotenuse) - near midpoint of hypotenuse, rotated parallel to the edge
      const midHx = (p1.x + p2.x) / 2;
      const midHy = (p1.y + p2.y) / 2;
      const dx = p2.x - p1.x;
      const dy = p2.y - p1.y;
      const angle = Math.atan2(dy, dx);

      // Offset outward from the triangle along the left-normal of the hypotenuse
      const len = Math.hypot(dx, dy) || 1;
      const nx = -dy / len; // left normal x
      const ny = dx / len;  // left normal y
      const offset = 24;
      const labelX = midHx + nx * offset;
      const labelY = midHy + ny * offset;

      drawFraction(data.c, labelX, labelY, angle, 18);
    }

    function drawNotCongruentMessage(n) {
      push();
      fill('#FF0000');
      noStroke();
      textAlign(CENTER, CENTER);
      textSize(20);
      text(n + " is not a congruent number", width / 2, height / 2);
      pop();
    }

    // Initialize drawing with default area = 13
    window.addEventListener('load', () => setArea(13));
  </script>
</body>
</html>
