<title>Primal/Dual Production-Cost Visualization</title>

<!-- three.js and helpers -->
<script src="https://cdn.jsdelivr.net/npm/three@0.128/build/three.min.js"></script>
<script src="https://cdn.jsdelivr.net/npm/three@0.128/examples/js/controls/OrbitControls.js"></script>
<script src="https://cdn.jsdelivr.net/npm/three@0.128/examples/js/geometries/ParametricGeometry.js"></script>

<style>
    :root{
        --panel-bg: #f5f5f7;
        --panel-border: #ddd;
        --accent: #e68a00;
        --text: #222;
    }
    body{
        margin:0;
        font-family: -apple-system, BlinkMacSystemFont, "Segoe UI", Roboto, Oxygen, Ubuntu, Cantarell, "Helvetica Neue", Arial, "Noto Sans", sans-serif;
        color:var(--text);
        background:#fff;
    }
    #main-container{
        max-width: 1100px;
        margin: 12px auto 40px auto;
        padding: 8px;
    }

    /* 1. Parameters Panel */
    #parameters-panel{
        background:var(--panel-bg);
        border:1px solid var(--panel-border);
        border-radius:8px;
        padding:8px 10px 10px 10px;
    }
    #parameters-panel .panel-header{
        display:flex;
        align-items:center;
        justify-content:space-between;
        margin-bottom:8px;
        font-size:14px;
        text-transform: lowercase;
    }
    #btn-reset{
        width:26px;
        height:26px;
        border-radius:50%;
        border:1px solid #bbb;
        background:#fff;
        cursor:pointer;
        font-weight:bold;
        line-height: 22px;
    }
    .controls-grid{
        display:grid;
        grid-template-columns: repeat(2, minmax(250px, 1fr));
        grid-gap: 8px 18px;
    }
    .control-item{
        display:flex;
        align-items:center;
        gap:8px;
        white-space:nowrap;
    }
    .control-item label{
        width:16px;
        text-align:right;
        color:#555;
    }
    .control-item input[type="range"]{
        flex:1;
    }
    .control-item span{
        width:46px;
        text-align:left;
        color:#444;
        font-variant-numeric: tabular-nums;
    }

    /* 2. Problem Control Panel */
    #problem-controls-panel{
        margin-top:10px;
        background:var(--panel-bg);
        border:1px solid var(--panel-border);
        border-radius:8px;
        padding:10px;
        display:grid;
        grid-template-columns: 1fr 1fr;
        grid-gap:14px;
    }
    #problem-controls-panel p{
        margin:0 0 6px 0;
        color:#444;
        font-size: 14px;
    }
    #problem-controls-panel .problem-column .control-item label{
        width:16px;
    }
    #problem-controls-panel button{
        margin-top:6px;
        padding:6px 10px;
        background:#fff;
        border:1px solid #bbb;
        border-radius:6px;
        cursor:pointer;
    }

    /* 3. Visualization Area */
    #visualization-area{
        margin-top:10px;
        border:1px solid var(--panel-border);
        border-radius:8px;
        padding:8px;
        display:flex;
        gap:10px;
        background:#fafafa;
    }
    .plot-container{
        flex:1;
        min-height:280px;
        height:340px;
        position:relative;
        background:#ffffff;
        border:1px solid #eee;
        border-radius:6px;
        overflow:hidden;
    }

    /* 4. Output Display */
    #output-area{
        margin-top:10px;
        display:flex;
        gap:10px;
        justify-content:space-between;
    }
    .output-box{
        flex:1;
        border:1px solid var(--panel-border);
        border-radius:8px;
        background:#fff;
        padding:10px;
        text-align:center;
        font-variant-numeric: tabular-nums;
        box-shadow: inset 0 0 0 1px #f2f2f2;
    }

    /* minor tweaks */
    @media (max-width: 850px){
        .controls-grid{ grid-template-columns: 1fr; }
        #problem-controls-panel{ grid-template-columns: 1fr; }
        #visualization-area{ flex-direction: column; }
    }
</style>
</head>
<body>
<!-- Main Container -->
<div id="main-container">

    <!-- 1. Parameters Panel -->
    <div id="parameters-panel">
        <div class="panel-header">
            <span>parameters</span>
            <button id="btn-reset" title="Reset to default values">+</button>
        </div>
        <div class="controls-grid">
            <div class="control-item">
                <label for="slider-alpha">α</label>
                <input type="range" id="slider-alpha" min="0.1" max="1.0" step="0.01" value="0.5">
                <span id="label-alpha">0.5</span>
            </div>
            <div class="control-item">
                <label for="slider-beta">β</label>
                <input type="range" id="slider-beta" min="0.1" max="1.0" step="0.01" value="0.5">
                <span id="label-beta">0.5</span>
            </div>
            <div class="control-item">
                <label for="slider-w">w</label>
                <input type="range" id="slider-w" min="0.1" max="2.0" step="0.01" value="1.0">
                <span id="label-w">1</span>
            </div>
            <div class="control-item">
                <label for="slider-r">r</label>
                <input type="range" id="slider-r" min="0.1" max="2.0" step="0.01" value="1.0">
                <span id="label-r">1</span>
            </div>
        </div>
    </div>

    <!-- 2. Problem Control Panel -->
    <div id="problem-controls-panel">
        <div class="problem-column">
            <p>primal problem: minimize costs C given volume Q</p>
            <div class="control-item">
                <label for="slider-Q">Q</label>
                <input type="range" id="slider-Q" min="1" max="10" step="0.1" value="3.0">
                <span id="label-Q">3</span>
            </div>
            <button id="btn-Q-from-dual">Q from dual</button>
        </div>
        <div class="problem-column">
            <p>dual problem: maximize volume Q given costs C</p>
            <div class="control-item">
                <label for="slider-C">C</label>
                <input type="range" id="slider-C" min="1" max="10" step="0.1" value="4.0">
                <span id="label-C">4</span>
            </div>
            <button id="btn-C-from-primal">C from primal</button>
        </div>
    </div>

    <!-- 3. Visualization Area -->
    <div id="visualization-area">
        <div id="plot-primal" class="plot-container"></div>
        <div id="plot-dual" class="plot-container"></div>
    </div>

    <!-- 4. Output Display -->
    <div id="output-area">
        <div id="output-primal" class="output-box">x = 3.00, y = 3.00, C = 6.00</div>
        <div id="output-dual" class="output-box">x = 2.00, y = 2.00, Q = 2.00</div>
    </div>

</div>

<script>
(() => {
    // Constants for the visualization box
    const XMAX = 10, YMAX = 10, ZMAX = 10;

    // Utility helpers
    const clamp = (v, a, b) => Math.min(Math.max(v, a), b);
    const fmt = (v) => (Math.abs(v) >= 10000 ? v.toFixed(0) : v.toFixed(2));

    // DOM Elements
    const els = {
        alpha: document.getElementById('slider-alpha'),
        beta:  document.getElementById('slider-beta'),
        w:     document.getElementById('slider-w'),
        r:     document.getElementById('slider-r'),
        Q:     document.getElementById('slider-Q'),
        C:     document.getElementById('slider-C'),
        lab_alpha: document.getElementById('label-alpha'),
        lab_beta:  document.getElementById('label-beta'),
        lab_w:     document.getElementById('label-w'),
        lab_r:     document.getElementById('label-r'),
        lab_Q:     document.getElementById('label-Q'),
        lab_C:     document.getElementById('label-C'),
        out_primal: document.getElementById('output-primal'),
        out_dual: document.getElementById('output-dual'),
        btn_reset: document.getElementById('btn-reset'),
        btnQFromDual: document.getElementById('btn-Q-from-dual'),
        btnCFromPrimal: document.getElementById('btn-C-from-primal')
    };

    const defaults = {
        alpha: 0.5, beta: 0.5, w: 1.0, r: 1.0, Q: 3.0, C: 4.0
    };

    // THREE scenes setup
    function init3D(containerId){
        const container = document.getElementById(containerId);
        const scene = new THREE.Scene();
        scene.background = new THREE.Color(0xffffff);

        const camera = new THREE.PerspectiveCamera(40, container.clientWidth / container.clientHeight, 0.1, 1000);
        camera.position.set(16, 14, 16);

        const renderer = new THREE.WebGLRenderer({antialias:true});
        renderer.setPixelRatio(window.devicePixelRatio || 1);
        renderer.setSize(container.clientWidth, container.clientHeight);
        container.appendChild(renderer.domElement);

        const controls = new THREE.OrbitControls(camera, renderer.domElement);
        controls.target.set(5,5,5);
        controls.update();

        // Lighting
        const ambient = new THREE.AmbientLight(0xffffff, 0.9);
        scene.add(ambient);

        // Bounding box 10x10x10 from (0,0,0) to (10,10,10)
        const boxMesh = new THREE.Mesh(new THREE.BoxGeometry(10,10,10), new THREE.MeshBasicMaterial({color:0x000000, transparent:true, opacity:0}));
        boxMesh.position.set(5,5,5);
        scene.add(boxMesh);
        const boxHelper = new THREE.BoxHelper(boxMesh, 0x999999);
        scene.add(boxHelper);

        // Axes
        const axesGroup = new THREE.Group();
        const makeAxis = (from, to, color) => {
            const geom = new THREE.BufferGeometry().setFromPoints([new THREE.Vector3(...from), new THREE.Vector3(...to)]);
            const mat = new THREE.LineBasicMaterial({color:color, linewidth:2});
            return new THREE.Line(geom, mat);
        };
        // x (right) - red
        axesGroup.add(makeAxis([0,0,0], [XMAX,0,0], 0xcc3333));
        // y (depth) - green
        axesGroup.add(makeAxis([0,0,0], [0,YMAX,0], 0x22aa44));
        // vertical (z) - blue
        axesGroup.add(makeAxis([0,0,0], [0,0,ZMAX], 0x3366cc));
        scene.add(axesGroup);

        // A group for dynamic objects
        const dynamicGroup = new THREE.Group();
        scene.add(dynamicGroup);

        function resize(){
            const w = container.clientWidth;
            const h = container.clientHeight;
            renderer.setSize(w, h);
            camera.aspect = w/h;
            camera.updateProjectionMatrix();
        }

        window.addEventListener('resize', resize);

        function render(){
            renderer.render(scene, camera);
        }

        // Animation loop
        function animate(){
            requestAnimationFrame(animate);
            controls.update();
            render();
        }
        animate();

        return {container, scene, camera, renderer, controls, dynamicGroup, resize, render};
    }

    // Create both plots
    const plotPrimal = init3D('plot-primal');
    const plotDual   = init3D('plot-dual');

    // Geometry builders
    function buildIsoquantCurtain(Q, alpha, beta){
        // vertical wall along isoquant y(x) = (Q / x^alpha)^(1/beta)
        const N = 120;
        const positions = [];
        const indices = [];
        for(let i=0;i<=N;i++){
            const x = 0.1 + (XMAX-0.1)*i/N;
            const yRaw = Math.pow(Q / Math.pow(Math.max(x,1e-6), alpha), 1/Math.max(beta,1e-6));
            const y = clamp(yRaw, 0, YMAX);
            // bottom (z=0) and top (z=ZMAX)
            positions.push(x, y, 0);
            positions.push(x, y, ZMAX);
        }
        for(let i=0;i<N;i++){
            const b0 = 2*i, t0 = 2*i+1, b1 = 2*(i+1), t1 = 2*(i+1)+1;
            // two triangles
            indices.push(b0, t0, t1);
            indices.push(b0, t1, b1);
        }
        const geom = new THREE.BufferGeometry();
        geom.setIndex(indices);
        geom.setAttribute('position', new THREE.Float32BufferAttribute(positions, 3));
        geom.computeVertexNormals();
        const mat = new THREE.MeshPhongMaterial({color:0xd97b1e, transparent:false, opacity:0.95, side:THREE.DoubleSide});
        return new THREE.Mesh(geom, mat);
    }

    function buildPlaneCost(w, r){
        // plane z = w*x + r*y over [0, XMAX] x [0, YMAX]
        const NX=60, NY=60;
        const positions = [];
        const indices = [];
        for(let iy=0; iy<=NY; iy++){
            const y = YMAX*iy/NY;
            for(let ix=0; ix<=NX; ix++){
                const x = XMAX*ix/NX;
                const z = w*x + r*y;
                positions.push(x, y, z);
            }
        }
        for(let iy=0; iy<NY; iy++){
            for(let ix=0; ix<NX; ix++){
                const a = iy*(NX+1) + ix;
                const b = a + 1;
                const c = a + (NX+1);
                const d = c + 1;
                indices.push(a, c, b);
                indices.push(b, c, d);
            }
        }
        const geom = new THREE.BufferGeometry();
        geom.setIndex(indices);
        geom.setAttribute('position', new THREE.Float32BufferAttribute(positions, 3));
        geom.computeVertexNormals();
        const mat = new THREE.MeshPhongMaterial({color:0x999999, transparent:true, opacity:0.6, side:THREE.DoubleSide});
        return new THREE.Mesh(geom, mat);
    }

    function buildProductionSurface(alpha, beta){
        const slices = 80, stacks = 80;
        const func = (u, v, target) => {
            const x = u * XMAX;
            const y = v * YMAX;
            let z = Math.pow(Math.max(x,0), alpha) * Math.pow(Math.max(y,0), beta);
            z = clamp(z, 0, ZMAX); // cap height to fit the box for visibility
            target.set(x, y, z);
        };
        const geom = new THREE.ParametricGeometry(func, slices, stacks);
        const mat = new THREE.MeshPhongMaterial({color:0xd97b1e, side:THREE.DoubleSide, flatShading:false});
        return new THREE.Mesh(geom, mat);
    }

    function buildBudgetCurtain(C, w, r){
        // vertical wall base is y = (C - w*x)/r, y>=0
        const N = 120;
        const positions = [];
        const indices = [];
        const xMaxLine = Math.min(XMAX, C/Math.max(w,1e-6));
        for(let i=0;i<=N;i++){
            const x = xMaxLine * i / N;
            let y = (C - w*x) / Math.max(r,1e-6);
            y = clamp(y, 0, YMAX);
            positions.push(x, y, 0);
            positions.push(x, y, ZMAX);
        }
        for(let i=0;i<N;i++){
            const b0 = 2*i, t0 = 2*i+1, b1 = 2*(i+1), t1 = 2*(i+1)+1;
            indices.push(b0, t0, t1);
            indices.push(b0, t1, b1);
        }
        const geom = new THREE.BufferGeometry();
        geom.setIndex(indices);
        geom.setAttribute('position', new THREE.Float32BufferAttribute(positions, 3));
        geom.computeVertexNormals();
        const mat = new THREE.MeshPhongMaterial({color:0x777777, transparent:true, opacity:0.55, side:THREE.DoubleSide});
        return new THREE.Mesh(geom, mat);
    }

    function buildIntersectionCurve(C, w, r, alpha, beta){
        const N=160;
        const points=[];
        const xMaxLine = Math.min(XMAX, C/Math.max(w,1e-6));
        for(let i=0;i<=N;i++){
            const x = xMaxLine * i / N;
            let y = (C - w*x) / Math.max(r,1e-6);
            if (y < 0) continue;
            if (y > YMAX) y = YMAX;
            let z = Math.pow(Math.max(x,0), alpha) * Math.pow(Math.max(y,0), beta);
            z = clamp(z, 0, ZMAX);
            points.push(new THREE.Vector3(x, y, z));
        }
        const geom = new THREE.BufferGeometry().setFromPoints(points);
        const mat = new THREE.LineBasicMaterial({color:0xcc3333, linewidth:3});
        return new THREE.Line(geom, mat);
    }

    function buildSolutionVector(x, y, z, color=0x3366cc){
        const geom = new THREE.BufferGeometry().setFromPoints([
            new THREE.Vector3(0,0,0),
            new THREE.Vector3(x,y,z)
        ]);
        const mat = new THREE.LineBasicMaterial({color, linewidth:4});
        return new THREE.Line(geom, mat);
    }

    function buildSphere(x, y, z, color=0x888888){
        const sphere = new THREE.Mesh(
            new THREE.SphereGeometry(0.18, 24, 16),
            new THREE.MeshStandardMaterial({color, metalness:0.2, roughness:0.5})
        );
        sphere.position.set(x,y,z);
        return sphere;
    }

    // Calculation logic
    function getValues(){
        return {
            alpha: parseFloat(els.alpha.value),
            beta: parseFloat(els.beta.value),
            w: parseFloat(els.w.value),
            r: parseFloat(els.r.value),
            Q: parseFloat(els.Q.value),
            C: parseFloat(els.C.value)
        };
    }

    function solvePrimal(alpha, beta, w, r, Q_in){
        const k = (w * beta) / (r * alpha);
        const x_p = Math.pow(Q_in / Math.pow(k, beta), 1.0 / (alpha + beta));
        const y_p = x_p * k;
        const C_out = w * x_p + r * y_p;
        return {x_p, y_p, C_out, k};
    }

    function solveDual(alpha, beta, w, r, C_in){
        const denom = (alpha + beta);
        const x_d = (C_in * alpha) / (w * denom);
        const y_d = (C_in * beta)  / (r * denom);
        const Q_out = Math.pow(Math.max(x_d,0), alpha) * Math.pow(Math.max(y_d,0), beta);
        return {x_d, y_d, Q_out};
    }

    // Update labels
    function refreshLabels(vals){
        els.lab_alpha.textContent = fmt(vals.alpha).replace(/\.00$/,'');
        els.lab_beta.textContent  = fmt(vals.beta).replace(/\.00$/,'');
        els.lab_w.textContent     = fmt(vals.w).replace(/\.00$/,'');
        els.lab_r.textContent     = fmt(vals.r).replace(/\.00$/,'');
        els.lab_Q.textContent     = fmt(vals.Q).replace(/\.00$/,'');
        els.lab_C.textContent     = fmt(vals.C).replace(/\.00$/,'');
    }

    // Main update: compute and redraw
    let lastPrimal = {C_out: 6.0};
    let lastDual   = {Q_out: 2.0};
    function update(){
        const v = getValues();
        refreshLabels(v);

        // solve both problems
        const primal = solvePrimal(v.alpha, v.beta, v.w, v.r, v.Q);
        const dual   = solveDual(v.alpha, v.beta, v.w, v.r, v.C);
        lastPrimal = primal;
        lastDual   = dual;

        // Output text
        els.out_primal.textContent = `x = ${fmt(primal.x_p)}, y = ${fmt(primal.y_p)}, C = ${fmt(primal.C_out)}`;
        els.out_dual.textContent   = `x = ${fmt(dual.x_d)}, y = ${fmt(dual.y_d)}, Q = ${fmt(dual.Q_out)}`;

        // Redraw primal plot
        while(plotPrimal.dynamicGroup.children.length) plotPrimal.dynamicGroup.remove(plotPrimal.dynamicGroup.children[0]);
        plotPrimal.dynamicGroup.add(buildIsoquantCurtain(v.Q, v.alpha, v.beta));
        plotPrimal.dynamicGroup.add(buildPlaneCost(v.w, v.r));
        plotPrimal.dynamicGroup.add(buildSolutionVector(primal.x_p, primal.y_p, primal.C_out, 0x3366cc));

        // Redraw dual plot
        while(plotDual.dynamicGroup.children.length) plotDual.dynamicGroup.remove(plotDual.dynamicGroup.children[0]);
        plotDual.dynamicGroup.add(buildProductionSurface(v.alpha, v.beta));
        plotDual.dynamicGroup.add(buildBudgetCurtain(v.C, v.w, v.r));
        plotDual.dynamicGroup.add(buildIntersectionCurve(v.C, v.w, v.r, v.alpha, v.beta));
        plotDual.dynamicGroup.add(buildSolutionVector(dual.x_d, dual.y_d, dual.Q_out, 0xcc3333));
        plotDual.dynamicGroup.add(buildSphere(dual.x_d, dual.y_d, dual.Q_out));
    }

    // Event bindings
    ['alpha','beta','w','r','Q','C'].forEach(id => {
        els[id].addEventListener('input', update);
    });

    els.btn_reset.addEventListener('click', () => {
        els.alpha.value = defaults.alpha;
        els.beta.value  = defaults.beta;
        els.w.value     = defaults.w;
        els.r.value     = defaults.r;
        els.Q.value     = defaults.Q;
        els.C.value     = defaults.C;
        update();
    });

    els.btnCFromPrimal.addEventListener('click', () => {
        els.C.value = lastPrimal.C_out;
        update();
    });

    els.btnQFromDual.addEventListener('click', () => {
        els.Q.value = lastDual.Q_out;
        update();
    });

    // Initial render
    update();

    // Ensure renderers fit containers after fonts/layout settle
    setTimeout(() => { plotPrimal.resize(); plotDual.resize(); }, 50);
})();
</script>
</body>
</html>
