</div>

<!-- CDN for p5.js -->
<script src="https://cdnjs.cloudflare.com/ajax/libs/p5.js/1.4.0/p5.min.js"></script>
<script>
/*
  Flash Distillation Interactive Demo
  - Uses p5.js to draw a dynamic process flow diagram.
  - Thermodynamics: Antoine K-values, Rachford-Rice, and a simplified energy balance.
*/

const components = [
  { name: 'n-butane',  A: 6.80165, B: 1005.157, C: -27.159, Cp: 132, Hvap: 22400 },
  { name: 'n-pentane', A: 6.86177, B: 1184.249, C: -35.223, Cp: 161, Hvap: 25800 },
  { name: 'n-heptane', A: 6.90237, B: 1358.59,  C: -51.303, Cp: 225, Hvap: 31800 },
  { name: 'n-octane',  A: 6.92383, B: 1431.149, C: -59.501, Cp: 255, Hvap: 34400 }
];

// Feed conditions and constants
const F = 10;                      // kmol/hr
const T_F = 323.2;                 // K
const P_F = 1000;                  // kPa (not used directly in calc)
const z = [0.25, 0.25, 0.25, 0.25];

const pressureSlider = document.getElementById('slider-pressure');
const heatSlider = document.getElementById('slider-heat');
const pressureValue = document.getElementById('pressure-value');
const heatValue = document.getElementById('heat-value');

// Global results used by p5 draw()
let flashResult = {
  T_flash: T_F,
  V_frac: 0,
  x: [0.25, 0.25, 0.25, 0.25],
  y: [0.25, 0.25, 0.25, 0.25],
  P_flash: Number(pressureSlider.value),
  Q_hr: Number(heatSlider.value)
};

// Utility formatters
function fmt(n, d=3) {
  return Number(n).toFixed(d);
}
function fmt1(n) { return Number(n).toFixed(1); }

// Thermodynamic helper functions
function Psat_kPa(T, comp) {
  // Antoine in kPa; T in K
  // P_sat = 10^(A - B/(T + C))
  const val = Math.pow(10, comp.A - comp.B / (T + comp.C));
  return Math.max(val, 1e-6); // avoid zeros
}
function K_values(T, PkPa) {
  return components.map(c => Psat_kPa(T, c) / PkPa);
}
function rachfordRice(V, zArr, KArr) {
  let sum = 0;
  for (let i = 0; i < zArr.length; i++) {
    const Ki = KArr[i];
    const denom = 1 + V * (Ki - 1);
    sum += zArr[i] * (Ki - 1) / denom;
  }
  return sum;
}
function solveV(zArr, KArr) {
  // Determine feasible bounds using classic RR bracketing
  let Vmin = 0.0;
  let Vmax = 1.0;
  let lower = -Infinity;
  let upper = Infinity;

  for (let i = 0; i < KArr.length; i++) {
    const Ki = KArr[i];
    if (Math.abs(Ki - 1) < 1e-14) continue;
    const vStar = -1 / (Ki - 1);
    if (Ki > 1) lower = Math.max(lower, vStar);
    if (Ki < 1) upper = Math.min(upper, vStar);
  }
  if (Number.isFinite(lower)) Vmin = Math.max(Vmin, lower + 1e-10);
  if (Number.isFinite(upper)) Vmax = Math.min(Vmax, upper - 1e-10);

  // Guard: if no proper bracket, choose based on overall volatility
  const sumKminus1 = zArr.reduce((s, zi, i) => s + zi * (KArr[i] - 1), 0);
  let fmin = rachfordRice(Vmin, zArr, KArr);
  let fmax = rachfordRice(Vmax, zArr, KArr);
  if (!isFinite(fmin) || !isFinite(fmax) || isNaN(fmin) || isNaN(fmax) || fmin * fmax > 0) {
    if (sumKminus1 > 0) return Math.min(0.999999, Vmax);
    return Math.max(0.000001, Vmin);
  }

  // Bisection
  for (let iter = 0; iter < 80; iter++) {
    const Vmid = 0.5 * (Vmin + Vmax);
    const fmid = rachfordRice(Vmid, zArr, KArr);
    if (Math.abs(fmid) < 1e-10 || (Vmax - Vmin) < 1e-12) return Vmid;
    if (fmin * fmid > 0) {
      Vmin = Vmid;
      fmin = fmid;
    } else {
      Vmax = Vmid;
      fmax = fmid;
    }
  }
  return 0.5 * (Vmin + Vmax);
}
function computeXY(zArr, KArr, V) {
  const x = [];
  const y = [];
  for (let i = 0; i < zArr.length; i++) {
    const denom = 1 + V * (KArr[i] - 1);
    x[i] = zArr[i] / denom;
    y[i] = KArr[i] * x[i];
  }
  // Normalize to avoid numerical drift
  const sx = x.reduce((a, b) => a + b, 0);
  const sy = y.reduce((a, b) => a + b, 0);
  const xn = x.map(v => v / (sx || 1));
  const yn = y.map(v => v / (sy || 1));
  return { x: xn, y: yn };
}
function energyResidual(T, PkPa, Q_hr) {
  const K = K_values(T, PkPa);
  const V = solveV(z, K);
  const { x, y } = computeXY(z, K, V);
  const Cp_avg = components.reduce((s, c, i) => s + z[i] * c.Cp, 0); // kJ/kmol-K
  const Hvap_mix = components.reduce((s, c, i) => s + y[i] * c.Hvap, 0); // kJ/kmol
  const left = Q_hr / F + Cp_avg * (T_F - T);
  const right = V * Hvap_mix;
  return left - right; // target 0
}
function solveTemperature(PkPa, Q_hr) {
  // Try to bracket the root and then bisection
  let Tlow = 260;
  let Thigh = 420;
  let flow = energyResidual(Tlow, PkPa, Q_hr);
  let fhigh = energyResidual(Thigh, PkPa, Q_hr);

  let expandCount = 0;
  while (flow * fhigh > 0 && expandCount < 30) {
    Tlow -= 5;
    Thigh += 5;
    flow = energyResidual(Tlow, PkPa, Q_hr);
    fhigh = energyResidual(Thigh, PkPa, Q_hr);
    expandCount++;
    if (Tlow < 200 && Thigh > 600) break;
  }

  if (flow * fhigh <= 0) {
    for (let i = 0; i < 70; i++) {
      const Tmid = 0.5 * (Tlow + Thigh);
      const fmid = energyResidual(Tmid, PkPa, Q_hr);
      if (Math.abs(fmid) < 1e-6 || (Thigh - Tlow) < 1e-6) return Tmid;
      if (flow * fmid > 0) {
        Tlow = Tmid; flow = fmid;
      } else {
        Thigh = Tmid; fhigh = fmid;
      }
    }
    return 0.5 * (Tlow + Thigh);
  }

  // Fall back to secant around feed temperature
  let T1 = T_F;
  let T2 = T_F + 8;
  let f1 = energyResidual(T1, PkPa, Q_hr);
  let f2 = energyResidual(T2, PkPa, Q_hr);
  for (let i = 0; i < 40; i++) {
    const denom = (f2 - f1);
    let Tnew = isFinite(denom) && Math.abs(denom) > 1e-12 ? T2 - f2 * (T2 - T1) / denom : (T1 + T2) / 2;
    Tnew = Math.min(600, Math.max(200, Tnew));
    const fnew = energyResidual(Tnew, PkPa, Q_hr);
    if (Math.abs(Tnew - T2) < 1e-6 || Math.abs(fnew) < 1e-6) return Tnew;
    T1 = T2; f1 = f2;
    T2 = Tnew; f2 = fnew;
  }
  return T2;
}

// Main calculation function
function calculateFlash(P_flash, Q_hr) {
  const T = solveTemperature(P_flash, Q_hr);
  const K = K_values(T, P_flash);
  const V = solveV(z, K);
  const xy = computeXY(z, K, V);
  return {
    T_flash: T,
    V_frac: Math.max(0, Math.min(1, V)),
    x: xy.x,
    y: xy.y,
    P_flash,
    Q_hr
  };
}

// Update and event bindings
function updateFromControls() {
  const P = Number(pressureSlider.value);
  const Q = Number(heatSlider.value);
  pressureValue.textContent = `${P.toFixed(0)} kPa`;
  heatValue.textContent = `${Q.toFixed(0)} kJ/hr`;
  flashResult = calculateFlash(P, Q);
