// Bind slider interactions
pressureSlider.addEventListener('input', updateFromControls);
heatSlider.addEventListener('input', updateFromControls);

// p5 rendering
let canvas;
function setup() {
  canvas = createCanvas(800, 500);
  canvas.parent('visualization-area');
