  textSize(14);
  textAlign(LEFT, CENTER);
  // Initial computation
  updateFromControls();
}
function drawArrow(x1, y1, x2, y2, col, w = 4, head = 8) {
  push();
  stroke(col);
  strokeWeight(w);
  line(x1, y1, x2, y2);
  const ang = Math.atan2(y2 - y1, x2 - x1);
  // Arrowhead
  line(x2, y2, x2 - head * Math.cos(ang - Math.PI / 6), y2 - head * Math.sin(ang - Math.PI / 6));
  line(x2, y2, x2 - head * Math.cos(ang + Math.PI / 6), y2 - head * Math.sin(ang + Math.PI / 6));
  pop();
}
function drawDashedArrow(x1, y1, x2, y2, col, seg=8, gap=6, w=3, head=8) {
  push();
  stroke(col);
  strokeWeight(w);
  const dx = x2 - x1, dy = y2 - y1;
  const len = Math.hypot(dx, dy);
  const ang = Math.atan2(dy, dx);
  let d = 0;
  while (d < len - head) {
    const xStart = x1 + (d / len) * dx;
    const yStart = y1 + (d / len) * dy;
    d += seg;
    const dClamped = Math.min(d, len - head - 0.1);
    const xEnd = x1 + (dClamped / len) * dx;
    const yEnd = y1 + (dClamped / len) * dy;
    line(xStart, yStart, xEnd, yEnd);
    d += gap;
  }
  // Arrowhead
  line(x2, y2, x2 - head * Math.cos(ang - Math.PI / 6), y2 - head * Math.sin(ang - Math.PI / 6));
  line(x2, y2, x2 - head * Math.cos(ang + Math.PI / 6), y2 - head * Math.sin(ang + Math.PI / 6));
  pop();
}
function draw() {
  background('#F0F0F0');

  // Coordinates (as specified)
  const flashX = 350, flashY = 200, flashW = 100, flashH = 100;
  const feedColor = '#9ACD32';
  const vaporColor = '#9370DB';
  const liquidColor = '#FFA500';
  const heatColor = '#FF0000';

  // Flash Vessel
  push();
  fill('#E6E6FA');
  stroke(0);
  strokeWeight(2);
  rect(flashX, flashY, flashW, flashH, 6);
  fill('#555');
  noStroke();
  textAlign(CENTER, CENTER);
  textSize(20);
  text('Flash', flashX + flashW / 2, flashY + flashH / 2);
  pop();

  // Feed Stream Arrow (left to vessel)
  drawArrow(100, 250, 350, 250, feedColor, 4, 10);

  // Vapor Stream Arrow (up then right)
  push();
  stroke(vaporColor);
  strokeWeight(4);
  line(400, 200, 400, 100);
  drawArrow(400, 100, 550, 100, vaporColor, 4, 10);
  pop();

  // Liquid Stream Arrow (down then right)
  push();
  stroke(liquidColor);
  strokeWeight(4);
  line(400, 300, 400, 400);
  drawArrow(400, 400, 550, 400, liquidColor, 4, 10);
  pop();

  // Heat Input (dashed red arrow)
  drawDashedArrow(290, 210, 350, 230, heatColor, 8, 6, 3, 9);

  // Text labels
  textAlign(LEFT, TOP);
  textSize(14);

  // Feed Info
  push();
  fill(feedColor);
  text('feed', 110, 270);
  fill('#666');
  text(`temperature = ${fmt1(T_F)} K`, 110, 290);
  text(`pressure = ${P_F}`, 110, 310);
  text(`n-butane = ${fmt(z[0], 3)}`, 110, 330);
  text(`n-pentane = ${fmt(z[1], 3)}`, 110, 350);
  text(`n-heptane = ${fmt(z[2], 3)}`, 110, 370);
  text(`n-octane = ${fmt(z[3], 3)}`, 110, 390);
  pop();

  // Vapor Info (top right)
  push();
  fill(vaporColor);
  text('vapor mole fraction:', 560, 70);
  fill('#333');
  const y = flashResult.y || [0,0,0,0];
  text(`n-butane = ${fmt(y[0], 3)}`, 560, 90);
  text(`n-pentane = ${fmt(y[1], 3)}`, 560, 110);
  text(`n-heptane = ${fmt(y[2], 3)}`, 560, 130);
  text(`n-octane = ${fmt(y[3], 3)}`, 560, 150);
  pop();

  // Liquid Info (bottom right)
  push();
  fill(liquidColor);
  text('liquid mole fraction:', 560, 350);
  fill('#333');
  const x = flashResult.x || [0,0,0,0];
  text(`n-butane = ${fmt(x[0], 3)}`, 560, 370);
  text(`n-pentane = ${fmt(x[1], 3)}`, 560, 390);
  text(`n-heptane = ${fmt(x[2], 3)}`, 560, 410);
  text(`n-octane = ${fmt(x[3], 3)}`, 560, 430);
  pop();

  // Process Info (middle right)
  push();
  fill('#333');
  const V = flashResult.V_frac || 0;
  text(`vapor fraction = ${fmt(V, 3)}`, 520, 230);
  text(`flash temperature = ${fmt1(flashResult.T_flash)} K`, 520, 250);
  text(`flash pressure = ${fmt1(flashResult.P_flash)} kPa`, 520, 270);
  pop();

  // Heat text
  push();
  fill(heatColor);
  text(`Q = ${fmt1(flashResult.Q_hr)} kJ/hr`, 300, 180);
  pop();
}

// Trigger initial calculation after p5 is ready
window.addEventListener('load', () => {
  updateFromControls();
});
</script>
</body>
