  });

  const earth = new THREE.Mesh(earthGeo, earthMaterial);
  earth.castShadow = true;
  earth.receiveShadow = false;
  scene.add(earth);

  const atmoGeo = new THREE.SphereGeometry(3.15, 64, 64);
  const atmoMaterial = new THREE.MeshBasicMaterial({
    color: 0x6688ff,
    transparent: true,
    opacity: 0.25,
    blending: THREE.AdditiveBlending
  });
  const atmosphere = new THREE.Mesh(atmoGeo, atmoMaterial);
  earth.add(atmosphere);

  // Sun visualization (sphere) and light direction
  const sun = new THREE.Mesh(
    new THREE.SphereGeometry(4, 48, 48),
    new THREE.MeshBasicMaterial({ color: 0xffcc55 })
  );
  sun.layers.enable(0);
  sun.visible = false; // visible in space view
  scene.add(sun);

  // Constellations: simple points/lines on a celestial sphere
  const constellationsData = {
    aries:  [ [0.15, 0.1], [0.28, 0.08], [0.39, 0.05], [0.52, 0.02] ],
    taurus: [ [1.1, 0.08], [1.2, 0.15], [1.32, 0.12], [1.42, 0.02], [1.55, 0.00] ],
    gemini: [ [2.1, 0.2], [2.0, 0.18], [1.9, 0.16], [1.85, 0.10], [1.78, 0.02] ]
  };
  const constellationGroup = new THREE.Group();
  scene.add(constellationGroup);

  function lonLatToVec3(lon, lat, radius) {
    const x = radius * Math.cos(lat) * Math.cos(lon);
    const z = radius * Math.cos(lat) * Math.sin(lon);
    const y = radius * Math.sin(lat);
    return new THREE.Vector3(x, y, z);
  }

  function buildConstellations() {
    const radius = 280;
    const starPositions = [];
    const linePositions = [];
    Object.values(constellationsData).forEach(arr => {
      for (let i = 0; i < arr.length; i++) {
        const [lon, lat] = arr[i];
        const p = lonLatToVec3(lon, lat, radius);
        starPositions.push(p.x, p.y, p.z);
        if (i > 0) {
          const [plon, plat] = arr[i-1];
          const pprev = lonLatToVec3(plon, plat, radius);
          linePositions.push(pprev.x, pprev.y, pprev.z, p.x, p.y, p.z);
        }
      }
    });
    const starsGeom = new THREE.BufferGeometry();
    starsGeom.setAttribute('position', new THREE.Float32BufferAttribute(starPositions, 3));
    const stars = new THREE.Points(starsGeom, new THREE.PointsMaterial({ color: 0xfff1a6, size: 2.2, sizeAttenuation: true }));
    constellationGroup.add(stars);

    const linesGeom = new THREE.BufferGeometry();
    linesGeom.setAttribute('position', new THREE.Float32BufferAttribute(linePositions, 3));
    const lines = new THREE.LineSegments(linesGeom, new THREE.LineBasicMaterial({ color: 0xfff1a6, linewidth: 1 }));
    constellationGroup.add(lines);
  }
  buildConstellations();

  // Scaling
  const earthStylizedScale = 1.0;
  const earthRealisticScale = 0.55;
  function applyEarthScale() {
    const s = params.realisticScale ? earthRealisticScale : earthStylizedScale;
    earth.scale.setScalar(s);
  }
  applyEarthScale();

  // Layout and view handling
  function resizeRendererToDisplaySize() {
    const container = document.getElementById('visualization-container');
    const w = container.clientWidth - 24; // padding in container
    const h = container.clientHeight - 24;
    const needResize = (canvas.width !== w || canvas.height !== h);
    if (needResize) {
      renderer.setSize(w, h, false);
      camera.aspect = w / h;
      camera.updateProjectionMatrix();
    }
  }

  function setView(mode) {
    params.view = mode;
    if (mode === 'land') {
      btnLand.classList.add('active');
      btnSpace.classList.remove('active');
      landPlane.visible = true;
      moonSphere.visible = false;
      sun.visible = false;
      // Camera at a location like a ridge looking out to the sky
      camera.position.set(0, 6, 28);
      camera.lookAt(0, 0, 0);
    } else {
      btnSpace.classList.add('active');
      btnLand.classList.remove('active');
      landPlane.visible = false;
      moonSphere.visible = true;
      sun.visible = true;
      camera.position.set(0, 30, 70);
      camera.lookAt(0, 0, 0);
    }
  }
  setView('land');

  // Update scene based on current parameters
  const R = 40; // Earth orbit radius around the Moon (selenocentric)
  const TAU = Math.PI * 2;
  function updateScene(dt=0) {
    // Update angles
    const daysPerOrbit = 27.32; // synodic-style for demo
    const earthAngle = (params.time / daysPerOrbit) * TAU;
    const sunAngle = earthAngle + params.sunEarthAngle;

    // Earth position on tilted orbit plane:
    const ex = R * Math.cos(earthAngle);
    const ey = R * Math.sin(earthAngle);
    // rotate around X by nodalAngle to tilt orbit plane
    const cosN = Math.cos(params.nodalAngle), sinN = Math.sin(params.nodalAngle);
    const epos = new THREE.Vector3(ex, ey * cosN, ey * sinN);
    earth.position.copy(epos);

    // Sun direction and object position (same plane, far away)
    const sx = Math.cos(sunAngle), sy = Math.sin(sunAngle);
    const sunDir = new THREE.Vector3(sx, sy * cosN, sy * sinN).normalize();
    const sunDistance = 500;
    sun.position.copy(sunDir.clone().multiplyScalar(sunDistance));
    dirLight.position.copy(sunDir.clone().multiplyScalar(300));
    dirLight.target.position.set(0, 0, 0);
    dirLight.target.updateMatrixWorld();

    // Constellations rotation (around vertical axis)
    constellationGroup.rotation.y = params.zodiac;

    // Eclipse detection: Earth between Sun and Moon when directions align
    const angle = sunDir.angleTo(epos.clone().normalize());
    const inEclipse = angle < 0.18; // threshold
    earth.material = inEclipse ? earthEclipseMaterial : earthMaterial;

    // Orient camera in land view to look toward mean Earth position softly
    if (params.view === 'land') {
      const look = earth.position.clone();
      look.y = Math.max(look.y, 0); // prevent looking below horizon too much
      camera.lookAt(look);
    }
  }

  // UI event handlers
  function format2(v) { return Number(v).toFixed(2); }

  function updateUI() {
    sliderTimeValue.textContent = format2(params.time);
    sliderNodalValue.textContent = format2(params.nodalAngle);
    sliderZodiacValue.textContent = format2(params.zodiac);
    // For Sun-Earth angle, once user interacts we show numeric. Initially already TeX.
    sliderSunEarthValue.textContent = format2(params.sunEarthAngle);
  }

  sliderTime.addEventListener('input', () => {
    params.time = parseFloat(sliderTime.value);
    sliderTimeValue.textContent = format2(params.time);
  });
  sliderNodal.addEventListener('input', () => {
    params.nodalAngle = parseFloat(sliderNodal.value);
    sliderNodalValue.textContent = format2(params.nodalAngle);
  });
  sliderSunEarth.addEventListener('input', () => {
    params.sunEarthAngle = parseFloat(sliderSunEarth.value);
    sliderSunEarthValue.textContent = format2(params.sunEarthAngle);
  });
  sliderZodiac.addEventListener('input', () => {
    params.zodiac = parseFloat(sliderZodiac.value);
    sliderZodiacValue.textContent = format2(params.zodiac);
  });

  function togglePlay(which, btn) {
    isAnimating[which] = !isAnimating[which];
    btn.classList.toggle('playing', isAnimating[which]);
    btn.textContent = isAnimating[which] ? '⏸' : '+';
  }
  btnTimePlay.addEventListener('click', () => togglePlay('time', btnTimePlay));
  btnNodalPlay.addEventListener('click', () => togglePlay('nodalAngle', btnNodalPlay));
  btnSunEarthPlay.addEventListener('click', () => togglePlay('sunEarthAngle', btnSunEarthPlay));
  btnZodiacPlay.addEventListener('click', () => togglePlay('zodiac', btnZodiacPlay));

  btnLand.addEventListener('click', () => setView('land'));
  btnSpace.addEventListener('click', () => setView('space'));

  checkboxConstellations.addEventListener('change', () => {
    params.showConstellations = checkboxConstellations.checked;
    constellationGroup.visible = params.showConstellations;
  });
  checkboxRealistic.addEventListener('change', () => {
    params.realisticScale = checkboxRealistic.checked;
    applyEarthScale();
  });

  // Initial UI display (MathJax will render the π/2 already in HTML)
  updateUI();
  constellationGroup.visible = params.showConstellations;

  // Animation loop
  let lastT = performance.now();
  function animate(now) {
    requestAnimationFrame(animate);
    const dt = Math.min(0.05, (now - lastT) / 1000); // clamp delta
    lastT = now;

    // Drive sliders if animating
    if (isAnimating.time) {
      params.time += dt * 10; // 10 days per second
      if (params.time > parseFloat(sliderTime.max)) params.time = parseFloat(sliderTime.min);
      sliderTime.value = params.time;
      sliderTimeValue.textContent = format2(params.time);
    }
    if (isAnimating.nodalAngle) {
      params.nodalAngle += dt * 0.4;
      if (params.nodalAngle > Math.PI*2) params.nodalAngle -= Math.PI*2;
      sliderNodal.value = params.nodalAngle;
      sliderNodalValue.textContent = format2(params.nodalAngle);
    }
    if (isAnimating.sunEarthAngle) {
      params.sunEarthAngle += dt * 0.6;
      if (params.sunEarthAngle > Math.PI*2) params.sunEarthAngle -= Math.PI*2;
      sliderSunEarth.value = params.sunEarthAngle;
      sliderSunEarthValue.textContent = format2(params.sunEarthAngle);
    }
    if (isAnimating.zodiac) {
      params.zodiac += dt * 0.5;
      if (params.zodiac > Math.PI*2) params.zodiac -= Math.PI*2;
      sliderZodiac.value = params.zodiac;
      sliderZodiacValue.textContent = format2(params.zodiac);
    }

    resizeRendererToDisplaySize();
    updateScene(dt);
    renderer.render(scene, camera);
  }
  requestAnimationFrame(animate);

  // Handle window resize
  window.addEventListener('resize', resizeRendererToDisplaySize);
});
</script>
</body>
</html>
