<body>
  <h1>Best Effort Global Warming Trajectories</h1>
  <p>Adjust the sliders to explore how emission policies and atmospheric residence time affect atmospheric carbon and annual emissions over the next three centuries.</p>

  <div id="controls-container">
    <div class="control-row">
      <label for="slider-residence-time">Atmospheric CO2 residence time (years)</label>
      <input type="range" id="slider-residence-time" min="50" max="500" step="1" value="200">
      <span id="value-residence-time" class="value-box">200</span>
    </div>
    <div class="control-row">
      <label for="slider-transition-pace">Transition to constant reduction pace (years)</label>
      <input type="range" id="slider-transition-pace" min="0" max="100" step="1" value="25">
      <span id="value-transition-pace" class="value-box">25</span>
    </div>
    <div class="control-row">
      <label for="slider-wedges">Number of constant pace wedges (above emission stabilization at t=0)</label>
      <input type="range" id="slider-wedges" min="0" max="10" step="0.01" value="4">
      <span id="value-wedges" class="value-box">4.00</span>
    </div>
  </div>

  <div id="plot-div"></div>

  <script>
    // Constants for the model
    const C0 = 850;      // GtC at t=0
    const E0 = 8.5;      // GtC/yr at t=0
    const C_eq = 586;    // GtC
    const E_floor = 1.5; // GtC/yr
    const rise_rate = 0.02; // GtC/yr^2
    const time_step = 0.5;  // years
    const max_time = 300;   // years

    // Elements
    const sliderResidence = document.getElementById('slider-residence-time');
    const sliderTransition = document.getElementById('slider-transition-pace');
    const sliderWedges    = document.getElementById('slider-wedges');

    const valueResidence  = document.getElementById('value-residence-time');
    const valueTransition = document.getElementById('value-transition-pace');
    const valueWedges     = document.getElementById('value-wedges');

    function formatNumber(n, digits=0) {
      return Number(n).toFixed(digits);
    }

    function simulate() {
      const t_res   = parseFloat(sliderResidence.value);
      const t_trans = parseFloat(sliderTransition.value);
      const N_w     = parseFloat(sliderWedges.value);

      const E_peak = E0 + rise_rate * t_trans;
      const reduction_rate = N_w / 50.0;

      const tArr = [];
      const E100Arr = [];
      const CArr = [];

      let C_curr = C0;

      for (let t = 0; t <= max_time + 1e-9; t += time_step) {
        // Emissions E(t)
        let E_t;
        if (t < t_trans) {
          E_t = E0 + rise_rate * t;
        } else {
          const E_linear = E_peak - reduction_rate * (t - t_trans);
          E_t = Math.max(E_floor, E_linear);
        }

        // Record current state
        tArr.push(t);
        E100Arr.push(100 * E_t);
        CArr.push(C_curr);

        // Update carbon via Euler step
        const dCdt = E_t - (C_curr - C_eq) / t_res;
        C_curr = C_curr + dCdt * time_step;
      }

      return { tArr, E100Arr, CArr };
    }

    function drawPlot() {
      const { tArr, E100Arr, CArr } = simulate();

      const traceCarbon = {
        type: 'scatter',
        mode: 'lines',
        x: tArr,
        y: CArr,
        line: { color: 'blue', width: 2 },
        name: 'Atmospheric Carbon'
      };

      const traceEmissions = {
        type: 'scatter',
        mode: 'lines',
        x: tArr,
        y: E100Arr,
        line: { color: 'purple', width: 2 },
        name: 'Annual Emissions ×100'
      };

      const traceCeiling = {
        type: 'scatter',
        mode: 'lines',
        x: [0, 300],
        y: [1200, 1200],
        line: { color: 'black', width: 2, dash: 'dash' },
        name: 'Target Ceiling'
      };

      const layout = {
        title: 'Atmospheric carbon (GtC; blue) and annual emissions (GtC/yr × 100; purple) versus time',
        paper_bgcolor: '#f9f9f9',
        plot_bgcolor: 'white',
        margin: { l: 60, r: 20, b: 40, t: 80 },
        showlegend: false,
        xaxis: {
          title: 'years',
          range: [0, 300],
          zeroline: true,
          showgrid: true,
          gridcolor: '#ddd'
        },
        yaxis: {
          title: 'GtC or GtC/yr × 100',
          range: [0, 1600],
          zeroline: true,
          showgrid: true,
          gridcolor: '#ddd'
        }
      };

      Plotly.react('plot-div', [traceCarbon, traceEmissions, traceCeiling], layout, {responsive: true});
    }

    function updateDisplays() {
      valueResidence.textContent  = formatNumber(sliderResidence.value, 0);
      valueTransition.textContent = formatNumber(sliderTransition.value, 0);
      valueWedges.textContent     = formatNumber(sliderWedges.value, 2);
    }

    function onInput() {
      updateDisplays();
      drawPlot();
    }

    // Initialize
    updateDisplays();
    drawPlot();

    // Event listeners for real-time updates
    sliderResidence.addEventListener('input', onInput);
    sliderTransition.addEventListener('input', onInput);
    sliderWedges.addEventListener('input', onInput);
  </script>
</body>
</html>
