  // Angle formatting helper for θ0 display
  function formatAngle(rad) {
    const pi = Math.PI;
    const tol = 0.03; // radians (~1.7 degrees)
    const candidates = [
      {n: 0, d: 1},  // 0
      {n: 1, d: 6},  // π/6
      {n: 1, d: 4},  // π/4
      {n: 1, d: 3},  // π/3
      {n: 1, d: 2},  // π/2
      {n: 2, d: 3},  // 2π/3
      {n: 3, d: 4},  // 3π/4
      {n: 5, d: 6},  // 5π/6
      {n: 1, d: 1},  // π
    ];

    // Check both signs
    for (const sign of [-1, 1]) {
      for (const f of candidates) {
        const target = sign * f.n * pi / f.d;
        if (Math.abs(rad - target) <= tol) {
          if (f.n === 0) return "0";
          const prefix = sign < 0 ? "-" : "";
          if (f.n === 1 && f.d === 1) return prefix + "π";
          if (f.d === 1) return prefix + f.n + "π";
          if (f.n === 1) return prefix + "π/" + f.d;
          return prefix + f.n + "π/" + f.d;
        }
      }
    }
    return rad.toFixed(2);
  }

  function updateValueDisplays() {
