  sliderTheta0.addEventListener('input', applySliderChange);
  sliderV0.addEventListener('input', applySliderChange);
  sliderLen.addEventListener('input', applySliderChange);

  // Play/Pause button
  btnPlayPause.addEventListener('click', () => {
    isPlaying = !isPlaying;
    btnPlayPause.textContent = isPlaying ? "||" : "▶";
  });

  // Reset button
  btnReset.addEventListener('click', () => {
    stopPlaying();
    resetPhysics();
  });

  // p5.js sketch
  let canvas;
  const W = 500, H = 500;

  function setup() {
    canvas = createCanvas(W, H);
