      // Optionally substep if needed for better stability
      const steps = Math.max(1, Math.ceil(dt / 0.016));
      dt = dt / steps;

      for (let i = 0; i < steps; i++) {
        // theta_ddot from given equation (M = m = 1)
        const sinT = Math.sin(theta);
        const cosT = Math.cos(theta);
        const denom = l * (M + m * sinT * sinT);
        const theta_ddot = (-m * l * sinT * cosT * theta_dot * theta_dot - g * (M + m) * sinT) / denom;

        // integrate
        theta_dot += theta_ddot * dt;
        theta += theta_dot * dt;

        // pivot velocity from conservation relation with v0 parameter
        x_dot = v0_param - (m * l * Math.cos(theta) * theta_dot) / (M + m);
        x += x_dot * dt;

        t += dt;
      }
    }

    // Convert physics coordinates to screen
    const px = width / 2 + x * scale;
    const py = height / 2;

    const bx = px + (l * scale) * Math.sin(theta);
    const by = py + (l * scale) * Math.cos(theta);

    // Draw pivot (red rectangle)
    noStroke();
    fill('red');
    const pivotW = 60, pivotH = 22;
    rect(px, py, pivotW, pivotH, 4);

    // Draw bob (orange circle)
    stroke('black');
    strokeWeight(2);
    fill('orange');
    const bobR = 14;
    circle(bx, by, bobR * 2);

    // Draw connector (black line)
    stroke('black');
    strokeWeight(2);
    line(px, py, bx, by);
  }

  // Expose p5's setup and draw to global scope
  window.setup = setup;
  window.draw = draw;
</script>
</body>
</html>
