    </div>
  </div>

  <!-- three.js via CDN (module build) -->
  <script type="module">
    import * as THREE from "https://unpkg.com/three@0.152.2/build/three.module.js";
    import { OrbitControls } from "https://unpkg.com/three@0.152.2/examples/jsm/controls/OrbitControls.js";

    // UI elements
    const elStrike = document.getElementById('slider-strike');
    const elDip = document.getElementById('slider-dip');
    const elRake = document.getElementById('slider-slip-angle');
    const elSlipAmt = document.getElementById('slider-slip-amount');
    const valStrike = document.getElementById('value-strike');
    const valDip = document.getElementById('value-dip');
    const valRake = document.getElementById('value-slip-angle');
    const valSlip = document.getElementById('value-slip-amount');
    const btnReset = document.getElementById('btn-reset');
    const checkboxBox = document.getElementById('checkbox-box');

    // Scene setup
    const canvas = document.getElementById('webgl-canvas');
    const renderer = new THREE.WebGLRenderer({ canvas, antialias: true, alpha: true });
    renderer.setPixelRatio(Math.min(window.devicePixelRatio, 2));

    const scene = new THREE.Scene();
    scene.background = null;

    const camera = new THREE.PerspectiveCamera(35, 1, 0.1, 100);
    camera.position.set(3.2, 2.0, 3.6);
    scene.add(camera);

    const controls = new OrbitControls(camera, renderer.domElement);
    controls.enableDamping = true;
    controls.target.set(0, 0, 0);

    // Lights
    scene.add(new THREE.AmbientLight(0xffffff, 0.85));
    const dir1 = new THREE.DirectionalLight(0xffffff, 0.8);
    dir1.position.set(3, 4, 2);
    scene.add(dir1);
    const dir2 = new THREE.DirectionalLight(0xffffff, 0.4);
    dir2.position.set(-3, -2, -3);
    scene.add(dir2);

    // Deformed sphere geometry + materials
    const sphereGeom = new THREE.SphereGeometry(1, 64, 32); // BufferGeometry
    // store original vertex positions
    const posAttr = sphereGeom.getAttribute('position');
    const posArray = posAttr.array;
    const originalPositions = new Float32Array(posArray.length);
    originalPositions.set(posArray);

    const colorArray = new Float32Array(posArray.length);
    sphereGeom.setAttribute('color', new THREE.BufferAttribute(colorArray, 3));

    const material = new THREE.MeshStandardMaterial({
      vertexColors: true,
      roughness: 0.85,
      metalness: 0.0
    });
    const sphere = new THREE.Mesh(sphereGeom, material);
    scene.add(sphere);

    // A thin wireframe to emulate the graticule on the surface
    const wfMaterial = new THREE.MeshBasicMaterial({ color: 0x666666, wireframe: true, transparent: true, opacity: 0.55 });
    const sphereWire = new THREE.Mesh(sphereGeom, wfMaterial);
    scene.add(sphereWire);

    // Bounding box helper (toggled via checkbox)
    const boxHelper = new THREE.BoxHelper(sphere, 0x888888);
    boxHelper.visible = false;
    scene.add(boxHelper);

    // Compass rose: dashed circle on XZ plane, plus labels
    const compass = new THREE.Group();
    scene.add(compass);

    function makeDashedCircle(radius=1.6, segments=256) {
      const curve = new THREE.EllipseCurve(0, 0, radius, radius, 0, Math.PI*2, false, 0);
      const pts = curve.getPoints(segments);
      const geom = new THREE.BufferGeometry().setFromPoints(pts.map(p => new THREE.Vector3(p.x, 0, p.y)));
      const mat = new THREE.LineDashedMaterial({ color: 0x999999, dashSize: 0.12, gapSize: 0.08, linewidth: 1 });
      const line = new THREE.Line(geom, mat);
      line.computeLineDistances();
      return line;
    }

    function makeTextSprite(text, options = {}) {
      const {
        size = 64,
        color = "#222",
        font = "600 28px Arial",
        padding = 8,
        outline = false
      } = options;
      const canvas = document.createElement('canvas');
      canvas.width = size;
      canvas.height = size;
      const ctx = canvas.getContext('2d');
      ctx.clearRect(0,0,size,size);
      ctx.font = font;
      ctx.fillStyle = color;
      ctx.textAlign = 'center';
      ctx.textBaseline = 'middle';
      if (outline) {
        ctx.lineWidth = 5;
        ctx.strokeStyle = "rgba(255,255,255,0.9)";
        ctx.strokeText(text, size/2, size/2);
      }
      ctx.fillText(text, size/2, size/2);

      const tex = new THREE.CanvasTexture(canvas);
      tex.minFilter = THREE.LinearFilter;
      const mat = new THREE.SpriteMaterial({ map: tex, transparent: true });
      const sprite = new THREE.Sprite(mat);
      const scale = 0.28;
      sprite.scale.set(scale, scale, scale);
      return sprite;
    }

    // Build compass
    const compassCircle = makeDashedCircle(1.8, 512);
    compass.add(compassCircle);

    const cardinalR = 1.8;
    const labelsCardinal = [
      { txt:'N', x:0, z:-cardinalR },
      { txt:'E', x:+cardinalR, z:0 },
      { txt:'S', x:0, z:+cardinalR },
      { txt:'W', x:-cardinalR, z:0 }
    ];
    labelsCardinal.forEach(({txt,x,z})=>{
      const s = makeTextSprite(txt, {font:"700 46px Arial", color:"#222", size:256, outline:true});
      s.position.set(x, 0.02, z);
      compass.add(s);
    });

    const degreeR = 2.05;
    const degreeMarks = [30,60,120,150,210,240,300,330];
    degreeMarks.forEach(d=>{
      const rad = THREE.MathUtils.degToRad(d);
      const x = degreeR * Math.sin(rad);
      const z = degreeR * Math.cos(rad);
      const s = makeTextSprite(`${d}°`, {font:"500 26px Arial", color:"#666", size:196});
      s.scale.set(0.22,0.22,0.22);
      s.position.set(x, 0.02, -z); // because compass circle used (x,0,y) with y -> z; here cos(rad) along N
      // Careful: our compassCircle used pts as (x,0,y). For degrees we want 0° at N (z negative).
      // Setting position as (sin, 0, -cos) places 0° at N, 90° at E etc.
      compass.add(s);
    });

    // Axis labels P, T, U
    const labelP = makeTextSprite('P', {font:"700 40px Arial", color:"#222", size:256, outline:true});
    const labelT = makeTextSprite('T', {font:"700 40px Arial", color:"#222", size:256, outline:true});
    const labelU = makeTextSprite('U', {font:"700 40px Arial", color:"#222", size:256, outline:true});
    [labelP,labelT,labelU].forEach(l=>{ l.center = new THREE.Vector2(0.5,0.5); });
    scene.add(labelP, labelT, labelU);

    // Utility: NED (x=N, y=E, z=D) to three.js (x=E, y=Up, z=South)
    function nedToThree(vNED) {
      return new THREE.Vector3(vNED.y, -vNED.z, -vNED.x); // [E, -D, -N]
    }

    function normalize(v) { return v.normalize(); }

    function computeVectors(strikeDeg, dipDeg, rakeDeg) {
      const φ = THREE.MathUtils.degToRad(strikeDeg);
      const δ = THREE.MathUtils.degToRad(dipDeg);
      const λ = THREE.MathUtils.degToRad(rakeDeg);

      // Fault normal n (unit) in NED coordinates
      // n = [-sinδ sinφ, sinδ cosφ, -cosδ]
      const nNED = new THREE.Vector3(
        -Math.sin(δ) * Math.sin(φ),
         Math.sin(δ) * Math.cos(φ),
        -Math.cos(δ)
      );

      // Slip vector u (unit) in NED
      // u = [cosλ cosφ + sinλ cosδ sinφ,
      //      cosλ sinφ - sinλ cosδ cosφ,
      //     -sinλ sinδ]
      const uNED = new THREE.Vector3(
        Math.cos(λ)*Math.cos(φ) + Math.sin(λ)*Math.cos(δ)*Math.sin(φ),
        Math.cos(λ)*Math.sin(φ) - Math.sin(λ)*Math.cos(δ)*Math.cos(φ),
        -Math.sin(λ)*Math.sin(δ)
      );

      const n = normalize(nedToThree(nNED));
      const u = normalize(nedToThree(uNED));

      // Principal axes (Aki & Richards convention as requested)
      const P = normalize(n.clone().add(u)).multiplyScalar(1/Math.SQRT2); // compressional
      const T = normalize(u.clone().sub(n)).multiplyScalar(1/Math.SQRT2); // tensional

      return { n, u, P: normalize(P), T: normalize(T) };
    }

    function updateLabels(P, T, U) {
      const r = 1.15;
      labelP.position.copy(P.clone().normalize().multiplyScalar(r));
      labelT.position.copy(T.clone().normalize().multiplyScalar(r));
      labelU.position.copy(U.clone().normalize().multiplyScalar(r));
    }

    // Geometry deformation and coloring
    const deformationScale = 0.35;

    function updateGeometry(params) {
      const { strike, dip, rake, slip } = params;
      const { P, T, u } = computeVectors(strike, dip, rake);

      // Update vertex positions and colors
      const pos = sphereGeom.attributes.position.array;
      const col = sphereGeom.attributes.color.array;

      const tmpV = new THREE.Vector3();
      for (let i = 0; i < pos.length; i += 3) {
        const ox = originalPositions[i], oy = originalPositions[i+1], oz = originalPositions[i+2];

        // direction from origin
        tmpV.set(ox, oy, oz).normalize();

        const dotT = tmpV.dot(T);
        const dotP = tmpV.dot(P);
        const f = (dotT*dotT) - (dotP*dotP); // >0 dilatational (orange), <0 compressional (white)

        // deform radially
        const radius = 1 + deformationScale * slip * f;
        const nx = tmpV.x * radius;
        const ny = tmpV.y * radius;
        const nz = tmpV.z * radius;

        pos[i] = nx; pos[i+1] = ny; pos[i+2] = nz;

        // vertex color
        const orange = new THREE.Color(0xFFA500);
        const white = new THREE.Color(0xFFFFFF);

        // mix a bit by magnitude for subtle shading
        const t = THREE.MathUtils.clamp(0.15 + 0.85*Math.abs(f), 0, 1);
        const c = f >= 0 ? orange.clone().lerp(new THREE.Color(0xffe0b3), 0.5*(1-t)) :
                           white.clone().lerp(new THREE.Color(0xeeeeee), 0.7*(1-t));

        col[i] = c.r; col[i+1] = c.g; col[i+2] = c.b;
      }

      sphereGeom.attributes.position.needsUpdate = true;
      sphereGeom.attributes.color.needsUpdate = true;
      sphereGeom.computeVertexNormals();

      // Update axis labels
      updateLabels(P, T, u);

      // Update bounding box helper
      boxHelper.update();
    }

    // Initial draw
    function getParams() {
      return {
        strike: Number(elStrike.value),
        dip: Number(elDip.value),
        rake: Number(elRake.value),
        slip: Number(elSlipAmt.value)
      };
    }

    function setValueBoxes() {
      valStrike.value = `${Number(elStrike.value).toFixed(0)}.`;
      valDip.value = `${Number(elDip.value).toFixed(0)}.`;
      valRake.value = `${Number(elRake.value).toFixed(0)}.`;
      valSlip.value = `${Number(elSlipAmt.value).toFixed(2)}`;
      const sr = document.getElementById('sr-announcer');
      sr.textContent = `strike ${valStrike.value}, dip ${valDip.value}, rake ${valRake.value}, slip ${valSlip.value}`;
    }

    function updateAll() {
      setValueBoxes();
      updateGeometry(getParams());
      renderOnce(); // ensure quick feedback
    }

    function resetControls() {
      elStrike.value = 180;
      elDip.value = 60;
      elRake.value = 90;
      elSlipAmt.value = 0.5;
      checkboxBox.checked = false;
      boxHelper.visible = false;
      updateAll();
    }

    // Events
    [elStrike, elDip, elRake, elSlipAmt].forEach(el=>{
      el.addEventListener('input', updateAll);
    });

    btnReset.addEventListener('click', resetControls);

    checkboxBox.addEventListener('change', (e)=>{
      boxHelper.visible = e.target.checked;
      renderOnce();
    });

    // Resize handling
    function onResize() {
      const rect = document.getElementById('canvas-container').getBoundingClientRect();
      const width = Math.max(200, rect.width);
      const height = Math.max(200, rect.height);
      renderer.setSize(width, height, false);
      camera.aspect = width / height;
      camera.updateProjectionMatrix();
      renderOnce();
    }
    window.addEventListener('resize', onResize);

    // Animation loop
    let needsRender = true;
    function renderOnce() { needsRender = true; }
    function animate() {
      requestAnimationFrame(animate);
      controls.update();
      if (needsRender) {
        renderer.render(scene, camera);
        needsRender = false;
      }
    }

    // Initialize
    resetControls();
    onResize();
    animate();

  </script>
</body>
</html>
