<body>
  <div id="app-container">
    <div id="control-panel">
      <div class="control-group">
        <label for="slider-db-size">database size</label>
        <input type="range" id="slider-db-size" min="8" max="100" step="1" value="10">
        <div class="value-display">+<span id="display-db-size">10</span></div>
      </div>
      <div class="control-group">
        <label for="slider-iterations">number of iterations</label>
        <input type="range" id="slider-iterations" min="0" max="20" step="1" value="2">
        <div class="value-display">+<span id="display-iterations">2</span></div>
      </div>
    </div>

    <div id="canvas-container"></div>

    <div id="probability-display">99.9% success probability</div>
  </div>

  <script src="https://cdnjs.cloudflare.com/ajax/libs/p5.js/1.4.0/p5.js"></script>
  <script>
    // DOM elements
    const sliderN = document.getElementById('slider-db-size');
    const sliderK = document.getElementById('slider-iterations');
    const dispN = document.getElementById('display-db-size');
    const dispK = document.getElementById('display-iterations');
    const probDiv = document.getElementById('probability-display');

    // Update displays and probability
    function updateDisplays() {
      dispN.textContent = sliderN.value;
      dispK.textContent = sliderK.value;
      const N = parseInt(sliderN.value, 10);
      const k = parseInt(sliderK.value, 10);
      const P = Math.pow(Math.sin((2 * k + 1) * Math.asin(1 / Math.sqrt(N))), 2);
      const pct = (P * 100).toFixed(1);
      probDiv.textContent = `${pct}% success probability`;
    }

    sliderN.addEventListener('input', updateDisplays);
    sliderK.addEventListener('input', updateDisplays);

    // p5.js sketch
    let cnv;
    const TARGET_INDEX = 6; // fixed target state
    const BAR_SCALE = 80;   // fixed scale in pixels per amplitude unit (per spec)
    const SECTION_COUNT = 4;

    function setup() {
      const w = getCanvasWidth();
      const h = getCanvasHeight();
      cnv = createCanvas(w, h);
      cnv.parent('canvas-container');
      textFont('Arial');
      textSize(14);
      noStroke();
      updateDisplays(); // ensure probability is correct at load
    }

    function windowResized() {
      resizeCanvas(getCanvasWidth(), getCanvasHeight());
    }

    function getCanvasWidth() {
      const container = document.getElementById('canvas-container');
      // Subtract padding visually by using clientWidth; p5 handles exact pixels
      return Math.max(320, container.clientWidth - 0);
    }

    function getCanvasHeight() {
      // Create a pleasant aspect ratio; enough height for four sections
      const base = 480;
      // Slightly responsive with width
      return Math.max(420, Math.min(640, Math.round(getCanvasWidth() * 0.6)));
    }

    function draw() {
      const N = parseInt(sliderN.value, 10);
      const k = parseInt(sliderK.value, 10);

      background(255);

      // Compute all stages
      const stage1 = initialAmplitudes(N);
      const stage2 = stage1.slice();
      inversion(stage2, TARGET_INDEX);
      const stage3 = diffusion(stage2);
      const stage4 = applyGrover(stage1, TARGET_INDEX, k);

      // Layout
      const padTop = 10;
      const padBottom = 10;
      const totalPlotHeight = height - padTop - padBottom;
      const sectionH = totalPlotHeight / SECTION_COUNT;
      const marginX = 36;

      // Draw each stage
      drawStage(0, "original amplitudes", stage1, {marginX, sectionH, showParens: true, N});
      drawStage(1, "after first inversion", stage2, {marginX, sectionH, showParens: false, N});
      drawStage(2, "after first diffusion", stage3, {marginX, sectionH, showParens: false, N});
      drawStage(3, `after ${k} iterations`, stage4, {marginX, sectionH, showParens: false, N});
    }

    // Helpers for Grover's algorithm
    function initialAmplitudes(N) {
      const a = 1 / Math.sqrt(N);
      return Array.from({length: N}, () => a);
    }

    function inversion(amps, targetIndex) {
      if (targetIndex >= 0 && targetIndex < amps.length) {
        amps[targetIndex] *= -1;
      }
      return amps;
    }

    function diffusion(amps) {
      const N = amps.length;
      let sum = 0;
      for (let i = 0; i < N; i++) sum += amps[i];
      const mean = sum / N;
      const out = new Array(N);
      for (let i = 0; i < N; i++) out[i] = 2 * mean - amps[i];
      return out;
    }

    function applyGrover(initial, targetIndex, k) {
      let state = initial.slice();
      for (let i = 0; i < k; i++) {
        inversion(state, targetIndex);
        state = diffusion(state);
      }
      return state;
    }

    // Drawing utilities
    function drawStage(index, title, amplitudes, config) {
      const {marginX, sectionH, showParens, N} = config;
      const top = index * sectionH;
      const centerY = top + sectionH / 2;
      const innerW = width - 2 * marginX;

      // Title
      fill(90);
      noStroke();
      textAlign(CENTER, BOTTOM);
      text(title, width / 2, top + 18);

      // Baseline
      stroke(160);
      strokeWeight(1);
      line(marginX, centerY, width - marginX, centerY);
      noStroke();

      // Spacing and bar width
      const dx = N > 1 ? innerW / (N - 1) : innerW;
      const barW = Math.max(1, Math.min(8, dx * 0.6));

      // Bars
      for (let i = 0; i < N; i++) {
        const x = marginX + i * dx;
        const a = amplitudes[i];
        const h = a * BAR_SCALE;
        const y = h >= 0 ? centerY - h : centerY;
        fill(i === TARGET_INDEX ? '#FFA500' : '#808080');
        noStroke();
        rectMode(CORNER);
        rect(x - barW / 2, y, barW, Math.abs(h));
      }

      // Parentheses for the first stage
      if (showParens) {
        const arcW = 40;
        const arcH = sectionH * 0.75;
        stroke('#666');
        strokeWeight(2);
        noFill();
        // left
        arc(marginX - arcW * 0.4, centerY, arcW, arcH, -HALF_PI, HALF_PI);
        // right
        arc(width - marginX + arcW * 0.4, centerY, arcW, arcH, HALF_PI, 3 * HALF_PI);
        noStroke();
      }
    }
  </script>
</body>
</html>
