<script>
/* -------------------- State helpers and UI wiring -------------------- */
const sliders = {
    volatility: document.getElementById('slider-volatility'),
    distillate: document.getElementById('slider-distillate'),
    bottom: document.getElementById('slider-bottom'),
    feedComp: document.getElementById('slider-feed-comp'),
    feedQuality: document.getElementById('slider-feed-quality')
};

const labels = {
    volatility: document.getElementById('val-volatility'),
    distillate: document.getElementById('val-distillate'),
    bottom: document.getElementById('val-bottom'),
    feedComp: document.getElementById('val-feed-comp'),
    feedQuality: document.getElementById('val-feed-quality')
};

const buttons = {
    volatility: document.getElementById('btn-volatility-plus'),
    distillate: document.getElementById('btn-distillate-plus'),
    bottom: document.getElementById('btn-bottom-plus'),
    feedComp: document.getElementById('btn-feed-comp-plus'),
    feedQuality: document.getElementById('btn-feed-quality-plus')
};

const stageDisplay = document.getElementById('stages-count-display');

function clamp(val, min, max) { return Math.min(max, Math.max(min, val)); }

/* Enforce: bottom < feed < distillate; dynamically adjust feedComp min/max */
function enforceConstraints(triggeredBy = null) {
    const stepF = parseFloat(sliders.feedComp.step);
    const xB = parseFloat(sliders.bottom.value);
    const xD = parseFloat(sliders.distillate.value);

    // dynamic min/max for feed composition
    sliders.feedComp.min = (xB + stepF).toFixed(2);
    sliders.feedComp.max = (xD - stepF).toFixed(2);

    // adjust feedComp if outside bounds or violated by change
    let xF = parseFloat(sliders.feedComp.value);
    if (xF <= xB) xF = xB + stepF;
    if (xF >= xD) xF = xD - stepF;

    xF = clamp(xF, parseFloat(sliders.feedComp.min), parseFloat(sliders.feedComp.max));
    sliders.feedComp.value = +xF.toFixed(2);
}

/* Update tiny value badges near labels */
function updateValueBadges() {
    labels.volatility.textContent = Number(sliders.volatility.value).toFixed(1);
    labels.distillate.textContent = Number(sliders.distillate.value).toFixed(2);
    labels.bottom.textContent = Number(sliders.bottom.value).toFixed(2);
    labels.feedComp.textContent = Number(sliders.feedComp.value).toFixed(2);
    labels.feedQuality.textContent = Number(sliders.feedQuality.value).toFixed(2);
}

/* Add input listeners */
Object.values(sliders).forEach(sl => {
    sl.addEventListener('input', () => {
        enforceConstraints();
        updateValueBadges();
        drawDiagram();
    });
});

/* Add + button behavior (increment by one step and trigger input) */
function addIncrement(btn, slider) {
    btn.addEventListener('click', () => {
        const step = parseFloat(slider.step);
        const max = parseFloat(slider.max);
        let val = parseFloat(slider.value) + step;
        if (val > max) val = max;
        // Fix floating precision issues
        const decimals = (slider.step.split('.')[1] || '').length;
        slider.value = +val.toFixed(decimals);
        slider.dispatchEvent(new Event('input', { bubbles: true }));
    });
}
addIncrement(buttons.volatility, sliders.volatility);
addIncrement(buttons.distillate, sliders.distillate);
addIncrement(buttons.bottom, sliders.bottom);
addIncrement(buttons.feedComp, sliders.feedComp);
addIncrement(buttons.feedQuality, sliders.feedQuality);

/* -------------------- p5.js sketch -------------------- */
let sketch = (p) => {
    let canvas;
    // plot margins
    let mLeft = 55, mRight = 20, mTop = 20, mBottom = 50;

    function plotWidth() { return p.width - mLeft - mRight; }
    function plotHeight() { return p.height - mTop - mBottom; }

    function xToPx(x) { return p.map(x, 0, 1, mLeft, mLeft + plotWidth()); }
    function yToPx(y) { return p.map(y, 0, 1, mTop + plotHeight(), mTop); } // invert Y

    p.setup = function() {
        const container = document.getElementById('canvas-container');
        const rect = container.getBoundingClientRect();
        canvas = p.createCanvas(Math.max(400, rect.width), Math.max(320, container.clientHeight));
        canvas.parent('canvas-container');
        p.noLoop();
        enforceConstraints();
        updateValueBadges();
        drawDiagram();
    };

    p.windowResized = function() {
        const container = document.getElementById('canvas-container');
        const rect = container.getBoundingClientRect();
        p.resizeCanvas(Math.max(400, rect.width), Math.max(320, container.clientHeight));
        drawDiagram();
    };

    /* ---------- Thermodynamics helpers ---------- */
    function eqY(x, alpha) {
        return (alpha * x) / (1 + (alpha - 1) * x);
    }
    function invEq(y, alpha) {
        // Solve y = alpha x / (1 + (alpha-1)x) for x
        return y / (alpha * (1 - y) + y);
    }

    function findIntersection(alpha, xF, q) {
        const eps = 1e-8;
        if (Math.abs(q - 1) < 1e-10) {
            const y = eqY(xF, alpha);
            return { x: xF, y: y };
        }
        if (Math.abs(q) < 1e-10) {
            const x = invEq(xF, alpha);
            return { x: x, y: xF };
        }
        const m = q / (q - 1);
        const yLine = (x) => m * (x - xF) + xF;
        const f = (x) => eqY(x, alpha) - yLine(x);

        let a = 0.0, b = 1.0, fa = f(a), fb = f(b);
        // Bisection with fallback to Newton if needed
        if (fa * fb > 0) {
            // Fallback Newton from xF
            let x = p.constrain(xF, 0 + eps, 1 - eps);
            for (let i = 0; i < 50; i++) {
                let denom = (1 + (alpha - 1) * x);
                let dEq = alpha / (denom * denom); // derivative of eqY wrt x
                let g = eqY(x, alpha) - yLine(x);
                let gp = dEq - m;
                if (Math.abs(gp) < 1e-8) break;
                x = x - g / gp;
                x = p.constrain(x, 0 + eps, 1 - eps);
            }
            return { x: x, y: eqY(x, alpha) };
        } else {
            // Bisection
            for (let i = 0; i < 80; i++) {
                const mid = 0.5 * (a + b);
                const fm = f(mid);
                if (fa * fm <= 0) { b = mid; fb = fm; }
                else { a = mid; fa = fm; }
                if (Math.abs(b - a) < 1e-8) break;
            }
            const x = 0.5 * (a + b);
            return { x: x, y: eqY(x, alpha) };
        }
    }

    function drawAxes() {
        p.push();
        p.stroke('#808080');
        p.fill('#808080');
        p.strokeWeight(1);

        // X and Y axes
        p.line(xToPx(0), yToPx(0), xToPx(1), yToPx(0));
        p.line(xToPx(0), yToPx(0), xToPx(0), yToPx(1));

        // Ticks and labels
        p.textSize(11);
        p.textAlign(p.CENTER, p.TOP);
        for (let t = 0; t <= 10; t++) {
            const val = t / 10;
            const x = xToPx(val), y0 = yToPx(0), y1 = y0 + 6;
            p.line(x, y0, x, y1);
            p.text(val.toFixed(1), x, y1 + 2);
        }
        p.textAlign(p.RIGHT, p.CENTER);
        for (let t = 0; t <= 10; t++) {
            const val = t / 10;
            const y = yToPx(val), x0 = xToPx(0), x1 = x0 - 6;
            p.line(x0, y, x1, y);
            p.push();
            p.translate(x1 - 2, y);
            p.scale(1, -1); // keep text upright with our inverted y?
            p.pop();
            p.text(val.toFixed(1), x1 - 1, y - 6); // manual placement
        }

        // Axis labels
        p.textSize(12);
        p.textAlign(p.CENTER, p.BOTTOM);
        p.text('x (liquid mole fraction)', mLeft + plotWidth() / 2, yToPx(0) + 28);
        p.push();
        p.translate(xToPx(0) - 40, mTop + plotHeight() / 2);
        p.rotate(-p.HALF_PI);
        p.text('y (vapor mole fraction)', 0, 0);
        p.pop();
        p.pop();
    }

    function drawCurve(alpha) {
        p.push();
        p.stroke('#FF0000'); // red
        p.fill('transparent');
        p.strokeWeight(2);
        p.beginShape();
        for (let i = 0; i <= 250; i++) {
            const x = i / 250;
            const y = eqY(x, alpha);
            p.vertex(xToPx(x), yToPx(y));
        }
        p.endShape();
        p.pop();

        // Diagonal y = x
        p.push();
        p.stroke('#228B22'); // green
        p.strokeWeight(2);
        p.line(xToPx(0), yToPx(0), xToPx(1), yToPx(1));
        p.pop();
    }

    function drawSegment(pt1, pt2, col, weight = 2) {
        p.push();
        p.stroke(col);
        p.strokeWeight(weight);
        p.line(xToPx(pt1.x), yToPx(pt1.y), xToPx(pt2.x), yToPx(pt2.y));
        p.pop();
    }

    function drawDiagramCore(alpha, xD, xB, xF, q) {
        p.clear();
        p.background('#ffffff');
        drawAxes();
        drawCurve(alpha);

        // Intersection of q-line with eq curve
        const inter = findIntersection(alpha, xF, q);
        const xInt = inter.x, yInt = inter.y;

        // Feed (q-) line
        p.push();
        p.stroke('#0000CD'); // dark blue
        p.strokeWeight(2);
        if (Math.abs(q - 1) < 1e-10) {
            // vertical
            drawSegment({x: xF, y: xF}, {x: xInt, y: yInt}, '#0000CD', 2);
        } else if (Math.abs(q) < 1e-10) {
            // horizontal
            drawSegment({x: xF, y: xF}, {x: xInt, y: yInt}, '#0000CD', 2);
        } else {
            drawSegment({x: xF, y: xF}, {x: xInt, y: yInt}, '#0000CD', 2);
        }
        p.pop();

        // Operating lines (as segments to intersection)
        const rectStart = { x: xD, y: xD };
        const stripStart = { x: xB, y: xB };
        drawSegment(rectStart, inter, '#00FFFF', 3);   // Rectifying (cyan)
        drawSegment(stripStart, inter, '#FF00FF', 3);  // Stripping (magenta)

        // line equations for y at given x
        function lineFromTwoPoints(p1, p2) {
            const m = (p2.y - p1.y) / (p2.x - p1.x);
            const b = p1.y - m * p1.x;
            return (x) => m * x + b;
        }
        const yRect = lineFromTwoPoints(rectStart, inter);
        const yStrip = lineFromTwoPoints(stripStart, inter);

        // Stage stepping
        let stageCount = 0;
        let x = xD;
        let y = xD;
        let safety = 0;

        p.push();
        p.stroke('#000000'); // black steps
        p.strokeWeight(2);

        while (safety++ < 400) {
            // Horizontal to equilibrium: find x such that eqY(x) = y
            const xNew = invEq(y, alpha);
            // Draw horizontal step
            p.line(xToPx(x), yToPx(y), xToPx(xNew), yToPx(y));
            x = xNew;

            // Check bottom condition
            if (x <= xB + 1e-8) {
                stageCount++; // count the last partial step
                break;
            }

            // Vertical to operating line at current x
            const yOp = (x > xInt) ? yRect(x) : yStrip(x);
            p.line(xToPx(x), yToPx(y), xToPx(x), yToPx(yOp));
            y = yOp;

            stageCount++;
            // Prevent hang if geometry goes weird
            if (!isFinite(x) || !isFinite(y)) break;
            if (x < 0 || x > 1 || y < 0 || y > 1) break;
        }
        p.pop();

        // Update display
        stageDisplay.textContent = `The number of stages is ${stageCount}.`;
    }

    // Expose drawDiagram to outer scope
    window.drawDiagram = function() {
        const alpha = parseFloat(sliders.volatility.value);
        const xD = parseFloat(sliders.distillate.value);
        const xB = parseFloat(sliders.bottom.value);
        const xF = parseFloat(sliders.feedComp.value);
        const q = parseFloat(sliders.feedQuality.value);

        drawDiagramCore(alpha, xD, xB, xF, q);
    };
};
new p5(sketch, 'canvas-container');

// Initial sync
enforceConstraints();
updateValueBadges();
</script>
</body>
</html>
