  const btn2D = document.getElementById('btn-2d');
  const btn3D = document.getElementById('btn-3d');
  const canvas = document.getElementById('main-canvas');
  const container = document.getElementById('visualization-area-inner');
  const helperText = document.getElementById('helper-text');

  let currentMonth = (new Date()).getMonth(); // 0..11
  monthSlider.value = currentMonth;

  let mode = '2d'; // '2d' or '3d'

  // Keep canvas resolution in sync with CSS size
  function fitCanvasToContainer() {
    const rect = container.getBoundingClientRect();
    const dpr = window.devicePixelRatio || 1;
    const w = Math.max(300, Math.floor(rect.width));
    const h = Math.max(220, Math.floor(rect.height));
    if (canvas.width !== Math.floor(w * dpr) || canvas.height !== Math.floor(h * dpr)) {
      canvas.width = Math.floor(w * dpr);
      canvas.height = Math.floor(h * dpr);
    }
    // Scale 2D drawing context for high-DPI displays
    const ctx = canvas.getContext('2d');
    ctx.setTransform(dpr, 0, 0, dpr, 0, 0);
    ctx.clearRect(0, 0, w, h);
    return { width: w, height: h, dpr };
  }

  function setActiveButton(which) {
    if (which === '2d') {
      btn2D.classList.add('active');
      btn3D.classList.remove('active');
    } else {
      btn3D.classList.add('active');
      btn2D.classList.remove('active');
    }
  }

  function tiltRadiansForMonth(m) {
    // tilt in degrees then to radians
    const tiltDeg = 23.5 * Math.cos(2 * Math.PI * (m / 12));
    return tiltDeg * Math.PI / 180;
  }

  // =========================
  // 2D view with p5.js (offscreen)
  // We use a hidden p5 Graphics buffer and blit it onto #main-canvas
  // so both 2D and 3D can share the same DOM canvas.
  // =========================
  let p5Instance = null;
  let p5Ready = false;

  function ensureP5() {
    if (p5Instance) return;
    p5Instance = new p5(function(p) {
      let g; // offscreen graphics
      let offW = 10, offH = 10;

      p.setup = function() {
        // Create a tiny visible canvas and hide it; we only use the offscreen buffer.
        const visible = p.createCanvas(2, 2);
        visible.elt.style.display = 'none';
        // Create graphics; actual size set in updateSize
        g = p.createGraphics(offW, offH);
        p.noLoop();
        p5Ready = true;
      };

      p.updateSize = function(w, h) {
        if (!g || g.width !== w || g.height !== h) {
          g = p.createGraphics(w, h);
        }
      };

      // Main 2D drawing into offscreen graphics, then blit to #main-canvas
      p.renderFrame = function(month) {
        if (!g) return;
        const w = g.width, h = g.height;
        const cx = w * 0.5, cy = h * 0.5;
        const tilt = tiltRadiansForMonth(month);

        // Clear graphics
        g.clear();
        g.push();

        // Draw all rotating elements in a rotated coordinate system
        // Solar wind will be drawn on the main canvas (non-rotating) later.
        g.translate(cx, cy);
        g.rotate(tilt);

        // Parameters
        const scale = Math.min(w, h) / 600; // scale for responsiveness
        const earthR = 30 * scale;
        const bowShockR = 170 * scale;
        const magnetopauseR = 140 * scale;

        // Magnetotail (semi-transparent dark blue on the night side, to the right)
        g.noStroke();
        g.fill('rgba(0,0,139,0.5)');
        g.beginShape();
        g.vertex(50 * scale, -80 * scale);
        g.bezierVertex(180 * scale, -60 * scale, 320 * scale, -40 * scale, 380 * scale, -20 * scale);
        g.vertex(380 * scale, 20 * scale);
        g.bezierVertex(320 * scale, 40 * scale, 180 * scale, 60 * scale, 50 * scale, 80 * scale);
        g.endShape();

        // Magnetic field lines (grey)
        g.noFill();
        g.stroke('#808080');
        g.strokeWeight(2 * scale);
        for (let i = -2; i <= 2; i++) {
          const offset = i * 16 * scale;
          g.beginShape();
          g.vertex(-120 * scale, -80 * scale + offset);
          g.bezierVertex(-40 * scale, -100 * scale + offset, 30 * scale, -60 * scale + offset, 60 * scale, -30 * scale + offset);
          g.bezierVertex(120 * scale, 20 * scale + offset, 220 * scale, 40 * scale + offset, 380 * scale, 10 * scale + offset*0.2);
          g.endShape();
        }
        // Closed loops near Earth
        for (let r = 60 * scale; r <= 100 * scale; r += 20 * scale) {
          g.beginShape();
          g.vertex(-r*0.8, -r*0.6);
          g.bezierVertex(-r*0.3, -r*1.2, r*0.3, -r*1.2, r*0.8, -r*0.6);
          g.bezierVertex(r*1.2, 0, r*1.2, 0, r*0.8, r*0.6);
          g.bezierVertex(r*0.3, r*1.2, -r*0.3, r*1.2, -r*0.8, r*0.6);
          g.bezierVertex(-r*1.2, 0, -r*1.2, 0, -r*0.8, -r*0.6);
          g.endShape();
        }

        // Bow shock (orange)
        g.stroke('#FFA500');
        g.strokeWeight(3 * scale);
        g.noFill();
        g.beginShape();
        g.vertex(-bowShockR, -100 * scale);
        g.bezierVertex(-120 * scale, -70 * scale, -60 * scale, -40 * scale, -20 * scale, -10 * scale);
        g.bezierVertex(0, 0, 0, 0, -20 * scale, 10 * scale);
        g.bezierVertex(-60 * scale, 40 * scale, -120 * scale, 70 * scale, -bowShockR, 100 * scale);
        g.endShape();

        // Magnetopause (cyan)
        g.stroke('#00FFFF');
        g.strokeWeight(3 * scale);
        g.beginShape();
        g.vertex(-magnetopauseR, -80 * scale);
        g.bezierVertex(-90 * scale, -50 * scale, -40 * scale, -25 * scale, 20 * scale, -10 * scale);
        g.bezierVertex(40 * scale, -5 * scale, 40 * scale, 5 * scale, 20 * scale, 10 * scale);
        g.bezierVertex(-40 * scale, 25 * scale, -90 * scale, 50 * scale, -magnetopauseR, 80 * scale);
        g.endShape();

        // Van Allen belts (two crescent shapes on night side)
        g.noStroke();
        // Outer
        g.fill('#32CD32');
        g.beginShape();
        for (let a = -0.4; a <= 0.4; a += 0.02) {
          g.vertex(earthR*2 + Math.cos(a) * earthR*2.1, Math.sin(a) * earthR*1.2);
        }
        for (let a = 0.4; a >= -0.4; a -= 0.02) {
          g.vertex(earthR*2.2 + Math.cos(a) * earthR*1.2, Math.sin(a) * earthR*0.7);
        }
        g.endShape();
        // Inner
        g.fill('#006400');
        g.beginShape();
        for (let a = -0.4; a <= 0.4; a += 0.02) {
          g.vertex(earthR*1.4 + Math.cos(a) * earthR*1.5, Math.sin(a) * earthR*0.9);
        }
        for (let a = 0.4; a >= -0.4; a -= 0.02) {
          g.vertex(earthR*1.55 + Math.cos(a) * earthR*0.8, Math.sin(a) * earthR*0.5);
        }
        g.endShape();

        // Polar cusps (khaki) toward Sun on the left side
        g.fill('#F0E68C');
        g.noStroke();
        const cuspW = 28 * scale;
        const cuspH = 40 * scale;
        // Top cusp
        g.beginShape();
        g.vertex(-earthR*0.2, -earthR*1.1);
        g.vertex(-earthR*2, -earthR*2.0);
        g.vertex(-earthR*1.2, -earthR*0.2);
        g.endShape();
        // Bottom cusp
        g.beginShape();
        g.vertex(-earthR*0.2, earthR*1.1);
        g.vertex(-earthR*2, earthR*2.0);
        g.vertex(-earthR*1.2, earthR*0.2);
        g.endShape();

        // Earth (blue with simple green landmasses)
        g.fill('#4169E1');
        g.stroke('#1E3FA9');
        g.strokeWeight(2 * scale);
        g.ellipse(0, 0, earthR*2, earthR*2);

        // Landmasses (simple shapes)
        g.noStroke();
        g.fill('#228B22');
        g.beginShape(); // Africa-like blob
        g.vertex(-5*scale, -5*scale);
        g.bezierVertex(5*scale, -18*scale, 18*scale, -6*scale, 12*scale, 8*scale);
        g.bezierVertex(6*scale, 12*scale, -2*scale, 12*scale, -8*scale, 6*scale);
        g.endShape();
        g.beginShape(); // Americas-like blob
        g.vertex(-15*scale, 4*scale);
        g.bezierVertex(-22*scale, -4*scale, -16*scale, -12*scale, -8*scale, -10*scale);
        g.bezierVertex(-12*scale, 0, -10*scale, 6*scale, -15*scale, 12*scale);
        g.endShape();

        g.pop();

        // Blit rotating system from offscreen to visible canvas context
        const ctx = canvas.getContext('2d');
        // First: overall black background already set outside; we'll draw non-rotating solar wind on ctx, then offscreen content
        // Draw offscreen onto main canvas
        ctx.drawImage(g.elt, 0, 0, w, h);
      };

      // expose graphics for solar wind blend if ever needed
      p.getGraphics = () => g;
    });
  }

  function draw2D() {
    ensureP5();
    const { width, height } = fitCanvasToContainer();
    // Clear background
    const ctx = canvas.getContext('2d');
    ctx.fillStyle = '#000000';
    ctx.fillRect(0, 0, width, height);

    // Solar wind: dark red arrows from left to right, static (non-rotating)
    const lines = 11;
    ctx.strokeStyle = '#990000';
    ctx.fillStyle = '#990000';
    ctx.lineWidth = 1.5;
    for (let i = 0; i < lines; i++) {
      const y = (height * 0.1) + i * (height * 0.8) / (lines - 1);
      const xStart = -20, xEnd = width * 0.48;
      ctx.beginPath();
      ctx.moveTo(xStart, y);
      ctx.lineTo(xEnd, y);
      ctx.stroke();
      // arrowheads along the line
      const spacing = 80;
      for (let x = 20; x < xEnd - 20; x += spacing) {
        ctx.beginPath();
        ctx.moveTo(x, y);
        ctx.lineTo(x - 10, y - 4);
        ctx.lineTo(x - 10, y + 4);
        ctx.closePath();
        ctx.fill();
      }
    }

    // Update p5 offscreen buffer size and render the rotated system
    if (p5Instance && p5Instance.updateSize) {
      p5Instance.updateSize(width, height);
      p5Instance.renderFrame(currentMonth);
    }

    helperText.textContent = '2D: Drag the month slider. Solar wind (red) is fixed; the Earth–magnetosphere rotates with seasonal tilt.';
  }

  // =========================
  // 3D view with three.js
  // =========================
  let renderer, scene, camera, controls;
  let threeActive = false;

  // Objects
  let sunMesh, earthMesh, axisArrow, orbitLine, magnetosphere;
  let windArrows = [];

  function init3D() {
    if (threeActive) return;

    const { width, height, dpr } = fitCanvasToContainer();

    renderer = new THREE.WebGLRenderer({ canvas: canvas, antialias: true });
    renderer.setPixelRatio(dpr);
    renderer.setSize(width, height, false);

    scene = new THREE.Scene();
    scene.background = new THREE.Color(0x000000);

    camera = new THREE.PerspectiveCamera(45, width / height, 0.1, 2000);
    camera.position.set(0, 120, 220);

    controls = new THREE.OrbitControls(camera, renderer.domElement);
    controls.enableDamping = true;
    controls.dampingFactor = 0.08;

    // Lights
    const amb = new THREE.AmbientLight(0xffffff, 0.6);
    scene.add(amb);
    const light = new THREE.PointLight(0xffffff, 1.2, 0, 2);
    light.position.set(0, 0, 0);
    scene.add(light);

    // Sun
    const sunGeom = new THREE.SphereGeometry(18, 32, 32);
    const sunMat = new THREE.MeshBasicMaterial({ color: 0xFFFF00 });
    sunMesh = new THREE.Mesh(sunGeom, sunMat);
    sunMesh.position.set(0, 0, 0);
    scene.add(sunMesh);

    // Earth's elliptical orbit (Sun at one focus)
    const a = 160; // semi-major
    const b = 120; // semi-minor
    const c = Math.sqrt(a*a - b*b); // focus distance
    const ellipse = new THREE.EllipseCurve(-c, 0, a, b, 0, 2*Math.PI, false, 0);
    const points2 = ellipse.getPoints(256);
    const points3 = points2.map(p => new THREE.Vector3(p.x, p.y, 0));
    const orbitGeom = new THREE.BufferGeometry().setFromPoints(points3);
    const orbitMat = new THREE.LineBasicMaterial({ color: 0xFFFFFF, transparent: true, opacity: 0.6 });
    orbitLine = new THREE.LineLoop(orbitGeom, orbitMat);
    scene.add(orbitLine);

    // Earth
    const earthGeom = new THREE.SphereGeometry(8, 32, 32);
    const earthMat = new THREE.MeshStandardMaterial({ color: 0x4169E1, roughness: 0.6, metalness: 0.0 });
    earthMesh = new THREE.Mesh(earthGeom, earthMat);
    scene.add(earthMesh);

    // Earth's axis arrow (tilted 23.5 deg relative to orbital normal +Z)
    const obliquity = THREE.MathUtils.degToRad(23.5);
    const axisWorld = new THREE.Vector3(Math.sin(obliquity), 0, Math.cos(obliquity)).normalize();
    axisArrow = new THREE.ArrowHelper(axisWorld.clone(), new THREE.Vector3(0,0,0), 26, 0x00FF00, 6, 4);
    earthMesh.add(axisArrow);

    // Solar wind arrows emanating from Sun
    const radialCount = 24;
    for (let i = 0; i < radialCount; i++) {
      const ang = i * (2 * Math.PI / radialCount);
      const dir = new THREE.Vector3(Math.cos(ang), Math.sin(ang), 0).normalize();
      const origin = dir.clone().multiplyScalar(20);
      const arrow = new THREE.ArrowHelper(dir, origin, 70, 0xAA0000, 5, 3);
      arrow.cone.material.transparent = true;
      arrow.cone.material.opacity = 0.6;
      arrow.line.material.transparent = true;
      arrow.line.material.opacity = 0.6;
      scene.add(arrow);
      windArrows.push(arrow);
    }

    // Magnetosphere: LatheGeometry shape, semi-transparent, parented to Earth
    magnetosphere = createMagnetosphereMesh();
    earthMesh.add(magnetosphere);

    // Position things according to currentMonth
    update3DPositions();

    threeActive = true;
    animate3D();
    helperText.textContent = '3D: Use mouse/touch to orbit, pan, and zoom. Tail always points away from the Sun.';
  }

  function createMagnetosphereMesh() {
    // Build a teardrop-like lathe profile along the local +Y axis, then rotate so that nose is +X
    const pts = [];
    const tailL = 40;  // length toward tail (negative y)
    const noseL = 10;  // small offset for nose (positive y)
    const maxR = 20;
    // From tail (-tailL) to nose (+noseL)
    for (let t = 0; t <= 1.0; t += 0.02) {
      const y = -tailL + (tailL + noseL) * t; // -L..+nose
      // radius profile: larger near y ~ -10..+0, sharp nose near +noseL
      const r = (0.2 + 0.8 * Math.exp(-Math.pow((y + 10)/18, 2))) * maxR * (1 - 0.3 * (y + tailL)/ (tailL + noseL));
      pts.push(new THREE.Vector2(Math.max(0.5, r), y));
    }
    const lathe = new THREE.LatheGeometry(pts, 64);
    lathe.rotateZ(-Math.PI / 2); // align axis +Y -> +X (nose points +X)
    const mat = new THREE.MeshStandardMaterial({ color: 0x0000FF, transparent: true, opacity: 0.35, roughness: 0.9, metalness: 0.0, side: THREE.DoubleSide });
    const mesh = new THREE.Mesh(lathe, mat);
    mesh.position.set(0, 0, 0);
    mesh.castShadow = false;
    mesh.receiveShadow = false;
    // Scale down slightly
    mesh.scale.set(0.5, 0.5, 0.5);
    return mesh;
  }

  function update3DPositions() {
    if (!scene) return;
    // Ellipse params must match those used when building orbit
    const a = 160, b = 120, c = Math.sqrt(a*a - b*b);
    const theta = 2 * Math.PI * (currentMonth / 12); // param angle
    const x = -c + a * Math.cos(theta);
    const y = b * Math.sin(theta);
    const z = 0;
    earthMesh.position.set(x, y, z);

    // Orient magnetosphere so its +X axis points toward the Sun (tail points away)
    const toSun = new THREE.Vector3().subVectors(sunMesh.position, earthMesh.position).normalize();
    const quat = new THREE.Quaternion().setFromUnitVectors(new THREE.Vector3(1,0,0), toSun);
    magnetosphere.setRotationFromQuaternion(quat);

    // Axis arrow already set to a constant direction in Earth's local space:
    // obliquity handled by axis direction; relative orientation to Sun changes as Earth orbits.
    // Nothing else needed here.
  }

  function animate3D() {
    if (!threeActive) return;
    controls.update();
    renderer.render(scene, camera);
    requestAnimationFrame(animate3D);
  }

  function dispose3D() {
    if (!renderer) return;
    threeActive = false;
    controls && controls.dispose();
    // Free resources
    scene.traverse(obj => {
      if (obj.geometry) obj.geometry.dispose && obj.geometry.dispose();
      if (obj.material) {
        if (Array.isArray(obj.material)) obj.material.forEach(m => m.dispose && m.dispose());
        else obj.material.dispose && obj.material.dispose();
      }
    });
    renderer.dispose();
    scene = null; camera = null; controls = null;
    renderer = null;
    // Clear the canvas to black
    const { width, height } = fitCanvasToContainer();
    const ctx = canvas.getContext('2d');
    ctx.fillStyle = '#000000';
    ctx.fillRect(0, 0, width, height);
  }

  // =========================
  // Event wiring
  // =========================
  btn2D.addEventListener('click', () => {
    if (mode === '2d') return;
    // Switch to 2D view
    setActiveButton('2d');
    mode = '2d';
    dispose3D();              // stop three.js and clear
    if (p5Instance) p5Instance.noLoop(); // ensure stopped auto loop (we draw on demand)
    draw2D();                 // render with current state
  });

  btn3D.addEventListener('click', () => {
    if (mode === '3d') return;
    // Switch to 3D view
    setActiveButton('3d');
    mode = '3d';
    init3D();
  });

  // Slider change -> update month
  monthSlider.addEventListener('input', () => {
    currentMonth = parseInt(monthSlider.value, 10);
    if (mode === '2d') {
      draw2D();
    } else {
      update3DPositions();
    }
  });

  // Window resize handling
  window.addEventListener('resize', () => {
    const { width, height, dpr } = fitCanvasToContainer();
    if (mode === '2d') {
      draw2D();
    } else if (renderer && camera) {
      renderer.setPixelRatio(dpr);
      renderer.setSize(width, height, false);
      camera.aspect = width / height;
      camera.updateProjectionMatrix();
    }
  });

  // =========================
  // Initial load: 2D mode active
  // =========================
  function init() {
    setActiveButton('2d');
    draw2D();
  }
  init();

})();
</script>
</body>
</html>
