  <script>
    // Lanczos approximation for the Gamma function with reflection for z < 0.5
    function gamma(z) {
      const g = 7;
      const p = [
        0.99999999999980993,
        676.5203681218851,
        -1259.1392167224028,
        771.32342877765313,
        -176.61502916214059,
        12.507343278686905,
        -0.13857109526572012,
        9.9843695780195716e-6,
        1.5056327351493116e-7
      ];
      if (z < 0.5) {
        // Reflection formula
        return Math.PI / (Math.sin(Math.PI * z) * gamma(1 - z));
      } else {
        z -= 1;
        let x = p[0];
        for (let i = 1; i < p.length; i++) {
          x += p[i] / (z + i);
        }
        const t = z + g + 0.5;
        return Math.sqrt(2 * Math.PI) * Math.pow(t, z + 0.5) * Math.exp(-t) * x;
      }
    }

    // Gamma PDF: f(x; alpha, beta)
    function gammaPDF(x, alpha, beta) {
      if (x <= 0) return 0;
      const numerator = Math.pow(x, alpha - 1) * Math.exp(-x / beta);
      const denom = Math.pow(beta, alpha) * gamma(alpha);
      return numerator / denom;
    }

    // Prepare arrays for plotting
    function generateData(alpha, beta, xMax = 25, dx = 0.1) {
      const xs = [];
      const pdf = [];
      const cdf = [];
      const n = Math.floor(xMax / dx) + 1;

      for (let i = 0; i < n; i++) {
        const x = i * dx;
        xs.push(x);
        pdf.push(gammaPDF(x, alpha, beta));
      }

      // Trapezoidal integration for CDF
      let acc = 0;
      cdf.push(0);
      for (let i = 1; i < n; i++) {
        acc += 0.5 * (pdf[i] + pdf[i - 1]) * dx;
        cdf.push(Math.min(acc, 1)); // keep in [0,1]
      }

      return { xs, pdf, cdf };
    }

    // Update MathJax for the MGF formula
    function updateMGF(alpha, beta) {
      const mj = `$$ (1 - ${beta.toFixed(3)}\\, t)^{-\\,${alpha.toFixed(3)}} = \\frac{1}{\\big(1 - ${beta.toFixed(3)}\\, t\\big)^{${alpha.toFixed(3)}}} $$`;
      const node = document.getElementById('mgf-formula');
      node.innerHTML = mj;
      if (window.MathJax && MathJax.typesetPromise) {
        MathJax.typesetPromise([node]);
      }
    }

    // Plot initialization and updates
    function renderPlot(alpha, beta) {
      const { xs, pdf, cdf } = generateData(alpha, beta);
      const mean = alpha * beta;
      const yMean = gammaPDF(mean, alpha, beta);

      const pdfTrace = {
        type: 'scatter',
        mode: 'lines',
        x: xs,
        y: pdf,
        line: { color: 'blue', width: 2.5 },
        hovertemplate: 'x=%{x:.2f}<br>pdf=%{y:.4f}<extra></extra>'
      };

      const cdfTrace = {
        type: 'scatter',
        mode: 'lines',
        x: xs,
        y: cdf,
        line: { color: 'orange', width: 2.5 },
        hovertemplate: 'x=%{x:.2f}<br>cdf=%{y:.4f}<extra></extra>'
      };

      const layout = {
        xaxis: { range: [0, 25], title: '' },
        yaxis: { range: [0, 1.1], title: '' },
        showlegend: false,
        margin: { l: 40, r: 15, t: 10, b: 40 },
        shapes: [
          {
            type: 'line',
            x0: mean,
            x1: mean,
            y0: 0,
            y1: Math.min(yMean, 1.05),
            line: { color: 'red', width: 3 }
          }
        ]
      };

      const config = { responsive: true, displayModeBar: false };

      Plotly.react('plot-container', [pdfTrace, cdfTrace], layout, config);
    }

    // Update everything when sliders move
    function updateAll() {
      const alpha = parseFloat(document.getElementById('slider-alpha').value);
      const beta = parseFloat(document.getElementById('slider-beta').value);

      // Update numeric displays
      document.getElementById('display-alpha').textContent = alpha.toFixed(3);
      document.getElementById('display-beta').textContent = beta.toFixed(3);

      // Calculations
      const mean = alpha * beta;
      const variance = alpha * beta * beta;

      // Update stats
      document.getElementById('mean-value').textContent = mean.toFixed(3);
      document.getElementById('variance-value').textContent = variance.toFixed(3);

      // Update MGF formula (MathJax)
      updateMGF(alpha, beta);

      // Update Plot
      renderPlot(alpha, beta);
    }

    // Initialize with defaults on window load
    window.addEventListener('load', () => {
      // Initial MGF and plot based on default slider values
      updateAll();

      // Event listeners
      document.getElementById('slider-alpha').addEventListener('input', updateAll);
      document.getElementById('slider-beta').addEventListener('input', updateAll);
    });
  </script>
</body>
</html>
