<!DOCTYPE html>
<html lang="en">
<head>
  <meta charset="UTF-8" />
  <title>Interactive Distillation Boundary with Locator (p5.js)</title>
  <meta name="viewport" content="width=device-width, initial-scale=1" />
  <style>
    body {
      margin: 0;
      font-family: Arial, Helvetica, sans-serif;
      background: #f7f7f7;
    }
    #canvas-container {
      width: 650px;
      margin: 24px auto;
      display: flex;
      justify-content: center;
      align-items: center;
    }
    /* Give the canvas a border as requested */
    #visualization-canvas {
      border: 1px solid #cccccc;
      background: #ffffff;
      cursor: crosshair;
      display: block;
    }
  </style>
</head>
<body>
  <div id="canvas-container">
    <!-- Placeholder canvas with the exact required ID.
         In setup(), p5 will replace it with its own canvas using the same ID. -->
    <canvas id="visualization-canvas" width="650" height="650"></canvas>
  </div>

  <!-- p5.js CDN -->
  <script src="https://cdn.jsdelivr.net/npm/p5@1.4.0/lib/p5.js"></script>

  <!-- Application Code -->
  <script>
    // Canvas and plot layout
    const CANVAS_SIZE = 650;
    const MARGIN = 60;

    // Azeotrope points (data coordinates)
    const A1 = { x: 0.655, y: 0.0 };
    const A2 = { x: 0.0,   y: 0.795 };
    const A3 = { x: 0.605, y: 0.345 };
    const A4 = { x: 0.26,  y: 0.335 };

    // Distillation boundary polyline constraint (A2 -> A4 -> A1)
    const boundaryPolyline = [A2, A4, A1];

    // Draggable Locator logical component
    const locator = {
      x: 0.2,   // chloroform mole fraction
      y: 0.22,  // acetone mole fraction
      isDragging: false
    };

    // Integration parameters
    const stepSize = 0.005;
    const stepsEachWay = 250;

    // Utility: clamp value
    function clamp(v, a, b) {
      return Math.max(a, Math.min(b, v));
    }

    // Check if a data point is inside the valid triangular region
    function inTriangle(x, y) {
      return x > 0 && y > 0 && (x + y) < 1;
    }

    // Mapping functions between data coordinates (0..1) and canvas pixels
    function plotWidth() {
      return CANVAS_SIZE - 2 * MARGIN;
    }
    function plotHeight() {
      return CANVAS_SIZE - 2 * MARGIN;
    }

    function dataToPixel(x, y) {
      const px = MARGIN + x * plotWidth();
      const py = CANVAS_SIZE - MARGIN - y * plotHeight();
      return { x: px, y: py };
    }

    function pixelToData(px, py) {
      const x = (px - MARGIN) / plotWidth();
      const y = (CANVAS_SIZE - MARGIN - py) / plotHeight();
      return { x, y };
    }

    // Compute closest point on a line segment AB to point P (all in data coordinates)
    function closestPointOnSegment(P, A, B) {
      const vx = B.x - A.x;
      const vy = B.y - A.y;
      const len2 = vx * vx + vy * vy;
      let t = 0;
      if (len2 > 0) {
        t = ((P.x - A.x) * vx + (P.y - A.y) * vy) / len2;
      }
      t = clamp(t, 0, 1);
      return {
        x: A.x + t * vx,
        y: A.y + t * vy,
        t
      };
    }

    // Compute closest point on the polyline (A2->A4->A1)
    function closestPointOnPolyline(P, poly) {
      let best = null;
      let bestDist2 = Infinity;
      for (let i = 0; i < poly.length - 1; i++) {
        const A = poly[i];
        const B = poly[i + 1];
        const C = closestPointOnSegment(P, A, B);
        const dx = P.x - C.x;
        const dy = P.y - C.y;
        const d2 = dx * dx + dy * dy;
        if (d2 < bestDist2) {
          bestDist2 = d2;
          best = { x: C.x, y: C.y, segIndex: i, t: C.t };
        }
      }
      return best;
    }

    // Vector field as specified (STRICT implementation)
    // x: chloroform mole fraction, y: acetone mole fraction
    function getVector(x, y) {
      const A3p = {x: 0.605, y: 0.345}; // Unstable node (repeller)
      const M = {x: 0, y: 0};           // Stable node (attractor)

      // Vector from repeller to current point
      let v_repel_x = x - A3p.x;
      let v_repel_y = y - A3p.y;
      // Vector from current point to attractor
      let v_attract_x = M.x - x;
      let v_attract_y = M.y - y;

      // Normalize influence by distance squared, add epsilon to avoid division by zero
      let dist_repel_sq = v_repel_x * v_repel_x + v_repel_y * v_repel_y + 0.01;
      let dist_attract_sq = v_attract_x * v_attract_x + v_attract_y * v_attract_y + 0.01;

      // Weighted sum of influences
      let dx = (v_repel_x / dist_repel_sq) * 0.05 + (v_attract_x / dist_attract_sq) * 0.03;
      let dy = (v_repel_y / dist_repel_sq) * 0.05 + (v_attract_y / dist_attract_sq) * 0.03;

      // Add a small rotational field to shape the curves
      dx += -0.1 * y;
      dy += 0.1 * x;

      return { dx, dy };
    }

    // Integrate forward/backward using Euler method
    function integrateCurve(x0, y0, dir) {
      const pts = [];
      let x = x0;
      let y = y0;
      for (let i = 0; i < stepsEachWay; i++) {
        const v = getVector(x, y);
        x = x + dir * v.dx * stepSize;
        y = y + dir * v.dy * stepSize;
        if (!inTriangle(x, y)) break;
        pts.push({ x, y });
      }
      return pts;
    }

    // p5.js sketch
    function setup() {
      // Remove the placeholder canvas to avoid duplicate IDs, then create p5 canvas with same ID.
      const placeholder = document.getElementById('visualization-canvas');
      if (placeholder) {
        placeholder.remove();
      }
      const cnv = createCanvas(CANVAS_SIZE, CANVAS_SIZE);
      cnv.parent('canvas-container');
      cnv.id('visualization-canvas');

      textFont('Arial');
      textSize(12);
    }

    function draw() {
      background('#FFFFFF');

      drawAxesAndTicks();
      drawHypotenuse();
      drawComponentLabels();
      drawAzeotropes();

      // Draw residue curve (distillation line) from current locator
      drawResidueCurve(locator.x, locator.y);

      // Draw locator on top
      drawLocator();

      // Optional: ensure the locator stays visible within plotting area (not required but safe)
    }

    function drawAxesAndTicks() {
      stroke(0);
      strokeWeight(1);
      noFill();
      // X axis (y=0): from (0,0) to (1,0)
      let p0 = dataToPixel(0, 0);
      let p1 = dataToPixel(1, 0);
      line(p0.x, p0.y, p1.x, p1.y);

      // Y axis (x=0): from (0,0) to (0,1)
      p1 = dataToPixel(0, 1);
      line(p0.x, p0.y, p1.x, p1.y);

      // Ticks and labels
      const tickLen = 6;
      fill(0);
      noStroke();
      textSize(12);

      // x-axis ticks and labels
      for (let i = 0; i <= 5; i++) {
        const val = i * 0.2;
        const pt = dataToPixel(val, 0);
        stroke(0);
        line(pt.x, pt.y, pt.x, pt.y - tickLen); // ticks into the plot area
        noStroke();
        textAlign(CENTER, TOP);
        text(val.toFixed(1), pt.x, pt.y + 2); // below axis
      }

      // y-axis ticks and labels
      for (let i = 0; i <= 5; i++) {
        const val = i * 0.2;
        const pt = dataToPixel(0, val);
        stroke(0);
        line(pt.x, pt.y, pt.x + tickLen, pt.y); // ticks into the plot area
        noStroke();
        textAlign(RIGHT, CENTER);
        text(val.toFixed(1), pt.x - 6, pt.y); // left of axis
      }
    }

    function drawHypotenuse() {
      stroke(0);
      strokeWeight(1);
      const pA = dataToPixel(1, 0);
      const pB = dataToPixel(0, 1);
      line(pA.x, pA.y, pB.x, pB.y);
    }

    function drawComponentLabels() {
      fill('#FF0000'); // red
      noStroke();
      textSize(16);
      textAlign(LEFT, TOP);
      // "methanol" near origin (0,0)
      const m = dataToPixel(0, 0);
      text('methanol', m.x - 4, m.y + 16);

      // "chloroform" near (1,0)
      const c = dataToPixel(1, 0);
      textAlign(RIGHT, TOP);
      text('chloroform', c.x - 2, c.y + 16);

      // "acetone" near (0,1)
      const a = dataToPixel(0, 1);
      textAlign(LEFT, TOP);
      text('acetone', a.x - 4, a.y - 26);
    }

    function drawAzeotropes() {
      const azeos = [
        { p: A1, label: 'A₁', dx: 12, dy: 10 },
        { p: A2, label: 'A₂', dx: -28, dy: -6 },
        { p: A3, label: 'A₃', dx: 12, dy: -10 },
        { p: A4, label: 'A₄', dx: -18, dy: -10 }
      ];

      for (const a of azeos) {
        const pp = dataToPixel(a.p.x, a.p.y);
        noStroke();
        fill('#990000');
        circle(pp.x, pp.y, 12); // radius 6px (diameter 12)
        fill(0);
        textSize(14);
        textAlign(LEFT, CENTER);
        text(a.label, pp.x + a.dx, pp.y + a.dy);
      }
    }

    function drawResidueCurve(x0, y0) {
      // Backward and forward integration
      const back = integrateCurve(x0, y0, -1);
      const fwd = integrateCurve(x0, y0, 1);

      // Build full curve: reverse(back), start, fwd
      const allPts = [];
      for (let i = back.length - 1; i >= 0; i--) allPts.push(back[i]);
      allPts.push({ x: x0, y: y0 });
      for (let i = 0; i < fwd.length; i++) allPts.push(fwd[i]);

      // Render as a smooth blue line
      noFill();
      stroke('#0000FF');
      strokeWeight(2);

      beginShape();
      // For curveVertex, duplicate first and last for smoother ends
      if (allPts.length > 0) {
        const first = dataToPixel(allPts[0].x, allPts[0].y);
        curveVertex(first.x, first.y);
      }
      for (let i = 0; i < allPts.length; i++) {
        const p = dataToPixel(allPts[i].x, allPts[i].y);
        curveVertex(p.x, p.y);
      }
      if (allPts.length > 0) {
        const last = dataToPixel(allPts[allPts.length - 1].x, allPts[allPts.length - 1].y);
        curveVertex(last.x, last.y);
      }
      endShape();
    }

    function drawLocator() {
      const p = dataToPixel(locator.x, locator.y);
      stroke(0);
      strokeWeight(2);
      fill('#FFFFFF');
      circle(p.x, p.y, 16); // radius 8 (diameter 16)
    }

    // Interaction handlers
    function mousePressed() {
      // Check if click is near the locator
      const p = dataToPixel(locator.x, locator.y);
      const d2 = (mouseX - p.x) * (mouseX - p.x) + (mouseY - p.y) * (mouseY - p.y);
      const r = 15;
      if (d2 <= r * r) {
        locator.isDragging = true;
      }
    }

    function mouseDragged() {
      if (!locator.isDragging) return;

      // Convert mouse pixel to data coords
      const md = pixelToData(mouseX, mouseY);

      // Constrain to boundary polyline
      const cp = closestPointOnPolyline(md, boundaryPolyline);
      if (cp) {
        locator.x = clamp(cp.x, 0, 1);
        locator.y = clamp(cp.y, 0, 1);
      }
    }

    function mouseReleased() {
      locator.isDragging = false;
    }
  </script>
</body>
</html>
