    min-height: 420px;
    background: #000;
    border: 1px solid #cfcfcf;
    border-radius: 4px;
    overflow: hidden;
  }
</style>
</head>
<body>
  <div id="main-container">
    <div id="control-panel">
      <div class="panel-title">lengths of semi-axes</div>

      <div id="slider-group-a" class="row">
        <label for="slider-a">a</label>
        <input type="range" id="slider-a" min="0.1" max="5" step="0.1" value="1.5">
        <input type="number" id="input-a" min="0.1" max="5" step="0.1" value="1.5">
      </div>

      <div id="slider-group-b" class="row">
        <label for="slider-b">b</label>
        <input type="range" id="slider-b" min="0.1" max="5" step="0.1" value="1.5">
        <input type="number" id="input-b" min="0.1" max="5" step="0.1" value="1.5">
      </div>

      <div id="slider-group-c" class="row">
        <label for="slider-c">c</label>
        <input type="range" id="slider-c" min="0.1" max="5" step="0.1" value="1.0">
        <input type="number" id="input-c" min="0.1" max="5" step="0.1" value="1.0">
      </div>
    </div>

    <div id="canvas-container"></div>
  </div>

  <!-- three.js and helpers -->
  <script src="https://unpkg.com/three@0.128.0/build/three.min.js"></script>
  <script src="https://unpkg.com/three@0.128.0/examples/js/controls/OrbitControls.js"></script>
  <script src="https://unpkg.com/three@0.128.0/examples/js/geometries/ParametricGeometry.js"></script>

  <script>
    // DOM references
    const container = document.getElementById('canvas-container');
    const sliderA = document.getElementById('slider-a');
    const inputA  = document.getElementById('input-a');
    const sliderB = document.getElementById('slider-b');
    const inputB  = document.getElementById('input-b');
    const sliderC = document.getElementById('slider-c');
    const inputC  = document.getElementById('input-c');

    // Three.js basics
    let scene, camera, renderer, controls, paraboloidGroup;

    init();
    animate();

    function init() {
      scene = new THREE.Scene();
      scene.background = new THREE.Color(0x000000);

      const fov = 45;
      const aspect = container.clientWidth / container.clientHeight;
      camera = new THREE.PerspectiveCamera(fov, aspect, 0.1, 1000);
      camera.position.set(5, 5, 5);
      camera.lookAt(0, 0, 0);

      renderer = new THREE.WebGLRenderer({ antialias: true });
      renderer.setPixelRatio(Math.min(window.devicePixelRatio, 2));
      renderer.setSize(container.clientWidth, container.clientHeight);
      container.appendChild(renderer.domElement);

      controls = new THREE.OrbitControls(camera, renderer.domElement);
      controls.enableDamping = true;

      // Lights
      scene.add(new THREE.AmbientLight(0x404040, 1.2));
      const dir = new THREE.DirectionalLight(0xffffff, 1.0);
      dir.position.set(3, 6, 4);
      scene.add(dir);

      // Axes as light grey lines from -5 to +5
      addAxes(5);

      // Initial surface
      rebuildParaboloid(getValues());

      // Events
      window.addEventListener('resize', onWindowResize);

      // Synchronize sliders and number inputs
      linkControls(sliderA, inputA, onParamsChanged);
      linkControls(sliderB, inputB, onParamsChanged);
      linkControls(sliderC, inputC, onParamsChanged);
    }

    function onWindowResize() {
      const w = container.clientWidth;
      const h = container.clientHeight;
      camera.aspect = w / h;
      camera.updateProjectionMatrix();
      renderer.setSize(w, h);
    }

    function addAxes(L=5) {
      const material = new THREE.LineBasicMaterial({ color: 0x888888, linewidth: 1 });
      // X axis
      const geomX = new THREE.BufferGeometry().setFromPoints([
        new THREE.Vector3(-L, 0, 0),
        new THREE.Vector3( L, 0, 0)
      ]);
      // Y axis
      const geomY = new THREE.BufferGeometry().setFromPoints([
        new THREE.Vector3(0, -L, 0),
        new THREE.Vector3(0,  L, 0)
      ]);
      // Z axis
      const geomZ = new THREE.BufferGeometry().setFromPoints([
        new THREE.Vector3(0, 0, -L),
        new THREE.Vector3(0, 0,  L)
      ]);

      scene.add(new THREE.Line(geomX, material));
      scene.add(new THREE.Line(geomY, material));
      scene.add(new THREE.Line(geomZ, material));
    }

    function getValues() {
      return {
        a: parseFloat(sliderA.value),
        b: parseFloat(sliderB.value),
        c: parseFloat(sliderC.value)
      };
    }

    function onParamsChanged() {
      rebuildParaboloid(getValues());
    }

    function linkControls(rangeEl, numberEl, onChange) {
      const min = parseFloat(rangeEl.min), max = parseFloat(rangeEl.max);
      function clamp(v) { return Math.min(max, Math.max(min, v)); }
      rangeEl.addEventListener('input', () => {
        numberEl.value = rangeEl.value;
        onChange();
      });
      numberEl.addEventListener('input', () => {
        const v = clamp(parseFloat(numberEl.value || rangeEl.value));
        numberEl.value = v.toFixed(1);
        rangeEl.value = v;
        onChange();
      });
    }

    function rebuildParaboloid({a, b, c}) {
      if (paraboloidGroup) {
        scene.remove(paraboloidGroup);
        // Dispose geometries/materials
        paraboloidGroup.traverse(obj => {
          if (obj.isMesh) {
            if (obj.geometry) obj.geometry.dispose();
            if (obj.material) {
              if (Array.isArray(obj.material)) obj.material.forEach(m => m.dispose());
              else obj.material.dispose();
            }
          }
        });
        paraboloidGroup = null;
      }

      paraboloidGroup = new THREE.Group();

      const range = 2.0;
      const slices = 50, stacks = 50;

      // Parametric function
      const paraboloidFunc = (u, v, target) => {
        const angle = u * Math.PI * 2.0;
        const r = v * range;
        const x = a * r * Math.cos(angle);
        const y = b * r * Math.sin(angle);
        const z = c * (r * r);
        target.set(x, y, z);
      };

      const geometrySolid = new THREE.ParametricGeometry(paraboloidFunc, slices, stacks);
      applyHeightColors(geometrySolid, c, range);

      const solidMat = new THREE.MeshPhongMaterial({
        side: THREE.DoubleSide,
        vertexColors: true,
        shininess: 70,
        specular: 0x222222
      });
      const solidMesh = new THREE.Mesh(geometrySolid, solidMat);

      // Wireframe overlay - clone geometry so disposal is simple
      const geometryWire = geometrySolid.clone();
      const wireMat = new THREE.MeshBasicMaterial({
        wireframe: true,
        color: 0xcccccc
      });
      const wireMesh = new THREE.Mesh(geometryWire, wireMat);

      paraboloidGroup.add(solidMesh);
      paraboloidGroup.add(wireMesh);
      scene.add(paraboloidGroup);
    }

    function applyHeightColors(geometry, c, range) {
      geometry.computeVertexNormals?.();

      // BufferGeometry expected
      const pos = geometry.attributes.position;
      const count = pos.count;
      const colors = new Float32Array(count * 3);

      const zMax = c * (range * range);
      const cLow = new THREE.Color(0xFFD700); // bright yellow/orange
      const cHigh = new THREE.Color(0xFF4500); // darker orange/red
      const tmp = new THREE.Color();

      for (let i = 0; i < count; i++) {
        const z = pos.getZ(i);
        const t = zMax > 0 ? Math.min(1, Math.max(0, z / zMax)) : 0;
        tmp.copy(cLow).lerp(cHigh, t);
        colors[i*3 + 0] = tmp.r;
        colors[i*3 + 1] = tmp.g;
        colors[i*3 + 2] = tmp.b;
      }
      geometry.setAttribute('color', new THREE.Float32BufferAttribute(colors, 3));
    }

    function animate() {
      requestAnimationFrame(animate);
      controls.update();
      renderer.render(scene, camera);
    }
  </script>
</body>
