<!-- Three.js and app code -->
<script type="module">
import * as THREE from 'https://cdn.jsdelivr.net/npm/three@0.158.0/build/three.module.js';
import { OrbitControls } from 'https://cdn.jsdelivr.net/npm/three@0.158.0/examples/jsm/controls/OrbitControls.js';

const state = {
  j1: 1.5,
  m1: 0.5,
  j2: 1.0,
  m2: 0.0,
  j: 2.5,
  isAnimating: false,
  phiZ: 0,
  phiJ: 0
};

let scene, camera, renderer, controls;
let vectorsGroup, conesGroup;
let jArrow = null, j1Arrow = null, j2Arrow = null;
let jCone = null, j1Cone = null, j2Cone = null;
let coupledMode = true;

const canvas = document.getElementById('main-canvas');
const btnPlayPause = document.getElementById('btn-play-pause');
const btnReset = document.getElementById('btn-reset');

function clamp(x, a=-1, b=1){ return Math.min(b, Math.max(a, x)); }
function L(q){ return Math.sqrt(q*(q+1)); }
function toFracLabel(x){
  // Represent numbers as integers or n/2
  const n2 = Math.round(x*2);
  const err = Math.abs(x*2 - n2);
  if (err > 1e-9) return x.toFixed(2);
  if (n2 === 0) return "0";
  if (n2 % 2 === 0) return String(n2/2);
  return (n2<0 ? "-" : "") + String(Math.abs(n2)) + "/2";
}
function defaultMFor(j){
  // smallest non-negative in {-j, -j+1, ..., j}
  const isInteger = Math.round(j*2) % 2 === 0;
  return isInteger ? 0 : 0.5;
}
function clearChildren(node){ while(node.firstChild) node.removeChild(node.firstChild); }
function setSelected(container, valueStr){
  [...container.querySelectorAll('button')].forEach(b=>{
    b.classList.toggle('selected', b.dataset.value === String(valueStr));
  });
}
function makeChip(value, label, onClick){
  const btn = document.createElement('button');
  btn.className = 'btn-chip';
  btn.dataset.value = String(value);
  btn.textContent = label ?? toFracLabel(Number(value));
  btn.addEventListener('click', onClick);
  return btn;
}
function populateMSelector(containerId, jVal, selectedVal){
  const container = document.getElementById(containerId);
  clearChildren(container);
  const start = -Math.round(jVal*2);
  const end = Math.round(jVal*2);
  for(let n2=start; n2<=end; n2+=2){ // step 1 in m -> 2 in half-steps
    const m = n2/2;
    const btn = makeChip(m, toFracLabel(m), (e)=>{
      const val = Number(e.currentTarget.dataset.value);
      if(containerId === 'm1-selector'){ state.m1 = val; }
      else { state.m2 = val; }
      setSelected(container, String(val));
      resetAnimation();
      rebuildScene();
    });
    container.appendChild(btn);
  }
  const pick = (selectedVal !== undefined) ? selectedVal : defaultMFor(jVal);
  setSelected(container, String(pick));
  if(containerId === 'm1-selector'){ state.m1 = pick; } else { state.m2 = pick; }
}

function populateJSelector(selectedVal){
  const container = document.getElementById('j-selector');
  clearChildren(container);
  const jmin = Math.abs(state.j1 - state.j2);
  const jmax = state.j1 + state.j2;
  // values in steps of 1 (i.e., every 2 in half-steps)
  const start = Math.round(jmin*2);
  const end = Math.round(jmax*2);
  for(let n2=start; n2<=end; n2+=2){
    const j = n2/2;
    const btn = makeChip(j, toFracLabel(j), (e)=>{
      state.j = Number(e.currentTarget.dataset.value);
      setSelected(container, String(state.j));
      resetAnimation();
      rebuildScene();
    });
    container.appendChild(btn);
  }
  const pick = (selectedVal !== undefined) ? selectedVal : jmax; // largest possible by default
  setSelected(container, String(pick));
  state.j = pick;
}

function attachStaticJButtons(){
  const j1Sel = document.getElementById('j1-selector');
  const j2Sel = document.getElementById('j2-selector');
  j1Sel.addEventListener('click', (e)=>{
    const b = e.target.closest('button'); if(!b) return;
    state.j1 = Number(b.dataset.value);
    setSelected(j1Sel, b.dataset.value);
    // repopulate dependent selectors
    populateMSelector('m1-selector', state.j1, defaultMFor(state.j1));
    populateJSelector(); // uses current j1 and j2
    resetAnimation();
    rebuildScene();
  });
  j2Sel.addEventListener('click', (e)=>{
    const b = e.target.closest('button'); if(!b) return;
    state.j2 = Number(b.dataset.value);
    setSelected(j2Sel, b.dataset.value);
    populateMSelector('m2-selector', state.j2, defaultMFor(state.j2));
    populateJSelector();
    resetAnimation();
    rebuildScene();
  });
}

function initThree(){
  scene = new THREE.Scene();
  scene.background = new THREE.Color(0xffffff);

  const aspect = canvas.clientWidth / canvas.clientHeight;
  camera = new THREE.PerspectiveCamera(45, aspect || 1, 0.1, 1000);
  camera.up.set(0,0,1);
  camera.position.set(6, 6, 5);
  camera.lookAt(0,0,0);

  renderer = new THREE.WebGLRenderer({canvas, antialias:true});
  renderer.setPixelRatio(Math.min(window.devicePixelRatio, 2));
  resizeRenderer();

  // Lighting
  const amb = new THREE.AmbientLight(0xffffff, 0.7);
  const dir = new THREE.DirectionalLight(0xffffff, 0.65);
  dir.position.set(5, 5, 8);
  scene.add(amb, dir);

  // Ground frame (thin)
  const grid = new THREE.GridHelper(8, 16, 0x888888, 0xdddddd);
  grid.rotation.x = Math.PI/2; // put on Z=0 plane (since Z is up)
  scene.add(grid);

  // Groups to hold current vectors/cones
  vectorsGroup = new THREE.Group();
  conesGroup = new THREE.Group();
  scene.add(conesGroup);
  scene.add(vectorsGroup);

  controls = new OrbitControls(camera, renderer.domElement);
  controls.enableDamping = true;

  window.addEventListener('resize', resizeRenderer);
  animate();
}

function resizeRenderer(){
  const rect = canvas.getBoundingClientRect();
  const width = rect.width, height = rect.height;
  renderer.setSize(width, height, false);
  camera.aspect = width / Math.max(1, height);
  camera.updateProjectionMatrix();
}

function resetAnimation(){
  state.isAnimating = false;
  state.phiZ = 0;
  state.phiJ = 0;
  btnPlayPause.textContent = '▶';
}

function removeObject(obj){
  if(!obj) return;
  if(obj.parent) obj.parent.remove(obj);
  obj.traverse?.((c)=>{
    if(c.geometry) c.geometry.dispose();
    if(c.material){
      if(Array.isArray(c.material)) c.material.forEach(m=>m.dispose());
      else c.material.dispose();
    }
  });
}

function rebuildScene(){
  // clear old
  vectorsGroup.clear();
  conesGroup.clear();
  jArrow = j1Arrow = j2Arrow = null;
  jCone = j1Cone = j2Cone = null;

  // Decide model
  coupledMode = (state.j >= state.j1 && state.j >= state.j2);

  const L1 = L(state.j1);
  const L2 = L(state.j2);

  const arrowHead = 0.17; // head length factor
  const arrowWidth = 0.05; // head width factor

  if(coupledMode){
    // J arrow
    const LJ = L(state.j);
    jArrow = new THREE.ArrowHelper(new THREE.Vector3(0,0,1), new THREE.Vector3(0,0,0), LJ, 0x000000, arrowHead*LJ, arrowWidth*LJ);
    vectorsGroup.add(jArrow);

    // J1 & J2 arrows (colors)
    j1Arrow = new THREE.ArrowHelper(new THREE.Vector3(1,0,0), new THREE.Vector3(0,0,0), L1, 0xd22, arrowHead*L1, arrowWidth*L1);
    j2Arrow = new THREE.ArrowHelper(new THREE.Vector3(-1,0,0), new THREE.Vector3(0,0,0), L2, 0x06f, arrowHead*L2, arrowWidth*L2);
    vectorsGroup.add(j1Arrow, j2Arrow);

    // Cones
    // J cone around Z
    {
      const m = state.m1 + state.m2;
      const thetaJ = Math.acos(clamp(m / LJ));
      const r = LJ * Math.tan(thetaJ);
      const geom = new THREE.ConeGeometry(Math.abs(r), LJ, 64, 1, true);
      geom.translate(0, -LJ/2, 0); // apex at origin (along +Y)
      const mat = new THREE.MeshStandardMaterial({color:0x6b5b53, transparent:true, opacity:0.35, roughness:0.8, side:THREE.DoubleSide});
      jCone = new THREE.Mesh(geom, mat);
      // orient from +Y to +Z
      jCone.quaternion.setFromUnitVectors(new THREE.Vector3(0,1,0), new THREE.Vector3(0,0,1));
      conesGroup.add(jCone);
    }
    // J1 & J2 cones aligned with J axis; built with local +Z as the J axis
    const alpha1 = Math.acos(clamp((L1*L1 + LJ*LJ - L2*L2)/(2*L1*LJ)));
    const alpha2 = Math.acos(clamp((L2*L2 + LJ*LJ - L1*L1)/(2*L2*LJ)));
    j1Cone = makeConeAlongZ(L1, alpha1, 0xd22);
    j2Cone = makeConeAlongZ(L2, alpha2, 0x06f);
    conesGroup.add(j1Cone, j2Cone);
  } else {
    // Uncoupled model: only J1 and J2
    j1Arrow = new THREE.ArrowHelper(new THREE.Vector3(0,0,1), new THREE.Vector3(0,0,0), L1, 0xd22, arrowHead*L1, arrowWidth*L1);
    j2Arrow = new THREE.ArrowHelper(new THREE.Vector3(0,0,1), new THREE.Vector3(0,0,0), L2, 0x06f, arrowHead*L2, arrowWidth*L2);
    vectorsGroup.add(j1Arrow, j2Arrow);

    // Cones around Z for J1 and J2
    const theta1 = Math.acos(clamp(state.m1 / L1));
    const theta2 = Math.acos(clamp(state.m2 / L2));
    j1Cone = makeZAxisCone(L1, theta1, 0xd22);
    j2Cone = makeZAxisCone(L2, theta2, 0x06f);
    conesGroup.add(j1Cone, j2Cone);
  }

  // ensure a first static update for arrows
  updateVectors(0);
}

function makeConeAlongZ(height, halfAngle, color){
  const r = height * Math.tan(halfAngle);
  const geom = new THREE.ConeGeometry(Math.abs(r), height, 64, 1, true);
  geom.translate(0, -height/2, 0); // apex at origin (along +Y)
  const mat = new THREE.MeshStandardMaterial({color, transparent:true, opacity:0.28, roughness:0.8, side:THREE.DoubleSide});
  const cone = new THREE.Mesh(geom, mat);
  // rotate +Y to +Z
  cone.quaternion.setFromUnitVectors(new THREE.Vector3(0,1,0), new THREE.Vector3(0,0,1));
  return cone;
}
function makeZAxisCone(height, halfAngle, color){
  // identical to makeConeAlongZ; alias for clarity
  return makeConeAlongZ(height, halfAngle, color);
}

function sphDir(theta, phi){
  const st = Math.sin(theta);
  return new THREE.Vector3(st*Math.cos(phi), st*Math.sin(phi), Math.cos(theta));
}

function updateVectors(dt){
  if(coupledMode){
    const L1 = L(state.j1), L2 = L(state.j2), LJ = L(state.j);
    const m = state.m1 + state.m2;
    const thetaJ = Math.acos(clamp(m / LJ));
    // J direction from spherical (thetaJ, phiZ)
    const jDir = sphDir(thetaJ, state.phiZ).normalize();

    // Update J arrow
    jArrow.setDirection(jDir);
    jArrow.setLength(LJ);

    // Rotate J-aligned cones to follow J orientation (but independent of phiJ)
    const qJ = new THREE.Quaternion().setFromUnitVectors(new THREE.Vector3(0,0,1), jDir);
    j1Cone.quaternion.copy(qJ);
    j2Cone.quaternion.copy(qJ);

    // Angles between J and J1/J2 from the triangle of magnitudes
    const alpha1 = Math.acos(clamp((L1*L1 + LJ*LJ - L2*L2)/(2*L1*LJ)));
    const alpha2 = Math.acos(clamp((L2*L2 + LJ*LJ - L1*L1)/(2*L2*LJ)));

    // J1 and J2 in J's local frame (z' || J). Use azimuths φ and φ+π to ensure J1+J2 || J.
    const v1_local = sphDir(alpha1, state.phiJ).multiplyScalar(L1);
    const v2_local = sphDir(alpha2, state.phiJ + Math.PI).multiplyScalar(L2);

    const v1_world = v1_local.clone().applyQuaternion(qJ);
    const v2_world = v2_local.clone().applyQuaternion(qJ);

    j1Arrow.setDirection(v1_world.clone().normalize());
    j1Arrow.setLength(L1);
    j2Arrow.setDirection(v2_world.clone().normalize());
    j2Arrow.setLength(L2);
  }else{
    // Uncoupled: both precess independently around Z with fixed polar angles
    const L1 = L(state.j1), L2 = L(state.j2);
    const theta1 = Math.acos(clamp(state.m1 / L1));
    const theta2 = Math.acos(clamp(state.m2 / L2));

    const dir1 = sphDir(theta1, state.phiZ);
    const dir2 = sphDir(theta2, state.phiZ + 0.0); // same frequency; could shift if desired

    j1Arrow.setDirection(dir1);
    j1Arrow.setLength(L1);
    j2Arrow.setDirection(dir2);
    j2Arrow.setLength(L2);
  }
}

function animate(){
  requestAnimationFrame(animate);
  if(state.isAnimating){
    state.phiZ += 0.012;
    state.phiJ += 0.028;
  }
  updateVectors(0);
  controls.update();
  renderer.render(scene, camera);
}

/* UI wiring */
attachStaticJButtons();

// Initialize with required initial state:
setSelected(document.getElementById('j1-selector'), String(state.j1));
setSelected(document.getElementById('j2-selector'), String(state.j2));
populateMSelector('m1-selector', state.j1, state.m1); // j1=1.5 -> m options -1.5 -0.5 0.5 1.5, select 0.5
populateMSelector('m2-selector', state.j2, state.m2); // j2=1 -> m options -1, 0, 1, select 0
populateJSelector(state.j); // with j1=1.5, j2=1 -> j in [0.5,1.5,2.5], select 2.5

btnPlayPause.addEventListener('click', ()=>{
  state.isAnimating = !state.isAnimating;
  btnPlayPause.textContent = state.isAnimating ? '❚❚' : '▶';
});
btnReset.addEventListener('click', ()=>{
  resetAnimation();
  rebuildScene();
});

// Start 3D
initThree();
rebuildScene();
</script>
</body>
</html>
