    // ----- Constants -----
    const N = 10;                   // Grid size
    const CELL = 20;                // Cell size
    const CANVAS = 201;             // Canvas size (adds a 1px outer edge)
    const EMPTY = 0, OBSTACLE = 1;
    const START = { x: 4, y: 4 };   // Starting cell for flood-fill
    const SCATTER_PROB = 0.25;      // ~25% obstacles for scattered
    const INITIAL_SLIDER_VALUE = 61;

    // ----- State -----
    const App = {
      grid: createGrid(N, EMPTY),          // OBSTACLE layout
      recursiveHistory: [],                // [{x, y}, ...]
      iterativeHistory: [],                // [{x, y}, ...]
      step: 0,                             // current slider step
      pattern: 'scattered',                // 'none' | 'box' | 'scattered'
      scatteredSeed: 20240                 // base seed (will adjust to guarantee >= 61 reachable)
    };

    // UI Elements
    const stepsSlider = document.getElementById('steps-slider');
    const stepsValue  = document.getElementById('steps-value');
    const btnNone     = document.getElementById('btn-none');
    const btnBox      = document.getElementById('btn-box');
    const btnScattered = document.getElementById('btn-scattered');

    // ----- Utility: Create 2D Grid -----
    function createGrid(n, fillVal = 0) {
      const g = new Array(n);
      for (let y = 0; y < n; y++) {
        g[y] = new Array(n).fill(fillVal);
      }
      return g;
    }

    // ----- Utility: Deep Clone Grid -----
    function cloneGrid(g) {
      return g.map(row => row.slice());
    }

    // ----- Seeded RNG (Mulberry32) -----
    function mulberry32(a) {
      return function() {
        let t = a += 0x6D2B79F5;
        t = Math.imul(t ^ (t >>> 15), t | 1);
        t ^= t + Math.imul(t ^ (t >>> 7), t | 61);
        return ((t ^ (t >>> 14)) >>> 0) / 4294967296;
      };
    }

    // ----- Pattern Generators -----
    function generateNone() {
      const g = createGrid(N, EMPTY);
      return g;
    }

    function generateBox() {
      const g = createGrid(N, EMPTY);
      for (let y = 0; y < N; y++) {
        for (let x = 0; x < N; x++) {
          if (x === 0 || x === N-1 || y === 0 || y === N-1) {
            g[y][x] = OBSTACLE;
          }
        }
      }
      // ensure start not an obstacle (it's inside so fine)
      return g;
    }

    function generateScattered(prob = SCATTER_PROB, baseSeed = 20240) {
      // We want a deterministic scattered grid that also guarantees >= 61 reachable cells.
      let seed = baseSeed;
      for (let attempt = 0; attempt < 500; attempt++) {
        const rng = mulberry32(seed);
        const g = createGrid(N, EMPTY);
        for (let y = 0; y < N; y++) {
          for (let x = 0; x < N; x++) {
            if (!(x === START.x && y === START.y)) {
              if (rng() < prob) g[y][x] = OBSTACLE;
            }
          }
        }
        // Evaluate reachable count from START
        const reachable = computeReachableCount(g, START.x, START.y);
        if (reachable >= INITIAL_SLIDER_VALUE) {
          // Found a deterministic pattern with enough reachable cells
          App.scatteredSeed = seed; // remember for consistency
          return g;
        }
        seed++;
      }
      // If not found within attempts, just return last g (highly unlikely)
      const rng = mulberry32(seed);
      const g = createGrid(N, EMPTY);
      for (let y = 0; y < N; y++) {
        for (let x = 0; x < N; x++) {
          if (!(x === START.x && y === START.y)) {
            if (rng() < prob) g[y][x] = OBSTACLE;
          }
        }
      }
      App.scatteredSeed = seed;
      return g;
    }

    // ----- Reachable Count Helper (BFS) -----
    function computeReachableCount(grid, sx, sy) {
      if (grid[sy][sx] === OBSTACLE) return 0;
      const visited = createGrid(N, false);
      const q = [{x: sx, y: sy}];
      visited[sy][sx] = true;
      let count = 0;
      const dirs = [
        {dx: 0, dy: -1}, // N
        {dx: -1, dy: 0}, // W
        {dx: 0, dy: 1},  // S
        {dx: 1, dy: 0}   // E
      ];
      while (q.length) {
        const {x, y} = q.shift();
        count++;
        for (const d of dirs) {
          const nx = x + d.dx, ny = y + d.dy;
          if (nx >= 0 && nx < N && ny >= 0 && ny < N &&
              grid[ny][nx] !== OBSTACLE && !visited[ny][nx]) {
            visited[ny][nx] = true;
            q.push({x: nx, y: ny});
          }
        }
      }
      return count;
    }

    // ----- Flood Fill Algorithms -----
    function computeHistories(grid) {
      App.recursiveHistory = [];
      App.iterativeHistory = [];
      // Recursive DFS (N, W, S, E)
      const visitedR = createGrid(N, false);
      function rec(x, y) {
        if (x < 0 || x >= N || y < 0 || y >= N) return;
        if (grid[y][x] === OBSTACLE) return;
        if (visitedR[y][x]) return;
        visitedR[y][x] = true;
        App.recursiveHistory.push({x, y});
        rec(x, y - 1); // N
        rec(x - 1, y); // W
        rec(x, y + 1); // S
        rec(x + 1, y); // E
      }
      if (grid[START.y][START.x] !== OBSTACLE) {
        rec(START.x, START.y);
      }

      // Iterative BFS (N, W, S, E)
      const visitedI = createGrid(N, false);
      const q = [];
      if (grid[START.y][START.x] !== OBSTACLE) {
        q.push({x: START.x, y: START.y});
        visitedI[START.y][START.x] = true;
      }
      const dirs = [
        {dx: 0, dy: -1}, // N
        {dx: -1, dy: 0}, // W
        {dx: 0, dy: 1},  // S
        {dx: 1, dy: 0}   // E
      ];
      while (q.length) {
        const {x, y} = q.shift();
        App.iterativeHistory.push({x, y});
        for (const d of dirs) {
          const nx = x + d.dx, ny = y + d.dy;
          if (nx >= 0 && nx < N && ny >= 0 && ny < N &&
              grid[ny][nx] !== OBSTACLE && !visitedI[ny][nx]) {
            visitedI[ny][nx] = true;
            q.push({x: nx, y: ny});
          }
        }
      }
    }

    // ----- p5 Sketches (instance mode) -----
    const recursiveSketch = function(p) {
      p.setup = function() {
        p.createCanvas(CANVAS, CANVAS);
        p.noLoop();
        drawGridRec();
      };
      function drawGridRec() {
        drawGridOnP5(p, App.grid, App.recursiveHistory, App.step);
      }
      p.redrawGrid = function() {
        drawGridRec();
      };
    };

    const iterativeSketch = function(p) {
      p.setup = function() {
        p.createCanvas(CANVAS, CANVAS);
        p.noLoop();
        drawGridIter();
      };
      function drawGridIter() {
        drawGridOnP5(p, App.grid, App.iterativeHistory, App.step);
      }
      p.redrawGrid = function() {
        drawGridIter();
      };
    };

    // Shared drawing routine used by both p5 instances
    function drawGridOnP5(p, grid, history, step) {
      p.push();
      p.background(255);
      p.stroke(0);
      p.strokeWeight(1);
      // Build set of filled cells up to "step"
      const filled = new Set();
      const upto = Math.min(step, history.length);
      for (let i = 0; i < upto; i++) {
        const c = history[i];
        filled.add(c.x + ',' + c.y);
      }
      for (let y = 0; y < N; y++) {
        for (let x = 0; x < N; x++) {
          if (grid[y][x] === OBSTACLE) {
            p.fill(0);
          } else if (filled.has(x + ',' + y)) {
            p.fill('#0000FF');
          } else {
            p.fill('#FFFFFF');
          }
          p.rect(x * CELL, y * CELL, CELL, CELL);
        }
      }
      p.pop();
    }

    // Instantiate p5 instances and attach to containers
    const p5Recursive = new p5(recursiveSketch, 'canvas-recursive');
    const p5Iterative = new p5(iterativeSketch, 'canvas-iterative');

    // ----- UI Handlers -----
    function setActiveButton(id) {
      [btnNone, btnBox, btnScattered].forEach(btn => btn.classList.remove('active'));
      document.getElementById(id).classList.add('active');
    }

    function updateSliderMax() {
      const maxSteps = Math.max(App.recursiveHistory.length, App.iterativeHistory.length);
      stepsSlider.max = String(maxSteps);
      return maxSteps;
    }

    function updateStepValueDisplay() {
      stepsValue.textContent = `+ ${App.step}`;
    }

    function redrawBoth() {
      if (p5Recursive && p5Recursive.redrawGrid) p5Recursive.redrawGrid();
      if (p5Iterative && p5Iterative.redrawGrid) p5Iterative.redrawGrid();
    }

    function regenerateForPattern(pattern) {
      App.pattern = pattern;
      if (pattern === 'none') {
        App.grid = generateNone();
      } else if (pattern === 'box') {
        App.grid = generateBox();
      } else {
        App.grid = generateScattered(SCATTER_PROB, App.scatteredSeed);
      }
      computeHistories(App.grid);
      const maxSteps = updateSliderMax();
      App.step = 0;
      stepsSlider.value = String(App.step);
      updateStepValueDisplay();
      redrawBoth();
    }

    // Slider interaction
    stepsSlider.addEventListener('input', () => {
      App.step = parseInt(stepsSlider.value, 10) || 0;
      updateStepValueDisplay();
      redrawBoth();
    });

    // Buttons
    btnNone.addEventListener('click', () => {
      setActiveButton('btn-none');
      regenerateForPattern('none');
    });
    btnBox.addEventListener('click', () => {
      setActiveButton('btn-box');
      regenerateForPattern('box');
    });
    btnScattered.addEventListener('click', () => {
      setActiveButton('btn-scattered');
      regenerateForPattern('scattered');
    });

    // ----- Initial Load -----
    function initialLoad() {
      // "Scattered" active by default
      setActiveButton('btn-scattered');
      App.grid = generateScattered(SCATTER_PROB, App.scatteredSeed);
      computeHistories(App.grid);
      const maxSteps = updateSliderMax();
      // Set initial slider value to 61 but clamp to max
      App.step = Math.min(INITIAL_SLIDER_VALUE, maxSteps);
      stepsSlider.value = String(App.step);
      updateStepValueDisplay();
      redrawBoth();
    }

    // Start
    window.addEventListener('load', initialLoad);
  </script>
</body>
</html>
